INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29872, 'nvi', '2 Timóteo', '2tm', 4, 1, 'Na presença de Deus e de Cristo Jesus, que há de julgar os vivos e os mortos por sua manifestação e por seu Reino, eu o exorto solenemente:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29873, 'nvi', '2 Timóteo', '2tm', 4, 2, 'Pregue a palavra, esteja preparado a tempo e fora de tempo, repreenda, corrija, exorte com toda a paciência e doutrina.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29874, 'nvi', '2 Timóteo', '2tm', 4, 3, 'Pois virá o tempo em que não suportarão a sã doutrina; pelo contrário, sentindo coceira nos ouvidos, segundo os seus próprios desejos juntarão mestres para si mesmos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29875, 'nvi', '2 Timóteo', '2tm', 4, 4, 'Eles se recusarão a dar ouvidos à verdade, voltando-se para os mitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29876, 'nvi', '2 Timóteo', '2tm', 4, 5, 'Você, porém, seja sóbrio em tudo, suporte os sofrimentos, faça a obra de um evangelista, cumpra plenamente o seu ministério.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29877, 'nvi', '2 Timóteo', '2tm', 4, 6, 'Eu já estou sendo derramado como uma oferta de bebida. Está próximo o tempo da minha partida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29878, 'nvi', '2 Timóteo', '2tm', 4, 7, 'Combati o bom combate, terminei a corrida, guardei a fé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29879, 'nvi', '2 Timóteo', '2tm', 4, 8, 'Agora me está reservada a coroa da justiça, que o Senhor, justo Juiz, me dará naquele dia; e não somente a mim, mas também a todos os que amam a sua vinda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29880, 'nvi', '2 Timóteo', '2tm', 4, 9, 'Procure vir logo ao meu encontro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29881, 'nvi', '2 Timóteo', '2tm', 4, 10, 'pois Demas, amando este mundo, abandonou-me e foi para Tessalônica. Crescente foi para a Galácia, e Tito, para a Dalmácia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29882, 'nvi', '2 Timóteo', '2tm', 4, 11, 'Só Lucas está comigo. Traga Marcos com você, porque ele me é útil para o ministério.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29883, 'nvi', '2 Timóteo', '2tm', 4, 12, 'Enviei Tíquico a Éfeso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29884, 'nvi', '2 Timóteo', '2tm', 4, 13, 'Quando você vier, traga a capa que deixei na casa de Carpo, em Trôade, e os meus livros, especialmente os pergaminhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29885, 'nvi', '2 Timóteo', '2tm', 4, 14, 'Alexandre, o ferreiro, causou-me muitos males. O Senhor lhe dará a retribuição pelo que fez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29886, 'nvi', '2 Timóteo', '2tm', 4, 15, 'Previna-se contra ele, porque se opôs fortemente às nossas palavras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29887, 'nvi', '2 Timóteo', '2tm', 4, 16, 'Na minha primeira defesa, ninguém apareceu para me apoiar; todos me abandonaram. Que isso não lhes cobrado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29888, 'nvi', '2 Timóteo', '2tm', 4, 17, 'Mas o Senhor permaneceu ao meu lado e me deu forças, para que por mim a mensagem fosse plenamente proclamada, e todos os gentios a ouvissem. E eu fui libertado da boca do leão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29889, 'nvi', '2 Timóteo', '2tm', 4, 18, 'O Senhor me livrará de toda obra maligna e me levará a salvo para o seu Reino celestial. A ele seja a glória para todo o sempre. Amém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29890, 'nvi', '2 Timóteo', '2tm', 4, 19, 'Saudações a Priscila e Áqüila, e à casa de Onesíforo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29891, 'nvi', '2 Timóteo', '2tm', 4, 20, 'Erasto permaneceu em Corinto, mas deixei Trófimo doente em Mileto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29892, 'nvi', '2 Timóteo', '2tm', 4, 21, 'Procure vir antes do inverno. Êubulo, Prudente, Lino, Cláudia e todos os irmãos enviam-lhe saudações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29893, 'nvi', '2 Timóteo', '2tm', 4, 22, 'O Senhor seja com o seu espírito. A graça seja com vocês.');
