INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22857, 'nvi', 'Ageu', 'ag', 2, 1, 'No dia vinte e um do sétimo mês, veio a palavra do Senhor por meio do profeta Ageu:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22858, 'nvi', 'Ageu', 'ag', 2, 2, '"Pergunte ao governador de Judá, Zorobabel, filho de Sealtiel, ao sumo sacerdote Josué, filho de Jeozadaque, e ao restante do povo, o seguinte:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22859, 'nvi', 'Ageu', 'ag', 2, 3, '‘Quem de vocês viu este templo em seu primeiro esplendor? Comparado com ele, não é como nada o que vocês vêem agora?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22860, 'nvi', 'Ageu', 'ag', 2, 4, '"Coragem, Zorobabel", declara o Senhor. "Coragem, sumo sacerdote Josué, filho de Jeozadaque. Coragem! Ao trabalho, ó povo da terra! ", declara o Senhor. "Porque eu estou com vocês", declara o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22861, 'nvi', 'Ageu', 'ag', 2, 5, 'Esta é a aliança que fiz com vocês quando vocês saíram do Egito: "Meu espírito está entre vocês. Não tenham medo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22862, 'nvi', 'Ageu', 'ag', 2, 6, 'Assim diz o Senhor dos Exércitos: "Dentro de pouco tempo farei tremer o céu, a terra, o mar e o continente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22863, 'nvi', 'Ageu', 'ag', 2, 7, 'Farei tremer todas as nações, que trarão para cá os seus tesouros, e encherei este templo de glória", diz o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22864, 'nvi', 'Ageu', 'ag', 2, 8, '"Tanto a prata quanto o ouro me pertencem", declara o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22865, 'nvi', 'Ageu', 'ag', 2, 9, '"A glória deste novo templo será maior do que a do antigo", diz o Senhor dos Exércitos. "E neste lugar estabelecerei a paz", declara o Senhor dos Exércitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22866, 'nvi', 'Ageu', 'ag', 2, 10, 'No dia vinte e quatro do nono mês, no segundo ano do reinado de Dario, a palavra do Senhor veio ao profeta Ageu:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22867, 'nvi', 'Ageu', 'ag', 2, 11, '"Assim diz o Senhor dos Exércitos: ‘Pergunte aos sacerdotes sobre a seguinte questão da Lei:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22868, 'nvi', 'Ageu', 'ag', 2, 12, 'Se alguém levar carne consagrada na borda de suas vestes, e com ela tocar num pão, ou em algo cozido, ou em vinho, ou em azeite ou em qualquer comida, isso ficará consagrado? ’ " Os sacerdotes responderam: "Não".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22869, 'nvi', 'Ageu', 'ag', 2, 13, 'Em seguida perguntou Ageu: "Se alguém ficar impuro por tocar num cadáver e depois tocar em alguma dessas coisas, ela ficará impura? "Sim", responderam os sacerdotes, "ficará impura".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22870, 'nvi', 'Ageu', 'ag', 2, 14, 'Então disse Ageu: "É o que acontece com este povo e com esta nação", declara o Senhor. "Tudo o que fazem e tudo o que me oferecem é impuro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22871, 'nvi', 'Ageu', 'ag', 2, 15, '"Agora prestem atenção: de hoje em diante reconsiderem. Como eram as coisas antes que se colocasse pedra sobre pedra no templo do Senhor?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22872, 'nvi', 'Ageu', 'ag', 2, 16, 'Quando alguém chegava a um monte de trigo procurando vinte medidas, havia apenas dez. Quando alguém ia ao depósito de vinho para tirar cinqüenta medidas, só encontrava vinte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22873, 'nvi', 'Ageu', 'ag', 2, 17, 'Eu destruí todo o trabalho das mãos de vocês, com mofo, ferrugem e granizo, mas vocês não se voltaram para mim", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22874, 'nvi', 'Ageu', 'ag', 2, 18, '"A partir de hoje, dia vinte e quatro do nono mês, atentem para o dia em que os fundamentos do templo do Senhor foram lançados. Reconsiderem:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22875, 'nvi', 'Ageu', 'ag', 2, 19, 'Ainda há alguma semente no celeiro? Até hoje a videira, a figueira, a romeira e a oliveira não têm dado fruto. Mas, de hoje em diante, abençoarei vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22876, 'nvi', 'Ageu', 'ag', 2, 20, 'A palavra do Senhor veio a Ageu pela segunda vez, no dia vinte e quatro do nono mês:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22877, 'nvi', 'Ageu', 'ag', 2, 21, '"Diga a Zorobabel, governador de Judá, que eu farei tremer o céu e a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22878, 'nvi', 'Ageu', 'ag', 2, 22, 'Derrubarei tronos e destruirei o poder dos reinos estrangeiros. Virarei os carros e os seus condutores; os cavalos e os seus cavaleiros cairão, cada um pela espada do seu companheiro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22879, 'nvi', 'Ageu', 'ag', 2, 23, '"Naquele dia", declara o Senhor dos Exércitos, "eu o tomarei, meu servo Zorobabel, filho de Sealtiel", declara o Senhor, "e farei de você um anel de selar, porque o tenho escolhido", declara o Senhor dos Exércitos.');
