INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30700, 'nvi', 'Apocalipse', 'ap', 1, 1, 'Revelação de Jesus Cristo, que Deus lhe deu para mostrar aos seus servos o que em breve há de acontecer. Ele enviou o seu anjo para torná-la conhecida ao seu servo João,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30701, 'nvi', 'Apocalipse', 'ap', 1, 2, 'que dá testemunho de tudo o que viu, isto é, a palavra de Deus e o testemunho de Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30702, 'nvi', 'Apocalipse', 'ap', 1, 3, 'Feliz aquele que lê as palavras desta profecia e felizes aqueles que ouvem e guardam o que nela está escrito, porque o tempo está próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30703, 'nvi', 'Apocalipse', 'ap', 1, 4, 'João às sete igrejas da província da Ásia: A vocês, graça e paz da parte daquele que é, que era e que há de vir, dos sete espíritos que estão diante do seu trono,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30704, 'nvi', 'Apocalipse', 'ap', 1, 5, 'e de Jesus Cristo, que é a testemunha fiel, o primogênito dentre os mortos e o soberano dos reis da terra. Ele que nos ama e nos libertou dos nossos pecados por meio do seu sangue,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30705, 'nvi', 'Apocalipse', 'ap', 1, 6, 'e nos constituiu reino e sacerdotes para servir a seu Deus e Pai. A ele sejam glória e poder para todo o sempre! Amém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30706, 'nvi', 'Apocalipse', 'ap', 1, 7, 'Eis que ele vem com as nuvens, e todo olho o verá, até mesmo aqueles que o traspassaram; e todos os povos da terra se lamentarão por causa dele. Assim será! Amém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30707, 'nvi', 'Apocalipse', 'ap', 1, 8, '"Eu sou o Alfa e o Ômega", diz o Senhor Deus, "o que é, o que era e o que há de vir, o Todo-poderoso".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30708, 'nvi', 'Apocalipse', 'ap', 1, 9, 'Eu, João, irmão e companheiro de vocês no sofrimento, no Reino e na perseverança em Jesus, estava na ilha de Patmos, por causa da palavra de Deus e do testemunho de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30709, 'nvi', 'Apocalipse', 'ap', 1, 10, 'No dia do Senhor achei-me no Espírito e ouvi por trás de mim uma voz forte, como de trombeta,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30710, 'nvi', 'Apocalipse', 'ap', 1, 11, 'que dizia: "Escreva num livro o que você vê e envie a estas sete igrejas: Éfeso, Esmirna, Pérgamo, Tiatira, Sardes, Filadélfia e Laodicéia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30711, 'nvi', 'Apocalipse', 'ap', 1, 12, 'Voltei-me para ver quem falava comigo. Voltando-me, vi sete candelabros de ouro');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30712, 'nvi', 'Apocalipse', 'ap', 1, 13, 'e entre os candelabros alguém "semelhante a um filho de homem", com uma veste que chegava aos seus pés e um cinturão de ouro ao redor do peito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30713, 'nvi', 'Apocalipse', 'ap', 1, 14, 'Sua cabeça e seus cabelos eram brancos como a lã, tão brancos quanto a neve, e seus olhos eram como chama de fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30714, 'nvi', 'Apocalipse', 'ap', 1, 15, 'Seus pés eram como o bronze numa fornalha ardente e sua voz como o som de muitas águas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30715, 'nvi', 'Apocalipse', 'ap', 1, 16, 'Tinha em sua mão direita sete estrelas, e da sua boca saía uma espada afiada de dois gumes. Sua face era como o sol quando brilha em todo o seu fulgor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30716, 'nvi', 'Apocalipse', 'ap', 1, 17, 'Quando o vi, caí aos seus pés como morto. Então ele colocou sua mão direita sobre mim e disse: "Não tenha medo. Eu sou o primeiro e o último.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30717, 'nvi', 'Apocalipse', 'ap', 1, 18, 'Sou aquele que vive. Estive morto mas agora estou vivo para todo o sempre! E tenho as chaves da morte e do Hades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30718, 'nvi', 'Apocalipse', 'ap', 1, 19, '"Escreva, pois, as coisas que você viu, tanto as presentes como as que estão por vir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30719, 'nvi', 'Apocalipse', 'ap', 1, 20, 'Este é o mistério das sete estrelas que você viu em minha mão direita e dos sete candelabros: as sete estrelas são os anjos das sete igrejas, e os sete candelabros são as sete igrejas".');
