INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31021, 'nvi', 'Apocalipse', 'ap', 19, 1, 'Depois disso ouvi no céu algo semelhante à voz de uma grande multidão, que exclamava: "Aleluia! A salvação, a glória e o poder pertencem ao nosso Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31022, 'nvi', 'Apocalipse', 'ap', 19, 2, 'pois verdadeiros e justos são os seus juízos. Ele condenou a grande prostituta que corrompia a terra com a sua prostituição. Ele cobrou dela o sangue dos seus servos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31023, 'nvi', 'Apocalipse', 'ap', 19, 3, 'E mais uma vez a multidão exclamou: "Aleluia! A fumaça que dela vem, sobe para todo o sempre".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31024, 'nvi', 'Apocalipse', 'ap', 19, 4, 'Os vinte e quatro anciãos e os quatro seres viventes prostraram-se e adoraram a Deus, que estava assentado no trono, e exclamaram: "Amém, Aleluia! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31025, 'nvi', 'Apocalipse', 'ap', 19, 5, 'Então veio do trono uma voz, conclamando: "Louvem o nosso Deus, todos vocês, seus servos, vocês que o temem, tanto pequenos como grandes! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31026, 'nvi', 'Apocalipse', 'ap', 19, 6, 'Então ouvi algo semelhante ao som de uma grande multidão, como o estrondo de muitas águas e fortes trovões, que bradava: "Aleluia! pois reina o Senhor, o nosso Deus, o Todo-poderoso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31027, 'nvi', 'Apocalipse', 'ap', 19, 7, 'Regozijemo-nos! Vamos nos alegrar e dar-lhe glória! Pois chegou a hora do casamento do Cordeiro, e a sua noiva já se aprontou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31028, 'nvi', 'Apocalipse', 'ap', 19, 8, 'Foi-lhe dado para vestir-se linho fino, brilhante e puro". O linho fino são os atos justos dos santos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31029, 'nvi', 'Apocalipse', 'ap', 19, 9, 'E o anjo me disse: "Escreva: Felizes os convidados para o banquete do casamento do Cordeiro! " E acrescentou: "Estas são as palavras verdadeiras de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31030, 'nvi', 'Apocalipse', 'ap', 19, 10, 'Então caí aos seus pés para adorá-lo, mas ele me disse: "Não faça isso! Sou servo como você e como os seus irmãos que se mantêm fiéis ao testemunho de Jesus. Adore a Deus! O testemunho de Jesus é o espírito de profecia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31031, 'nvi', 'Apocalipse', 'ap', 19, 11, 'Vi o céu aberto e diante de mim um cavalo branco, cujo cavaleiro se chama Fiel e Verdadeiro. Ele julga e guerreia com justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31032, 'nvi', 'Apocalipse', 'ap', 19, 12, 'Seus olhos são como chamas de fogo, e em sua cabeça há muitas coroas e um nome que só ele conhece, e ninguém mais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31033, 'nvi', 'Apocalipse', 'ap', 19, 13, 'Está vestido com um manto tingido de sangue, e o seu nome é Palavra de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31034, 'nvi', 'Apocalipse', 'ap', 19, 14, 'Os exércitos do céu o seguiam, vestidos de linho fino, branco e puro, e montados em cavalos brancos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31035, 'nvi', 'Apocalipse', 'ap', 19, 15, 'De sua boca sai uma espada afiada, com a qual ferirá as nações. "Ele as governará com cetro de ferro". Ele pisa o lagar do vinho do furor da ira do Deus todo-poderoso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31036, 'nvi', 'Apocalipse', 'ap', 19, 16, 'Em seu manto e em sua coxa está escrito este nome: REI DOS REIS E SENHOR DOS SENHORES.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31037, 'nvi', 'Apocalipse', 'ap', 19, 17, 'Vi um anjo que estava de pé no sol e que clamava em alta voz a todas as aves que voavam pelo meio do céu: "Venham, reúnam-se para o grande banquete de Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31038, 'nvi', 'Apocalipse', 'ap', 19, 18, 'para comerem carne de reis, generais e poderosos, carne de cavalos e seus cavaleiros, carne de todos: livres e escravos, pequenos e grandes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31039, 'nvi', 'Apocalipse', 'ap', 19, 19, 'Então vi a besta, os reis da terra e os seus exércitos reunidos para guerrearem contra aquele que está montado no cavalo e contra o seu exército.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31040, 'nvi', 'Apocalipse', 'ap', 19, 20, 'Mas a besta foi presa, e com ela o falso profeta que havia realizado os sinais miraculosos em nome dela, com os quais ele havia enganado os que receberam a marca da besta e adoraram a imagem dela. Os dois foram lançados vivos no lago de fogo que arde com enxofre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31041, 'nvi', 'Apocalipse', 'ap', 19, 21, 'Os demais foram mortos com a espada que saía da boca daquele que está montado no cavalo. E todas as aves se fartaram com a carne deles.');
