INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31084, 'nvi', 'Apocalipse', 'ap', 22, 1, 'Então o anjo me mostrou o rio da água da vida que, claro como cristal, fluía do trono de Deus e do Cordeiro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31085, 'nvi', 'Apocalipse', 'ap', 22, 2, 'no meio da rua principal da cidade. De cada lado do rio estava a árvore da vida, que dá doze colheitas, dando fruto todos os meses. As folhas da árvore servem para a cura das nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31086, 'nvi', 'Apocalipse', 'ap', 22, 3, 'Já não haverá maldição nenhuma. O trono de Deus e do Cordeiro estará na cidade, e os seus servos o servirão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31087, 'nvi', 'Apocalipse', 'ap', 22, 4, 'Eles verão a sua face, e o seu nome estará em suas testas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31088, 'nvi', 'Apocalipse', 'ap', 22, 5, 'Não haverá mais noite. Eles não precisarão de luz de candeia nem da luz do sol, pois o Senhor Deus os iluminará; e eles reinarão para todo o sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31089, 'nvi', 'Apocalipse', 'ap', 22, 6, 'O anjo me disse: "Estas palavras são dignas de confiança e verdadeiras. O Senhor, o Deus dos espíritos dos profetas, enviou o seu anjo para mostrar aos seus servos as coisas que em breve hão de acontecer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31090, 'nvi', 'Apocalipse', 'ap', 22, 7, '"Eis que venho em breve! Feliz é aquele que guarda as palavras da profecia deste livro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31091, 'nvi', 'Apocalipse', 'ap', 22, 8, 'Eu, João, sou aquele que ouviu e viu estas coisas. Tendo-as ouvido e visto, caí aos pés do anjo que me mostrou tudo aquilo para mim, para adorá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31092, 'nvi', 'Apocalipse', 'ap', 22, 9, 'Mas ele me disse: "Não faça isso! Sou servo como você e seus irmãos, os profetas, e como os que guardam as palavras deste livro. Adore a Deus! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31093, 'nvi', 'Apocalipse', 'ap', 22, 10, 'Então me disse: "Não sele as palavras da profecia deste livro, pois o tempo está próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31094, 'nvi', 'Apocalipse', 'ap', 22, 11, 'Continue o injusto a praticar injustiça; continue o imundo na imundícia; continue o justo a praticar justiça; e continue o santo a santificar-se".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31095, 'nvi', 'Apocalipse', 'ap', 22, 12, '"Eis que venho em breve! A minha recompensa está comigo, e eu retribuirei a cada um de acordo com o que fez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31096, 'nvi', 'Apocalipse', 'ap', 22, 13, 'Eu sou o Alfa e o Ômega, o Primeiro e o Último, o Princípio e o Fim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31097, 'nvi', 'Apocalipse', 'ap', 22, 14, '"Felizes os que lavam as suas vestes, para que tenham direito à árvore da vida e possam entrar na cidade pelas portas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31098, 'nvi', 'Apocalipse', 'ap', 22, 15, 'Fora ficam os cães, os que praticam feitiçaria, os que cometem imoralidades sexuais, os assassinos, os idólatras e todos os que amam e praticam a mentira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31099, 'nvi', 'Apocalipse', 'ap', 22, 16, '"Eu, Jesus, enviei o meu anjo para dar a vocês este testemunho concernente às igrejas. Eu sou a Raiz e o Descendente de Davi, e a resplandecente Estrela da Manhã".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31100, 'nvi', 'Apocalipse', 'ap', 22, 17, 'O Espírito e a noiva dizem: "Vem! " E todo aquele que ouvir diga: "Vem! " Quem tiver sede, venha; e quem quiser, beba de graça da água da vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31101, 'nvi', 'Apocalipse', 'ap', 22, 18, 'Declaro a todos os que ouvem as palavras da profecia deste livro: se alguém lhe acrescentar algo, Deus lhe acrescentará as pragas descritas neste livro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31102, 'nvi', 'Apocalipse', 'ap', 22, 19, 'Se alguém tirar alguma palavra deste livro de profecia, Deus tirará dele a sua parte na árvore da vida e na cidade santa, que são descritas neste livro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31103, 'nvi', 'Apocalipse', 'ap', 22, 20, 'Aquele que dá testemunho destas coisas diz: "Sim, venho em breve! " Amém. Vem, Senhor Jesus!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (31104, 'nvi', 'Apocalipse', 'ap', 22, 21, 'A graça do Senhor Jesus seja com todos. Amém.');
