INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30843, 'nvi', 'Apocalipse', 'ap', 9, 1, 'O quinto anjo tocou a sua trombeta, e vi uma estrela que havia caído do céu sobre a terra. À estrela foi dada a chave do poço do Abismo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30844, 'nvi', 'Apocalipse', 'ap', 9, 2, 'Quando ela abriu o Abismo, subiu dele fumaça como a de uma gigantesca fornalha. O sol e o céu escureceram com a fumaça que saía do abismo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30845, 'nvi', 'Apocalipse', 'ap', 9, 3, 'Da fumaça saíram gafanhotos que vieram sobre a terra, e lhes foi dado poder como o dos escorpiões da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30846, 'nvi', 'Apocalipse', 'ap', 9, 4, 'Eles receberam ordens para não causar dano nem à relva da terra nem a qualquer planta ou árvore, mas apenas àqueles que não tinham o selo de Deus na testa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30847, 'nvi', 'Apocalipse', 'ap', 9, 5, 'Não lhes foi dado poder para matá-los, mas sim para causar-lhes tormento durante cinco meses. A agonia que eles sofreram era como a da picada do escorpião.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30848, 'nvi', 'Apocalipse', 'ap', 9, 6, 'Naqueles dias os homens procurarão a morte, mas não a encontrarão; desejarão morrer, mas a morte fugirá deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30849, 'nvi', 'Apocalipse', 'ap', 9, 7, 'Os gafanhotos pareciam cavalos preparados para a batalha. Tinham sobre a cabeça algo como coroas de ouro, e o rosto deles parecia rosto humano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30850, 'nvi', 'Apocalipse', 'ap', 9, 8, 'Os cabelos deles eram como os de mulheres e os dentes como os de leão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30851, 'nvi', 'Apocalipse', 'ap', 9, 9, 'Tinham couraças como couraças de ferro, e o som das suas asas era como o barulho de muitos cavalos e carruagens correndo para a batalha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30852, 'nvi', 'Apocalipse', 'ap', 9, 10, 'Tinham caudas e ferrões como de escorpiões, e na cauda tinham poder para causar tormento aos homens durante cinco meses.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30853, 'nvi', 'Apocalipse', 'ap', 9, 11, 'Tinham um rei sobre eles, o anjo do Abismo, cujo nome, em hebraico, é Abadom, e, em grego, Apoliom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30854, 'nvi', 'Apocalipse', 'ap', 9, 12, 'O primeiro ai passou; dois outros ais ainda estão por vir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30855, 'nvi', 'Apocalipse', 'ap', 9, 13, 'O sexto anjo tocou a sua trombeta, e ouvi uma voz que vinha das pontas do altar de ouro que está diante de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30856, 'nvi', 'Apocalipse', 'ap', 9, 14, 'Ela disse ao sexto anjo que tinha a trombeta: "Solte os quatro anjos que estão amarrados junto ao grande rio Eufrates".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30857, 'nvi', 'Apocalipse', 'ap', 9, 15, 'Os quatro anjos, que estavam preparados para aquela hora, dia, mês e ano, foram soltos para matar um terço da humanidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30858, 'nvi', 'Apocalipse', 'ap', 9, 16, 'O número dos cavaleiros que compunham os exércitos era de duzentos milhões; eu ouvi o seu número.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30859, 'nvi', 'Apocalipse', 'ap', 9, 17, 'Os cavalos e os cavaleiros que vi em minha visão tinham este aspecto: as suas couraças eram vermelhas como o fogo, azul-escuras, e amarelas como o enxofre. A cabeça dos cavalos parecia a cabeça de um leão, e da boca lançavam fogo, fumaça e enxofre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30860, 'nvi', 'Apocalipse', 'ap', 9, 18, 'Um terço da humanidade foi morto pelas três pragas de fogo, fumaça e enxofre que saíam das suas bocas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30861, 'nvi', 'Apocalipse', 'ap', 9, 19, 'O poder dos cavalos estava na boca e na cauda; pois as suas caudas eram como cobras; tinham cabeças com as quais feriam as pessoas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30862, 'nvi', 'Apocalipse', 'ap', 9, 20, 'O restante da humanidade que não morreu por essas pragas, nem assim se arrependeu das obras das suas mãos; eles não pararam de adorar os demônios e os ídolos de ouro, prata, bronze, pedra e madeira, ídolos que não podem ver nem ouvir nem andar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (30863, 'nvi', 'Apocalipse', 'ap', 9, 21, 'Também não se arrependeram dos seus assassinatos, das suas feitiçarias, da sua imoralidade sexual e dos seus roubos.');
