INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26925, 'nvi', 'Atos', 'atos', 1, 1, 'Em meu livro anterior, Teófilo, escrevi a respeito de tudo o que Jesus começou a fazer e a ensinar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26926, 'nvi', 'Atos', 'atos', 1, 2, 'até o dia em que foi elevado ao céu, depois de ter dado instruções por meio do Espírito Santo aos apóstolos que havia escolhido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26927, 'nvi', 'Atos', 'atos', 1, 3, 'Depois do seu sofrimento, Jesus apresentou-se a eles e deu-lhes muitas provas indiscutíveis de que estava vivo. Apareceu-lhes por um período de quarenta dias falando-lhes acerca do Reino de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26928, 'nvi', 'Atos', 'atos', 1, 4, 'Certa ocasião, enquanto comia com eles, deu-lhes esta ordem: "Não saiam de Jerusalém, mas esperem pela promessa de meu Pai, da qual lhes falei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26929, 'nvi', 'Atos', 'atos', 1, 5, 'Pois João batizou com água, mas dentro de poucos dias vocês serão batizados com o Espírito Santo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26930, 'nvi', 'Atos', 'atos', 1, 6, 'Então os que estavam reunidos lhe perguntaram: "Senhor, é neste tempo que vais restaurar o reino a Israel? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26931, 'nvi', 'Atos', 'atos', 1, 7, 'Ele lhes respondeu: "Não lhes compete saber os tempos ou as datas que o Pai estabeleceu pela sua própria autoridade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26932, 'nvi', 'Atos', 'atos', 1, 8, 'Mas receberão poder quando o Espírito Santo descer sobre vocês, e serão minhas testemunhas em Jerusalém, em toda a Judéia e Samaria, e até os confins da terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26933, 'nvi', 'Atos', 'atos', 1, 9, 'Tendo dito isso, foi elevado às alturas enquanto eles olhavam, e uma nuvem o encobriu da vista deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26934, 'nvi', 'Atos', 'atos', 1, 10, 'E eles ficaram com os olhos fixos no céu enquanto ele subia. De repente surgiram diante deles dois homens vestidos de branco,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26935, 'nvi', 'Atos', 'atos', 1, 11, 'que lhes disseram: "Galileus, por que vocês estão olhando para o céu? Este mesmo Jesus, que dentre vocês foi elevado ao céu, voltará da mesma forma como o viram subir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26936, 'nvi', 'Atos', 'atos', 1, 12, 'Então eles voltaram para Jerusalém, vindo do monte chamado das Oliveiras, que fica perto da cidade, cerca de um quilômetro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26937, 'nvi', 'Atos', 'atos', 1, 13, 'Quando chegaram, subiram ao aposento onde estavam hospedados. Achavam-se presentes Pedro, João, Tiago e André; Filipe, Tomé, Bartolomeu e Mateus; Tiago, filho de Alfeu, Simão, o zelote, e Judas, filho de Tiago.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26938, 'nvi', 'Atos', 'atos', 1, 14, 'Todos eles se reuniam sempre em oração, com as mulheres, inclusive Maria, a mãe de Jesus, e com os irmãos de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26939, 'nvi', 'Atos', 'atos', 1, 15, 'Naqueles dias Pedro levantou-se entre os irmãos, um grupo de cerca de cento e vinte pessoas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26940, 'nvi', 'Atos', 'atos', 1, 16, 'e disse: "Irmãos, era necessário que se cumprisse a Escritura que o Espírito Santo predisse por boca de Davi, a respeito de Judas, que serviu de guia aos que prenderam Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26941, 'nvi', 'Atos', 'atos', 1, 17, 'Ele foi contado como um dos nossos e teve participação neste ministério".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26942, 'nvi', 'Atos', 'atos', 1, 18, '( Com a recompensa que recebeu pelo seu pecado, Judas comprou um campo. Ali caiu de cabeça, seu corpo partiu-se ao meio, e as suas vísceras se derramaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26943, 'nvi', 'Atos', 'atos', 1, 19, 'Todos em Jerusalém ficaram sabendo disso, de modo que, na língua deles, esse campo passou a chamar-se Aceldama, isto é, campo de Sangue. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26944, 'nvi', 'Atos', 'atos', 1, 20, '"Porque", prosseguiu Pedro, "está escrito no Livro de Salmos: ‘Fique deserto o seu lugar, e não haja ninguém que nele habite’; e ainda: ‘Que outro ocupe o seu lugar’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26945, 'nvi', 'Atos', 'atos', 1, 21, 'Portanto, é necessário que escolhamos um dos homens que estiveram conosco durante todo o tempo em que o Senhor Jesus viveu entre nós,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26946, 'nvi', 'Atos', 'atos', 1, 22, 'desde o batismo de João até o dia em que Jesus foi elevado dentre nós às alturas. É preciso que um deles seja conosco testemunha de sua ressurreição".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26947, 'nvi', 'Atos', 'atos', 1, 23, 'Então indicaram dois nomes: José, chamado Barsabás, também conhecido como Justo, e Matias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26948, 'nvi', 'Atos', 'atos', 1, 24, 'Depois oraram: "Senhor, tu conheces o coração de todos. Mostra-nos qual destes dois tens escolhido');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26949, 'nvi', 'Atos', 'atos', 1, 25, 'para assumir este ministério apostólico que Judas abandonou, indo para o lugar que lhe era devido".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26950, 'nvi', 'Atos', 'atos', 1, 26, 'Então tiraram sortes, e a sorte caiu sobre Matias; assim, ele foi acrescentado aos onze apóstolos.');
