INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27309, 'nvi', 'Atos', 'atos', 11, 1, 'Os apóstolos e os irmãos de toda a Judéia ouviram falar que os gentios também haviam recebido a palavra de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27310, 'nvi', 'Atos', 'atos', 11, 2, 'Assim, quando Pedro subiu a Jerusalém, os que eram do partido dos circuncisos o criticavam, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27311, 'nvi', 'Atos', 'atos', 11, 3, '"Você entrou na casa de homens incircuncisos e comeu com eles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27312, 'nvi', 'Atos', 'atos', 11, 4, 'Pedro, então, começou a explicar-lhes exatamente como tudo havia acontecido:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27313, 'nvi', 'Atos', 'atos', 11, 5, '"Eu estava na cidade de Jope orando; caindo em êxtase, tive uma visão. Vi algo parecido com um grande lençol sendo baixado do céu, preso pelas quatro pontas, e que vinha até o lugar onde eu estava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27314, 'nvi', 'Atos', 'atos', 11, 6, 'Olhei para dentro dele e notei que havia ali quadrúpedes da terra, animais selvagens, répteis e aves do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27315, 'nvi', 'Atos', 'atos', 11, 7, 'Então ouvi uma voz que me dizia: ‘Levante-se, Pedro; mate e coma’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27316, 'nvi', 'Atos', 'atos', 11, 8, '"Eu respondi: De modo nenhum, Senhor! Nunca entrou em minha boca algo impuro ou imundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27317, 'nvi', 'Atos', 'atos', 11, 9, '"A voz falou do céu segunda vez: ‘Não chame impuro ao que Deus purificou’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27318, 'nvi', 'Atos', 'atos', 11, 10, 'Isso aconteceu três vezes, e então tudo foi recolhido ao céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27319, 'nvi', 'Atos', 'atos', 11, 11, '"Na mesma hora chegaram à casa em que eu estava hospedado três homens que me haviam sido enviados de Cesaréia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27320, 'nvi', 'Atos', 'atos', 11, 12, 'O Espírito me disse que não hesitasse em ir com eles. Estes seis irmãos também foram comigo, e entramos na casa de um certo homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27321, 'nvi', 'Atos', 'atos', 11, 13, 'Ele nos contou como um anjo lhe tinha aparecido em sua casa e dissera: ‘Mande buscar, em Jope, a Simão, chamado Pedro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27322, 'nvi', 'Atos', 'atos', 11, 14, 'Ele lhe trará uma mensagem por meio da qual serão salvos você e todos os da sua casa’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27323, 'nvi', 'Atos', 'atos', 11, 15, '"Quando comecei a falar, o Espírito Santo desceu sobre eles como sobre nós no princípio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27324, 'nvi', 'Atos', 'atos', 11, 16, 'Então me lembrei do que o Senhor tinha dito: ‘João batizou com água, mas vocês serão batizados com o Espírito Santo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27325, 'nvi', 'Atos', 'atos', 11, 17, 'Se, pois, Deus lhes deu o mesmo dom que nos dera quando cremos no Senhor Jesus Cristo, quem era eu para pensar em opor-me a Deus? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27326, 'nvi', 'Atos', 'atos', 11, 18, 'Ouvindo isso, não apresentaram mais objeções e louvaram a Deus, dizendo: "Então, Deus concedeu arrependimento para a vida até mesmo aos gentios! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27327, 'nvi', 'Atos', 'atos', 11, 19, 'Os que tinham sido dispersos por causa da perseguição desencadeada com a morte de Estêvão chegaram até à Fenícia, Chipre e Antioquia, anunciando a mensagem apenas aos judeus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27328, 'nvi', 'Atos', 'atos', 11, 20, 'Alguns deles, todavia, cipriotas e cireneus, foram a Antioquia e começaram a falar também aos gregos, contando-lhes as boas novas a respeito do Senhor Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27329, 'nvi', 'Atos', 'atos', 11, 21, 'A mão do Senhor estava com eles, e muitos creram e se converteram ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27330, 'nvi', 'Atos', 'atos', 11, 22, 'Notícias desse fato chegaram aos ouvidos da igreja em Jerusalém, e eles enviaram Barnabé a Antioquia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27331, 'nvi', 'Atos', 'atos', 11, 23, 'Este, ali chegando e vendo a graça de Deus, ficou alegre e os animou a permanecerem fiéis ao Senhor, de todo o coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27332, 'nvi', 'Atos', 'atos', 11, 24, 'Ele era um homem bom, cheio do Espírito Santo e de fé; e muitas pessoas foram acrescentadas ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27333, 'nvi', 'Atos', 'atos', 11, 25, 'Então Barnabé foi a Tarso procurar Saulo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27334, 'nvi', 'Atos', 'atos', 11, 26, 'e, quando o encontrou, levou-o para Antioquia. Assim, durante um ano inteiro Barnabé e Saulo se reuniram com a igreja e ensinaram a muitos. Em Antioquia, os discípulos foram pela primeira vez chamados cristãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27335, 'nvi', 'Atos', 'atos', 11, 27, 'Naqueles dias alguns profetas desceram de Jerusalém para Antioquia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27336, 'nvi', 'Atos', 'atos', 11, 28, 'Um deles, Ágabo, levantou-se e pelo Espírito predisse que uma grande fome sobreviria a todo o mundo romano, o que aconteceu durante o reinado de Cláudio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27337, 'nvi', 'Atos', 'atos', 11, 29, 'Os discípulos, cada um segundo as suas possibilidades, decidiram providenciar ajuda para os irmãos que viviam na Judéia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27338, 'nvi', 'Atos', 'atos', 11, 30, 'E o fizeram, enviando suas ofertas aos presbíteros pelas mãos de Barnabé e Saulo.');
