INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27339, 'nvi', 'Atos', 'atos', 12, 1, 'Nessa ocasião, o rei Herodes prendeu alguns que pertenciam à igreja, com a intenção de maltratá-los,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27340, 'nvi', 'Atos', 'atos', 12, 2, 'e mandou matar à espada Tiago, irmão de João.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27341, 'nvi', 'Atos', 'atos', 12, 3, 'Vendo que isso agradava aos judeus, prosseguiu, prendendo também Pedro, durante a festa dos pães sem fermento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27342, 'nvi', 'Atos', 'atos', 12, 4, 'Tendo-o prendido, lançou-o no cárcere, entregando-o para ser guardado por quatro escoltas de quatro soldados cada uma. Herodes pretendia submetê-lo a julgamento público depois da Páscoa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27343, 'nvi', 'Atos', 'atos', 12, 5, 'Pedro, então, ficou detido na prisão, mas a igreja orava intensamente a Deus por ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27344, 'nvi', 'Atos', 'atos', 12, 6, 'Na noite anterior ao dia em que Herodes iria submetê-lo a julgamento, Pedro estava dormindo entre dois soldados, preso com duas algemas, e sentinelas montavam guarda à entrada do cárcere.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27345, 'nvi', 'Atos', 'atos', 12, 7, 'Repentinamente apareceu um anjo do Senhor, e uma luz brilhou na cela. Ele tocou no lado de Pedro e o acordou. "Depressa, levante-se! ", disse ele. Então as algemas caíram dos punhos de Pedro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27346, 'nvi', 'Atos', 'atos', 12, 8, 'O anjo lhe disse: "Vista-se e calce as sandálias". E Pedro assim fez. Disse-lhe ainda o anjo: "Ponha a capa e siga-me".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27347, 'nvi', 'Atos', 'atos', 12, 9, 'E, saindo, Pedro o seguiu, não sabendo que era real o que se fazia por meio do anjo; tudo lhe parecia uma visão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27348, 'nvi', 'Atos', 'atos', 12, 10, 'Passaram a primeira e a segunda guarda, e chegaram ao portão de ferro que dava para a cidade. Este se abriu por si mesmo para eles, e passaram. Tendo saído, caminharam ao longo de uma rua, e de repente, o anjo o deixou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27349, 'nvi', 'Atos', 'atos', 12, 11, 'Então Pedro caiu em si e disse: "Agora sei, sem nenhuma dúvida, que o Senhor enviou o seu anjo e me libertou das mãos de Herodes e de tudo o que o povo judeu esperava".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27350, 'nvi', 'Atos', 'atos', 12, 12, 'Percebendo isso, ele se dirigiu à casa de Maria, mãe de João, também chamado Marcos, onde muita gente se havia reunido e estava orando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27351, 'nvi', 'Atos', 'atos', 12, 13, 'Pedro bateu à porta do alpendre, e uma serva chamada Rode veio atender.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27352, 'nvi', 'Atos', 'atos', 12, 14, 'Ao reconhecer a voz de Pedro, tomada de alegria, ela correu de volta, sem abrir a porta, e exclamou: "Pedro está à porta! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27353, 'nvi', 'Atos', 'atos', 12, 15, 'Eles porém lhe disseram: "Você está fora de si! " Insistindo ela em afirmar que era Pedro, disseram-lhe: "Deve ser o anjo dele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27354, 'nvi', 'Atos', 'atos', 12, 16, 'Mas Pedro continuou batendo e, quando abriram a porta e o viram, ficaram perplexos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27355, 'nvi', 'Atos', 'atos', 12, 17, 'Mas ele, fazendo-lhes sinal para que se calassem, descreveu como o Senhor o havia tirado da prisão e disse: "Contem isso a Tiago e aos irmãos". Então saiu e foi para outro lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27356, 'nvi', 'Atos', 'atos', 12, 18, 'De manhã, não foi pequeno o alvoroço entre os soldados quanto ao que tinha acontecido a Pedro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27357, 'nvi', 'Atos', 'atos', 12, 19, 'Fazendo uma busca completa e não o encontrando, Herodes fez uma investigação entre os guardas e ordenou que fossem executados. Depois Herodes foi da Judéia para Cesaréia e permaneceu ali durante algum tempo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27358, 'nvi', 'Atos', 'atos', 12, 20, 'Ele estava cheio de ira contra o povo de Tiro e Sidom; contudo, eles haviam se reunido e procuravam ter uma audiência com ele. Tendo conseguido o apoio de Blasto, homem de confiança do rei, pediram paz, porque dependiam das terras do rei para obter alimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27359, 'nvi', 'Atos', 'atos', 12, 21, 'No dia marcado, Herodes, vestindo seus trajes reais, sentou-se em seu trono e fez um discurso ao povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27360, 'nvi', 'Atos', 'atos', 12, 22, 'Eles começaram a gritar: "É voz de deus, e não de homem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27361, 'nvi', 'Atos', 'atos', 12, 23, 'Visto que Herodes não glorificou a Deus, imediatamente um anjo do Senhor o feriu; e ele morreu comido por vermes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27362, 'nvi', 'Atos', 'atos', 12, 24, 'Entretanto, a palavra de Deus continuava a crescer e a espalhar-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27363, 'nvi', 'Atos', 'atos', 12, 25, 'Tendo terminado sua missão, Barnabé e Saulo voltaram de Jerusalém, levando consigo João, também chamado Marcos.');
