INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27364, 'nvi', 'Atos', 'atos', 13, 1, 'Na igreja de Antioquia havia profetas e mestres: Barnabé, Simeão, chamado Níger, Lúcio de Cirene, Manaém, que fora criado com Herodes, o tetrarca, e Saulo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27365, 'nvi', 'Atos', 'atos', 13, 2, 'Enquanto adoravam ao Senhor e jejuavam, disse o Espírito Santo: "Separem-me Barnabé e Saulo para a obra a que os tenho chamado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27366, 'nvi', 'Atos', 'atos', 13, 3, 'Assim, depois de jejuar e orar, impuseram-lhes as mãos e os enviaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27367, 'nvi', 'Atos', 'atos', 13, 4, 'Enviados pelo Espírito Santo, desceram a Selêucia e dali navegaram para Chipre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27368, 'nvi', 'Atos', 'atos', 13, 5, 'Chegando em Salamina, proclamaram a palavra de Deus nas sinagogas judaicas. João estava com eles como auxiliar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27369, 'nvi', 'Atos', 'atos', 13, 6, 'Viajaram por toda a ilha, até que chegaram a Pafos. Ali encontraram um judeu, chamado Barjesus, que praticava magia e era falso profeta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27370, 'nvi', 'Atos', 'atos', 13, 7, 'Ele era assessor do procônsul Sérgio Paulo. O procônsul, sendo homem culto, mandou chamar Barnabé e Saulo, porque queria ouvir a palavra de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27371, 'nvi', 'Atos', 'atos', 13, 8, 'Mas Elimas, o mágico ( esse é o significado do seu nome ) opôs-se a eles e tentava desviar da fé o procônsul.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27372, 'nvi', 'Atos', 'atos', 13, 9, 'Então Saulo, também chamado Paulo, cheio do Espírito Santo, olhou firmemente para Elimas e disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27373, 'nvi', 'Atos', 'atos', 13, 10, '"Filho do diabo e inimigo de tudo o que é justo! Você está cheio de toda espécie de engano e maldade. Quando é que vai parar de perverter os retos caminhos do Senhor?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27374, 'nvi', 'Atos', 'atos', 13, 11, 'Saiba agora que a mão do Senhor está contra você, e você ficará cego e incapaz de ver a luz do sol durante algum tempo". Imediatamente vieram sobre ele névoa e escuridão, e ele, tateando, procurava quem o guiasse pela mão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27375, 'nvi', 'Atos', 'atos', 13, 12, 'O procônsul, vendo o que havia acontecido, creu, profundamente impressionado com o ensino do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27376, 'nvi', 'Atos', 'atos', 13, 13, 'De Pafos, Paulo e seus companheiros navegaram para Perge, na Panfília. João os deixou ali e voltou para Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27377, 'nvi', 'Atos', 'atos', 13, 14, 'De Perge prosseguiram até Antioquia da Pisídia. No sábado, entraram na sinagoga e se assentaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27378, 'nvi', 'Atos', 'atos', 13, 15, 'Depois da leitura da Lei e dos Profetas, os chefes da sinagoga lhes mandaram dizer: "Irmãos, se vocês têm uma mensagem de encorajamento para o povo, falem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27379, 'nvi', 'Atos', 'atos', 13, 16, 'Pondo-se de pé, Paulo fez sinal com a mão e disse: "Israelitas e gentios que temem a Deus, ouçam-me!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27380, 'nvi', 'Atos', 'atos', 13, 17, 'O Deus do povo de Israel escolheu nossos antepassados, e exaltou o povo durante a sua permanência no Egito; com grande poder os fez sair daquele país');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27381, 'nvi', 'Atos', 'atos', 13, 18, 'e os aturou no deserto durante cerca de quarenta anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27382, 'nvi', 'Atos', 'atos', 13, 19, 'Ele destruiu sete nações em Canaã e deu a terra delas como herança ao seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27383, 'nvi', 'Atos', 'atos', 13, 20, 'Tudo isso levou cerca de quatrocentos e cinqüenta anos. "Depois disso, ele lhes deu juízes até o tempo do profeta Samuel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27384, 'nvi', 'Atos', 'atos', 13, 21, 'Então o povo pediu um rei, e Deus lhes deu Saul, filho de Quis, da tribo de Benjamim, que reinou quarenta anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27385, 'nvi', 'Atos', 'atos', 13, 22, 'Depois de rejeitar Saul, levantou-lhes Davi como rei, sobre quem testemunhou: ‘Encontrei Davi, filho de Jessé, homem segundo o meu coração; ele fará tudo o que for da minha vontade’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27386, 'nvi', 'Atos', 'atos', 13, 23, '"Da descendência desse homem Deus trouxe a Israel o Salvador Jesus, como prometera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27387, 'nvi', 'Atos', 'atos', 13, 24, 'Antes da vinda de Jesus, João pregou um batismo de arrependimento para todo o povo de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27388, 'nvi', 'Atos', 'atos', 13, 25, 'Quando estava completando sua carreira, João disse: ‘Quem vocês pensam que eu sou? Não sou quem vocês pensam. Mas eis que vem depois de mim aquele cujas sandálias não sou digno nem de desamarrar’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27389, 'nvi', 'Atos', 'atos', 13, 26, '"Irmãos, filhos de Abraão, e gentios que temem a Deus, a nós foi enviada esta mensagem de salvação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27390, 'nvi', 'Atos', 'atos', 13, 27, 'O povo de Jerusalém e seus governantes não reconheceram Jesus, mas, ao condená-lo, cumpriram as palavras dos profetas, que são lidas todos os sábados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27391, 'nvi', 'Atos', 'atos', 13, 28, 'Mesmo não achando motivo legal para uma sentença de morte, pediram a Pilatos que o mandasse executar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27392, 'nvi', 'Atos', 'atos', 13, 29, 'Tendo cumprido tudo o que estava escrito a respeito dele, tiraram-no do madeiro e o colocaram num sepulcro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27393, 'nvi', 'Atos', 'atos', 13, 30, 'Mas Deus o ressuscitou dos mortos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27394, 'nvi', 'Atos', 'atos', 13, 31, 'e, por muitos dias, foi visto por aqueles que tinham ido com ele da Galiléia para Jerusalém. Eles agora são testemunhas dele para o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27395, 'nvi', 'Atos', 'atos', 13, 32, '"Nós lhes anunciamos as boas novas: o que Deus prometeu a nossos antepassados');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27396, 'nvi', 'Atos', 'atos', 13, 33, 'ele cumpriu para nós, seus filhos, ressuscitando Jesus, como está escrito no Salmo segundo: ‘Tu és meu filho; eu hoje te gerei’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27397, 'nvi', 'Atos', 'atos', 13, 34, 'O fato de que Deus o ressuscitou dos mortos, para que nunca entrasse em decomposição, é declarado nestas palavras: ‘Eu lhes dou as santas e fiéis bênçãos prometidas a Davi’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27398, 'nvi', 'Atos', 'atos', 13, 35, 'Assim ele diz noutra passagem: ‘Não permitirás que o teu Santo sofra decomposição’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27399, 'nvi', 'Atos', 'atos', 13, 36, '"Tendo, pois, Davi servido ao propósito de Deus em sua geração, adormeceu, foi sepultado com os seus antepassados e seu corpo se decompôs.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27400, 'nvi', 'Atos', 'atos', 13, 37, 'Mas aquele a quem Deus ressuscitou não sofreu decomposição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27401, 'nvi', 'Atos', 'atos', 13, 38, '"Portanto, meus irmãos, quero que saibam que mediante Jesus lhes é proclamado o perdão dos pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27402, 'nvi', 'Atos', 'atos', 13, 39, 'Por meio dele, todo aquele que crê é justificado de todas as coisas das quais não podiam ser justificados pela lei de Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27403, 'nvi', 'Atos', 'atos', 13, 40, 'Cuidem para que não lhes aconteça o que disseram os profetas:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27404, 'nvi', 'Atos', 'atos', 13, 41, '‘Olhem, escarnecedores, admirem-se e pereçam; pois nos dias de vocês farei algo que vocês jamais creriam se alguém lhes contasse! ’"');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27405, 'nvi', 'Atos', 'atos', 13, 42, 'Quando Paulo e Barnabé estavam saindo da sinagoga, o povo os convidou a falar mais a respeito dessas coisas no sábado seguinte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27406, 'nvi', 'Atos', 'atos', 13, 43, 'Despedida a congregação, muitos dos judeus e estrangeiros piedosos convertidos ao judaísmo seguiram Paulo e Barnabé. Estes conversavam com eles, recomendando-lhes que continuassem na graça de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27407, 'nvi', 'Atos', 'atos', 13, 44, 'No sábado seguinte, quase toda a cidade se reuniu para ouvir a palavra do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27408, 'nvi', 'Atos', 'atos', 13, 45, 'Quando os judeus viram a multidão, ficaram cheios de inveja e, blasfemando, contradiziam o que Paulo estava dizendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27409, 'nvi', 'Atos', 'atos', 13, 46, 'Então Paulo e Barnabé lhes responderam corajosamente: "Era necessário anunciar primeiro a vocês a palavra de Deus; uma vez que a rejeitam e não se julgam dignos da vida eterna, agora nos voltamos para os gentios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27410, 'nvi', 'Atos', 'atos', 13, 47, 'Pois assim o Senhor nos ordenou: ‘Eu fiz de você luz para os gentios, para que você leve a salvação até aos confins da terra’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27411, 'nvi', 'Atos', 'atos', 13, 48, 'Ouvindo isso, os gentios alegraram-se e bendisseram a palavra do Senhor; e creram todos os que haviam sido designados para a vida eterna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27412, 'nvi', 'Atos', 'atos', 13, 49, 'A palavra do Senhor se espalhava por toda a região.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27413, 'nvi', 'Atos', 'atos', 13, 50, 'Mas os judeus incitaram as mulheres piedosas de elevada posição e os principais da cidade. E, provocando perseguição contra Paulo e Barnabé, os expulsaram do seu território.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27414, 'nvi', 'Atos', 'atos', 13, 51, 'Estes sacudiram o pó dos seus pés em protesto contra eles e foram para Icônio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27415, 'nvi', 'Atos', 'atos', 13, 52, 'Os discípulos continuavam cheios de alegria e do Espírito Santo.');
