INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27444, 'nvi', 'Atos', 'atos', 15, 1, 'Alguns homens desceram da Judéia para Antioquia e passaram a ensinar aos irmãos: "Se vocês não forem circuncidados conforme o costume ensinado por Moisés, não poderão ser salvos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27445, 'nvi', 'Atos', 'atos', 15, 2, 'Isso levou Paulo e Barnabé a uma grande contenda e discussão com eles. Assim, Paulo e Barnabé foram designados, juntamente com outros, para irem a Jerusalém tratar dessa questão com os apóstolos e com os presbíteros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27446, 'nvi', 'Atos', 'atos', 15, 3, 'A igreja os enviou e, ao passarem pela Fenícia e por Samaria, contaram como os gentios tinham se convertido; essas notícias alegravam muito a todos os irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27447, 'nvi', 'Atos', 'atos', 15, 4, 'Chegando a Jerusalém, foram bem recebidos pela igreja, pelos apóstolos e pelos presbíteros, a quem relataram tudo o que Deus tinha feito por meio deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27448, 'nvi', 'Atos', 'atos', 15, 5, 'Então se levantaram alguns do partido religioso dos fariseus que haviam crido e disseram: "É necessário circuncidá-los e exigir deles que obedeçam à lei de Moisés".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27449, 'nvi', 'Atos', 'atos', 15, 6, 'Os apóstolos e os presbíteros se reuniram para considerar essa questão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27450, 'nvi', 'Atos', 'atos', 15, 7, 'Depois de muita discussão, Pedro levantou-se e dirigiu-se a eles: "Irmãos, vocês sabem que há muito tempo Deus me escolheu dentre vocês para que os gentios ouvissem de meus lábios a mensagem do evangelho e cressem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27451, 'nvi', 'Atos', 'atos', 15, 8, 'Deus, que conhece os corações, demonstrou que os aceitou, dando-lhes o Espírito Santo, como antes nos tinha concedido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27452, 'nvi', 'Atos', 'atos', 15, 9, 'Ele não fez distinção alguma entre nós e eles, visto que purificou os seus corações pela fé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27453, 'nvi', 'Atos', 'atos', 15, 10, 'Então, por que agora vocês estão querendo tentar a Deus, impondo sobre os discípulos um jugo que nem nós nem nossos antepassados conseguimos suportar?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27454, 'nvi', 'Atos', 'atos', 15, 11, 'De modo nenhum! Cremos que somos salvos pela graça de nosso Senhor Jesus, assim como eles também".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27455, 'nvi', 'Atos', 'atos', 15, 12, 'Toda a assembléia ficou em silêncio, enquanto ouvia Barnabé e Paulo falando de todos os sinais e maravilhas que, por meio deles, Deus fizera entre os gentios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27456, 'nvi', 'Atos', 'atos', 15, 13, 'Quando terminaram de falar, Tiago tomou a palavra e disse: "Irmãos, ouçam-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27457, 'nvi', 'Atos', 'atos', 15, 14, 'Simão nos expôs como Deus, no princípio, voltou-se para os gentios a fim de reunir dentre as nações um povo para o seu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27458, 'nvi', 'Atos', 'atos', 15, 15, 'Concordam com isso as palavras dos profetas, conforme está escrito:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27459, 'nvi', 'Atos', 'atos', 15, 16, '‘Depois disso voltarei e reconstruirei a tenda caída de Davi. Reedificarei as suas ruínas, e a restaurarei,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27460, 'nvi', 'Atos', 'atos', 15, 17, 'para que o restante dos homens busque o Senhor, e todos os gentios sobre os quais tem sido invocado o meu nome, diz o Senhor, que faz estas coisas’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27461, 'nvi', 'Atos', 'atos', 15, 18, 'conhecidas desde os tempos antigos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27462, 'nvi', 'Atos', 'atos', 15, 19, '"Portanto, julgo que não devemos pôr dificuldades aos gentios que estão se convertendo a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27463, 'nvi', 'Atos', 'atos', 15, 20, 'Pelo contrário, devemos escrever a eles, dizendo-lhes que se abstenham de comida contaminada pelos ídolos, da imoralidade sexual, da carne de animais estrangulados e do sangue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27464, 'nvi', 'Atos', 'atos', 15, 21, 'Pois, desde os tempos antigos, Moisés é pregado em todas as cidades, sendo lido nas sinagogas todos os sábados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27465, 'nvi', 'Atos', 'atos', 15, 22, 'Então os apóstolos e os presbíteros, com toda a igreja, decidiram escolher alguns dentre eles e enviá-los a Antioquia com Paulo e Barnabé. Escolheram Judas, chamado Barsabás, e Silas, dois líderes entre os irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27466, 'nvi', 'Atos', 'atos', 15, 23, 'Com eles enviaram a seguinte carta: Os irmãos apóstolos e presbíteros, aos cristãos gentios que estão em Antioquia, na Síria e na Cilícia: Saudações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27467, 'nvi', 'Atos', 'atos', 15, 24, 'Soubemos que alguns saíram de nosso meio, sem nossa autorização, e os perturbaram, transtornando suas mentes com o que disseram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27468, 'nvi', 'Atos', 'atos', 15, 25, 'Assim, concordamos todos em escolher alguns homens e enviá-los a vocês com nossos amados irmãos Paulo e Barnabé,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27469, 'nvi', 'Atos', 'atos', 15, 26, 'homens que têm arriscado a vida pelo nome de nosso Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27470, 'nvi', 'Atos', 'atos', 15, 27, 'Portanto, estamos enviando Judas e Silas para confirmarem verbalmente o que estamos escrevendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27471, 'nvi', 'Atos', 'atos', 15, 28, 'Pareceu bem ao Espírito Santo e a nós não impor a vocês nada além das seguintes exigências necessárias:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27472, 'nvi', 'Atos', 'atos', 15, 29, 'Abster-se de comida sacrificada aos ídolos, do sangue, da carne de animais estrangulados e da imoralidade sexual. Vocês farão bem em evitar essas coisas. Que tudo lhes vá bem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27473, 'nvi', 'Atos', 'atos', 15, 30, 'Uma vez despedidos, os homens desceram para Antioquia, onde reuniram a igreja e entregaram a carta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27474, 'nvi', 'Atos', 'atos', 15, 31, 'Os irmãos a leram e se alegraram com a sua animadora mensagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27475, 'nvi', 'Atos', 'atos', 15, 32, 'Judas e Silas, que eram profetas, encorajaram e fortaleceram os irmãos com muitas palavras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27476, 'nvi', 'Atos', 'atos', 15, 33, 'Tendo passado algum tempo ali, foram despedidos pelos irmãos com a bênção da paz para voltarem aos que os tinham enviado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27477, 'nvi', 'Atos', 'atos', 15, 34, 'Mas Silas decidiu ficar ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27478, 'nvi', 'Atos', 'atos', 15, 35, 'Mas Paulo e Barnabé permaneceram em Antioquia, onde, com muitos outros ensinavam e pregavam a palavra do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27479, 'nvi', 'Atos', 'atos', 15, 36, 'Algum tempo depois, Paulo disse a Barnabé: "Voltemos para visitar os irmãos em todas as cidades onde pregamos a palavra do Senhor, para ver como estão indo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27480, 'nvi', 'Atos', 'atos', 15, 37, 'Barnabé queria levar João, também chamado Marcos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27481, 'nvi', 'Atos', 'atos', 15, 38, 'Mas Paulo não achava prudente levá-lo, pois ele, abandonando-os na Panfília, não permanecera com eles no trabalho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27482, 'nvi', 'Atos', 'atos', 15, 39, 'Tiveram um desentendimento tão sério que se separaram. Barnabé, levando consigo Marcos, navegou para Chipre,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27483, 'nvi', 'Atos', 'atos', 15, 40, 'mas Paulo escolheu Silas e partiu, encomendado pelos irmãos à graça do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27484, 'nvi', 'Atos', 'atos', 15, 41, 'Passou, então, pela Síria e pela Cilícia, fortalecendo as igrejas.');
