INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27485, 'nvi', 'Atos', 'atos', 16, 1, 'Chegou a Derbe e depois a Listra, onde vivia um discípulo chamado Timóteo. Sua mãe era uma judia convertida e seu pai era grego.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27486, 'nvi', 'Atos', 'atos', 16, 2, 'Os irmãos de Listra e Icônio davam bom testemunho dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27487, 'nvi', 'Atos', 'atos', 16, 3, 'Paulo, querendo levá-lo na viagem, circuncidou-o por causa dos judeus que viviam naquela região, pois todos sabiam que seu pai era grego.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27488, 'nvi', 'Atos', 'atos', 16, 4, 'Nas cidades por onde passavam, transmitiam as decisões tomadas pelos apóstolos e presbíteros em Jerusalém, para que fossem obedecidas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27489, 'nvi', 'Atos', 'atos', 16, 5, 'Assim as igrejas eram fortalecidas na fé e cresciam em número cada dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27490, 'nvi', 'Atos', 'atos', 16, 6, 'Paulo e seus companheiros viajaram pela região da Frígia e da Galácia, tendo sido impedidos pelo Espírito Santo de pregar a palavra na província da Ásia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27491, 'nvi', 'Atos', 'atos', 16, 7, 'Quando chegaram à fronteira da Mísia, tentaram entrar na Bitínia, mas o Espírito de Jesus os impediu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27492, 'nvi', 'Atos', 'atos', 16, 8, 'Então, contornaram a Mísia e desceram a Trôade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27493, 'nvi', 'Atos', 'atos', 16, 9, 'Durante a noite Paulo teve uma visão, na qual um homem da Macedônia estava em pé e lhe suplicava: "Passe à Macedônia e ajude-nos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27494, 'nvi', 'Atos', 'atos', 16, 10, 'Depois que Paulo teve essa visão, preparamo-nos imediatamente para partir para a Macedônia, concluindo que Deus nos tinha chamado para lhes pregar o evangelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27495, 'nvi', 'Atos', 'atos', 16, 11, 'Partindo de Trôade, navegamos diretamente para Samotrácia e, no dia seguinte, para Neápolis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27496, 'nvi', 'Atos', 'atos', 16, 12, 'Dali partimos para Filipos, na Macedônia, que é colônia romana e a principal cidade daquele distrito. Ali ficamos vários dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27497, 'nvi', 'Atos', 'atos', 16, 13, 'No sábado saímos da cidade e fomos para a beira do rio, onde esperávamos encontrar um lugar de oração. Sentamo-nos e começamos a conversar com as mulheres que se haviam reunido ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27498, 'nvi', 'Atos', 'atos', 16, 14, 'Uma das que ouviam era uma mulher temente a Deus chamada Lídia, vendedora de tecido de púrpura, da cidade de Tiatira. O Senhor abriu seu coração para atender à mensagem de Paulo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27499, 'nvi', 'Atos', 'atos', 16, 15, 'Tendo sido batizada, bem como os de sua casa, ela nos convidou, dizendo: "Se os senhores me consideram uma crente no Senhor, venham ficar em minha casa". E nos convenceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27500, 'nvi', 'Atos', 'atos', 16, 16, 'Certo dia, indo nós para o lugar de oração, encontramos uma escrava que tinha um espírito pelo qual predizia o futuro. Ela ganhava muito dinheiro para os seus senhores com adivinhações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27501, 'nvi', 'Atos', 'atos', 16, 17, 'Essa moça seguia a Paulo e a nós, gritando: "Estes homens são servos do Deus Altíssimo e lhes anunciam o caminho da salvação".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27502, 'nvi', 'Atos', 'atos', 16, 18, 'Ela continuou fazendo isso por muitos dias. Finalmente, Paulo ficou indignado, voltou-se e disse ao espírito: "Em nome de Jesus Cristo eu lhe ordeno que saia dela! " No mesmo instante o espírito a deixou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27503, 'nvi', 'Atos', 'atos', 16, 19, 'Percebendo que a sua esperança de lucro tinha se acabado, os donos da escrava agarraram Paulo e Silas e os arrastaram para a praça principal, diante das autoridades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27504, 'nvi', 'Atos', 'atos', 16, 20, 'E, levando-os aos magistrados, disseram: "Estes homens são judeus e estão perturbando a nossa cidade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27505, 'nvi', 'Atos', 'atos', 16, 21, 'propagando costumes que a nós, romanos, não é permitido aceitar nem praticar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27506, 'nvi', 'Atos', 'atos', 16, 22, 'A multidão ajuntou-se contra Paulo e Silas, e os magistrados ordenaram que se lhes tirassem as roupas e fossem açoitados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27507, 'nvi', 'Atos', 'atos', 16, 23, 'Depois de serem severamente açoitados, foram lançados na prisão. O carcereiro recebeu instrução para vigiá-los com cuidado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27508, 'nvi', 'Atos', 'atos', 16, 24, 'Tendo recebido tais ordens, ele os lançou no cárcere interior e lhes prendeu os pés no tronco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27509, 'nvi', 'Atos', 'atos', 16, 25, 'Por volta da meia-noite, Paulo e Silas estavam orando e cantando hinos a Deus; os outros presos os ouviam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27510, 'nvi', 'Atos', 'atos', 16, 26, 'De repente, houve um terremoto tão violento que os alicerces da prisão foram abalados. Imediatamente todas as portas se abriram, e as correntes de todos se soltaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27511, 'nvi', 'Atos', 'atos', 16, 27, 'O carcereiro acordou e, vendo abertas as portas da prisão, desembainhou sua espada para se matar, porque pensava que os presos tivessem fugido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27512, 'nvi', 'Atos', 'atos', 16, 28, 'Mas Paulo gritou: "Não faça isso! Estamos todos aqui! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27513, 'nvi', 'Atos', 'atos', 16, 29, 'O carcereiro pediu luz, entrou correndo e, trêmulo, prostrou-se diante de Paulo e Silas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27514, 'nvi', 'Atos', 'atos', 16, 30, 'Então levou-os para fora e perguntou: "Senhores, que devo fazer para ser salvo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27515, 'nvi', 'Atos', 'atos', 16, 31, 'Eles responderam: "Creia no Senhor Jesus, e serão salvos, você e os de sua casa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27516, 'nvi', 'Atos', 'atos', 16, 32, 'E pregaram a palavra de Deus, a ele e a todos os de sua casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27517, 'nvi', 'Atos', 'atos', 16, 33, 'Naquela mesma hora da noite o carcereiro lavou as feridas deles; em seguida, ele e todos os seus foram batizados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27518, 'nvi', 'Atos', 'atos', 16, 34, 'Então os levou para a sua casa, serviu-lhes uma refeição e com todos os de sua casa alegrou-se muito por haver crido em Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27519, 'nvi', 'Atos', 'atos', 16, 35, 'Quando amanheceu, os magistrados mandaram os seus soldados ao carcereiro com esta ordem: "Solte estes homens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27520, 'nvi', 'Atos', 'atos', 16, 36, 'O carcereiro disse a Paulo: "Os magistrados deram ordens para que você e Silas sejam libertados. Agora podem sair. Vão em paz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27521, 'nvi', 'Atos', 'atos', 16, 37, 'Mas Paulo disse aos soldados: "Sendo nós cidadãos romanos, eles nos açoitaram publicamente sem processo formal e nos lançaram na prisão. E agora querem livrar-se de nós secretamente? Não! Venham eles mesmos e nos libertem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27522, 'nvi', 'Atos', 'atos', 16, 38, 'Os soldados relataram isso aos magistrados, os quais, ouvindo que Paulo e Silas eram romanos, ficaram atemorizados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27523, 'nvi', 'Atos', 'atos', 16, 39, 'Vieram para se desculpar diante deles e, conduzindo-os para fora da prisão, pediram-lhes que saíssem da cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27524, 'nvi', 'Atos', 'atos', 16, 40, 'Depois de saírem da prisão, Paulo e Silas foram à casa de Lídia, onde se encontraram com os irmãos e os encorajaram. E então partiram.');
