INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27525, 'nvi', 'Atos', 'atos', 17, 1, 'Tendo passado por Anfípolis e Apolônia, chegaram a Tessalônica, onde havia uma sinagoga judaica.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27526, 'nvi', 'Atos', 'atos', 17, 2, 'Segundo o seu costume, Paulo foi à sinagoga e por três sábados discutiu com eles com base nas Escrituras,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27527, 'nvi', 'Atos', 'atos', 17, 3, 'explicando e provando que o Cristo deveria sofrer e ressuscitar dentre os mortos. E dizia: "Este Jesus que lhes proclamo é o Cristo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27528, 'nvi', 'Atos', 'atos', 17, 4, 'Alguns dos judeus foram persuadidos e se uniram a Paulo e Silas, bem como muitos gregos tementes a Deus, e não poucas mulheres de alta posição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27529, 'nvi', 'Atos', 'atos', 17, 5, 'Mas os judeus ficaram com inveja. Reuniram alguns homens perversos dentre os desocupados e, com a multidão, iniciaram um tumulto na cidade. Invadiram a casa de Jasom, em busca de Paulo e Silas, a fim de trazê-los para o meio da multidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27530, 'nvi', 'Atos', 'atos', 17, 6, 'Contudo, não os achando, arrastaram Jasom e alguns outros irmãos para diante dos oficiais da cidade, gritando: "Esses homens que têm causado alvoroço por todo o mundo, agora chegaram aqui,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27531, 'nvi', 'Atos', 'atos', 17, 7, 'e Jasom os recebeu em sua casa. Todos eles estão agindo contra os decretos de César, dizendo que existe um outro rei, chamado Jesus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27532, 'nvi', 'Atos', 'atos', 17, 8, 'Ouvindo isso, a multidão e os oficiais da cidade ficaram agitados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27533, 'nvi', 'Atos', 'atos', 17, 9, 'Então receberam de Jasom e dos outros a fiança estipulada e os soltaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27534, 'nvi', 'Atos', 'atos', 17, 10, 'Logo que anoiteceu, os irmãos enviaram Paulo e Silas para Beréia. Chegando ali, eles foram à sinagoga judaica.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27535, 'nvi', 'Atos', 'atos', 17, 11, 'Os bereanos eram mais nobres do que os tessalonicenses, pois receberam a mensagem com grande interesse, examinando todos os dias as Escrituras, para ver se tudo era assim mesmo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27536, 'nvi', 'Atos', 'atos', 17, 12, 'E creram muitos dentre os judeus, bem como dentre os gregos, um bom número de mulheres de elevada posição e não poucos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27537, 'nvi', 'Atos', 'atos', 17, 13, 'Quando os judeus de Tessalônica ficaram sabendo que Paulo estava pregando a palavra de Deus em Beréia, dirigiram-se também para lá, agitando e alvoroçando as multidões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27538, 'nvi', 'Atos', 'atos', 17, 14, 'Imediatamente os irmãos enviaram Paulo para o litoral, mas Silas e Timóteo permaneceram em Beréia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27539, 'nvi', 'Atos', 'atos', 17, 15, 'Os homens que foram com Paulo o levaram até Atenas, partindo depois com instruções para que Silas e Timóteo se juntassem a ele, tão logo fosse possível.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27540, 'nvi', 'Atos', 'atos', 17, 16, 'Enquanto esperava por eles em Atenas, Paulo ficou profundamente indignado ao ver que a cidade estava cheia de ídolos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27541, 'nvi', 'Atos', 'atos', 17, 17, 'Por isso, discutia na sinagoga com judeus e com gregos tementes a Deus, bem como na praça principal, todos os dias, com aqueles que por ali se encontravam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27542, 'nvi', 'Atos', 'atos', 17, 18, 'Alguns filósofos epicureus e estóicos começaram a discutir com ele. Alguns perguntavam: "O que está tentando dizer esse tagarela? " Outros diziam: "Parece que ele está anunciando deuses estrangeiros", pois Paulo estava pregando as boas novas a respeito de Jesus e da ressurreição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27543, 'nvi', 'Atos', 'atos', 17, 19, 'Então o levaram a uma reunião do Areópago, onde lhe perguntaram: "Podemos saber que novo ensino é esse que você está anunciando?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27544, 'nvi', 'Atos', 'atos', 17, 20, 'Você está nos apresentando algumas idéias estranhas, e queremos saber o que elas significam".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27545, 'nvi', 'Atos', 'atos', 17, 21, 'Todos os atenienses e estrangeiros que ali viviam não cuidavam de outra coisa senão falar ou ouvir as últimas novidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27546, 'nvi', 'Atos', 'atos', 17, 22, 'Então Paulo levantou-se na reunião do Areópago e disse: "Atenienses! Vejo que em todos os aspectos vocês são muito religiosos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27547, 'nvi', 'Atos', 'atos', 17, 23, 'pois, andando pela cidade, observei cuidadosamente seus objetos de culto e encontrei até um altar com esta inscrição: AO DEUS DESCONHECIDO. Ora, o que vocês adoram, apesar de não conhecerem, eu lhes anuncio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27548, 'nvi', 'Atos', 'atos', 17, 24, '"O Deus que fez o mundo e tudo o que nele há é o Senhor do céu e da terra, e não habita em santuários feitos por mãos humanas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27549, 'nvi', 'Atos', 'atos', 17, 25, 'Ele não é servido por mãos de homens, como se necessitasse de algo, porque ele mesmo dá a todos a vida, o fôlego e as demais coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27550, 'nvi', 'Atos', 'atos', 17, 26, 'De um só fez ele todos os povos, para que povoassem toda a terra, tendo determinado os tempos anteriormente estabelecidos e os lugares exatos em que deveriam habitar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27551, 'nvi', 'Atos', 'atos', 17, 27, 'Deus fez isso para que os homens o buscassem e talvez, tateando, pudessem encontrá-lo, embora não esteja longe de cada um de nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27552, 'nvi', 'Atos', 'atos', 17, 28, '‘Pois nele vivemos, nos movemos e existimos’, como disseram alguns dos poetas de vocês: ‘Também somos descendência dele’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27553, 'nvi', 'Atos', 'atos', 17, 29, '"Assim, visto que somos descendência de Deus, não devemos pensar que a Divindade é semelhante a uma escultura de ouro, prata ou pedra, feita pela arte e imaginação do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27554, 'nvi', 'Atos', 'atos', 17, 30, 'No passado Deus não levou em conta essa ignorância, mas agora ordena que todos, em todo lugar, se arrependam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27555, 'nvi', 'Atos', 'atos', 17, 31, 'Pois estabeleceu um dia em que há de julgar o mundo com justiça, por meio do homem que designou. E deu provas disso a todos, ressuscitando-o dentre os mortos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27556, 'nvi', 'Atos', 'atos', 17, 32, 'Quando ouviram sobre a ressurreição dos mortos, alguns deles zombaram, e outros disseram: "A esse respeito nós o ouviremos outra vez".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27557, 'nvi', 'Atos', 'atos', 17, 33, 'Com isso, Paulo retirou-se do meio deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27558, 'nvi', 'Atos', 'atos', 17, 34, 'Alguns homens juntaram-se a ele e creram. Entre eles estava Dionísio, membro do Areópago, e também uma mulher chamada Dâmaris, e outros com eles.');
