INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27559, 'nvi', 'Atos', 'atos', 18, 1, 'Depois disso Paulo saiu de Atenas e foi para Corinto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27560, 'nvi', 'Atos', 'atos', 18, 2, 'Ali, encontrou um judeu chamado Áqüila, natural do Ponto, que havia chegado recentemente da Itália com Priscila, sua mulher, pois Cláudio havia ordenado que todos os judeus saíssem de Roma. Paulo foi vê-los');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27561, 'nvi', 'Atos', 'atos', 18, 3, 'e, uma vez que tinham a mesma profissão, ficou morando e trabalhando com eles, pois eram fabricantes de tendas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27562, 'nvi', 'Atos', 'atos', 18, 4, 'Todos os sábados ele debatia na sinagoga, e convencia judeus e gregos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27563, 'nvi', 'Atos', 'atos', 18, 5, 'Depois que Silas e Timóteo chegaram da Macedônia, Paulo se dedicou exclusivamente à pregação, testemunhando aos judeus que Jesus era o Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27564, 'nvi', 'Atos', 'atos', 18, 6, 'Opondo-se eles e lançando maldições, Paulo sacudiu a roupa e lhes disse: "Caia sobre a cabeça de vocês o seu próprio sangue! Estou livre da minha responsabilidade. De agora em diante irei para os gentios".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27565, 'nvi', 'Atos', 'atos', 18, 7, 'Então Paulo saiu da sinagoga e foi para a casa de Tício Justo, que era temente a Deus e que morava ao lado da sinagoga.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27566, 'nvi', 'Atos', 'atos', 18, 8, 'Crispo, chefe da sinagoga, creu no Senhor, ele e toda a sua casa; e dos coríntios que o ouviam, muitos criam e eram batizados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27567, 'nvi', 'Atos', 'atos', 18, 9, 'Certa noite o Senhor falou a Paulo em visão: "Não tenha medo, continue falando e não fique calado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27568, 'nvi', 'Atos', 'atos', 18, 10, 'pois estou com você, e ninguém vai lhe fazer mal ou feri-lo, porque tenho muita gente nesta cidade".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27569, 'nvi', 'Atos', 'atos', 18, 11, 'Assim, Paulo ficou ali durante um ano e meio, ensinando-lhes a palavra de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27570, 'nvi', 'Atos', 'atos', 18, 12, 'Sendo Gálio procônsul da Acaia, os judeus fizeram em conjunto um levante contra Paulo e o levaram ao tribunal, fazendo a seguinte acusação:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27571, 'nvi', 'Atos', 'atos', 18, 13, '"Este homem está persuadindo o povo a adorar a Deus de maneira contrária à lei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27572, 'nvi', 'Atos', 'atos', 18, 14, 'Quando Paulo ia começar a falar, Gálio disse aos judeus: "Se vocês, judeus, estivessem apresentando queixa de algum delito ou crime grave, seria razoável que eu os ouvisse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27573, 'nvi', 'Atos', 'atos', 18, 15, 'Mas, visto que se trata de uma questão de palavras e nomes de sua própria lei, resolvam o problema vocês mesmos. Não serei juiz dessas coisas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27574, 'nvi', 'Atos', 'atos', 18, 16, 'E mandou expulsá-los do tribunal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27575, 'nvi', 'Atos', 'atos', 18, 17, 'Então todos se voltaram contra Sóstenes, o chefe da sinagoga, e o espancaram diante do tribunal. Mas Gálio não demonstrava nenhuma preocupação com isso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27576, 'nvi', 'Atos', 'atos', 18, 18, 'Paulo permaneceu em Corinto por algum tempo. Depois despediu-se dos irmãos e navegou para a Síria, acompanhado de Priscila e Áqüila. Antes de embarcar, rapou a cabeça em Cencréia, devido a um voto que havia feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27577, 'nvi', 'Atos', 'atos', 18, 19, 'Chegaram a Éfeso, onde Paulo deixou Priscila e Áqüila. Ele, porém, entrando na sinagoga, começou a debater com os judeus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27578, 'nvi', 'Atos', 'atos', 18, 20, 'Pedindo eles que ficasse mais tempo, não cedeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27579, 'nvi', 'Atos', 'atos', 18, 21, 'Mas, ao partir, prometeu: "Voltarei, se for da vontade de Deus". Então, embarcando, partiu de Éfeso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27580, 'nvi', 'Atos', 'atos', 18, 22, 'Ao chegar a Cesaréia, subiu até a igreja para saudá-la, e depois desceu para Antioquia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27581, 'nvi', 'Atos', 'atos', 18, 23, 'Depois de passar algum tempo em Antioquia, Paulo partiu dali e viajou por toda a região da Galácia e da Frígia, fortalecendo todos os discípulos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27582, 'nvi', 'Atos', 'atos', 18, 24, 'Enquanto isso, um judeu chamado Apolo, natural de Alexandria, chegou a Éfeso. Ele era homem culto e tinha grande conhecimento das Escrituras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27583, 'nvi', 'Atos', 'atos', 18, 25, 'Fora instruído no caminho do Senhor e com grande fervor falava e ensinava com exatidão acerca de Jesus, embora conhecesse apenas o batismo de João.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27584, 'nvi', 'Atos', 'atos', 18, 26, 'Logo começou a falar corajosamente na sinagoga. Quando Priscila e Áqüila o ouviram, convidaram-no para ir à sua casa e lhe explicaram com mais exatidão o caminho de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27585, 'nvi', 'Atos', 'atos', 18, 27, 'Querendo ele ir para a Acaia, os irmãos o encorajaram e escreveram aos discípulos que o recebessem. Ao chegar, ele auxiliou muito os que pela graça haviam crido,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27586, 'nvi', 'Atos', 'atos', 18, 28, 'pois refutava vigorosamente os judeus em debate público, provando pelas Escrituras que Jesus é o Cristo.');
