INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27587, 'nvi', 'Atos', 'atos', 19, 1, 'Enquanto Apolo estava em Corinto, Paulo, atravessando as regiões altas, chegou a Éfeso. Ali encontrou alguns discípulos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27588, 'nvi', 'Atos', 'atos', 19, 2, 'e lhes perguntou: "Vocês receberam o Espírito Santo quando creram? " Eles responderam: "Não, nem sequer ouvimos que existe o Espírito Santo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27589, 'nvi', 'Atos', 'atos', 19, 3, '"Então, que batismo vocês receberam? ", perguntou Paulo. "O batismo de João", responderam eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27590, 'nvi', 'Atos', 'atos', 19, 4, 'Disse Paulo: "O batismo de João foi um batismo de arrependimento. Ele dizia ao povo que cresse naquele que viria depois dele, isto é, em Jesus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27591, 'nvi', 'Atos', 'atos', 19, 5, 'Ouvindo isso, eles foram batizados no nome do Senhor Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27592, 'nvi', 'Atos', 'atos', 19, 6, 'Quando Paulo lhes impôs as mãos, veio sobre eles o Espírito Santo, e começaram a falar em línguas e a profetizar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27593, 'nvi', 'Atos', 'atos', 19, 7, 'Eram ao todo uns doze homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27594, 'nvi', 'Atos', 'atos', 19, 8, 'Paulo entrou na sinagoga e ali falou com liberdade durante três meses, argumentando convincentemente acerca do Reino de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27595, 'nvi', 'Atos', 'atos', 19, 9, 'Mas alguns deles se endureceram e se recusaram a crer, e começaram a falar mal do Caminho diante da multidão. Paulo, então, afastou-se deles. Tomando consigo os discípulos, passou a ensinar diariamente na escola de Tirano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27596, 'nvi', 'Atos', 'atos', 19, 10, 'Isso continuou por dois anos, de forma que todos os judeus e os gregos que viviam na província da Ásia ouviram a palavra do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27597, 'nvi', 'Atos', 'atos', 19, 11, 'Deus fazia milagres extraordinários por meio de Paulo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27598, 'nvi', 'Atos', 'atos', 19, 12, 'de modo que até lenços e aventais que Paulo usava eram levados e colocados sobre os enfermos. Estes eram curados de suas doenças, e os espíritos malignos saíam deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27599, 'nvi', 'Atos', 'atos', 19, 13, 'Alguns judeus que andavam expulsando espíritos malignos tentaram invocar o nome do Senhor Jesus sobre os endemoninhados, dizendo: "Em nome de Jesus, a quem Paulo prega, eu lhes ordeno que saiam! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27600, 'nvi', 'Atos', 'atos', 19, 14, 'Os que estavam fazendo isso eram os sete filhos de Ceva, um dos chefes dos sacerdotes dos judeus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27601, 'nvi', 'Atos', 'atos', 19, 15, 'Um dia, o espírito maligno lhes respondeu: "Jesus, eu conheço, Paulo, eu sei quem é; mas vocês, quem são? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27602, 'nvi', 'Atos', 'atos', 19, 16, 'Então o endemoninhado saltou sobre eles e os dominou, espancando-os com tamanha violência que eles fugiram da casa nus e feridos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27603, 'nvi', 'Atos', 'atos', 19, 17, 'Quando isso se tornou conhecido de todos os judeus e os gregos que viviam em Éfeso, todos eles foram tomados de temor; e o nome do Senhor Jesus era engrandecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27604, 'nvi', 'Atos', 'atos', 19, 18, 'Muitos dos que creram vinham, e confessavam e declaravam abertamente suas más obras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27605, 'nvi', 'Atos', 'atos', 19, 19, 'Grande número dos que tinham praticado ocultismo reuniram seus livros e os queimaram publicamente. Calculado o valor total, este chegou a cinqüenta mil dracmas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27606, 'nvi', 'Atos', 'atos', 19, 20, 'Dessa maneira a palavra do Senhor muito se difundia e se fortalecia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27607, 'nvi', 'Atos', 'atos', 19, 21, 'Depois dessas coisas, Paulo decidiu no espírito ir a Jerusalém, passando pela Macedônia e pela Acaia. Ele dizia: "Depois de haver estado ali, é necessário também que eu vá visitar Roma".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27608, 'nvi', 'Atos', 'atos', 19, 22, 'Então enviou à Macedônia dois dos seus auxiliares, Timóteo e Erasto, e permaneceu mais um pouco na província da Ásia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27609, 'nvi', 'Atos', 'atos', 19, 23, 'Naquele tempo houve um grande tumulto por causa do Caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27610, 'nvi', 'Atos', 'atos', 19, 24, 'Um ourives chamado Demétrio, que fazia miniaturas de prata do templo de Ártemis e que dava muito lucro aos artífices,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27611, 'nvi', 'Atos', 'atos', 19, 25, 'reuniu-os juntamente com os trabalhadores dessa profissão e disse: "Senhores, vocês sabem que temos uma boa fonte de lucro nesta atividade');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27612, 'nvi', 'Atos', 'atos', 19, 26, 'e estão vendo e ouvindo como este indivíduo, Paulo, está convencendo e desviando grande número de pessoas aqui em Éfeso e em quase toda a província da Ásia. Diz ele que deuses feitos por mãos humanas não são deuses.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27613, 'nvi', 'Atos', 'atos', 19, 27, 'Não somente há o perigo de nossa profissão perder sua reputação, mas também de o templo da grande deusa Ártemis cair em descrédito e de a própria deusa, adorada em toda a província da Ásia e em todo o mundo, ser destituída de sua majestade divina".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27614, 'nvi', 'Atos', 'atos', 19, 28, 'Ao ouvirem isso, eles ficaram furiosos e começaram a gritar: "Grande é a Ártemis dos efésios! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27615, 'nvi', 'Atos', 'atos', 19, 29, 'Em pouco tempo a cidade toda estava em tumulto. O povo foi às pressas para o teatro, arrastando os companheiros de viagem de Paulo, os macedônios Gaio e Aristarco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27616, 'nvi', 'Atos', 'atos', 19, 30, 'Paulo queria apresentar-se à multidão, mas os discípulos não o permitiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27617, 'nvi', 'Atos', 'atos', 19, 31, 'Alguns amigos de Paulo dentre as autoridades da província chegaram a mandar-lhe um recado, pedindo-lhe que não se arriscasse a ir ao teatro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27618, 'nvi', 'Atos', 'atos', 19, 32, 'A assembléia estava em confusão: uns gritavam uma coisa, outros gritavam outra. A maior parte do povo nem sabia por que estava ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27619, 'nvi', 'Atos', 'atos', 19, 33, 'Alguns da multidão julgaram que Alexandre era a causa do tumulto, quando os judeus o empurraram para frente. Ele fez sinal pedindo silêncio, com a intenção de fazer sua defesa diante do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27620, 'nvi', 'Atos', 'atos', 19, 34, 'Mas quando ficaram sabendo que ele era judeu, todos gritaram a uma só voz durante cerca de duas horas: "Grande é a Ártemis dos efésios! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27621, 'nvi', 'Atos', 'atos', 19, 35, 'O escrivão da cidade acalmou a multidão e disse: "Efésios, quem não sabe que a cidade de Éfeso é a guardiã do templo da grande Ártemis e da sua imagem que caiu do céu?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27622, 'nvi', 'Atos', 'atos', 19, 36, 'Portanto, visto que estes fatos são inegáveis, acalmem-se e não façam nada precipitadamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27623, 'nvi', 'Atos', 'atos', 19, 37, 'Vocês trouxeram estes homens aqui, embora eles não tenham roubado templos nem blasfemado contra a nossa deusa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27624, 'nvi', 'Atos', 'atos', 19, 38, 'Se Demétrio e seus companheiros de profissão têm alguma queixa contra alguém, os tribunais estão abertos, e há procônsules. Eles que apresentem suas queixas ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27625, 'nvi', 'Atos', 'atos', 19, 39, 'Se há mais alguma coisa que vocês desejam apresentar, isso será decidido em assembléia, conforme a lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27626, 'nvi', 'Atos', 'atos', 19, 40, 'Da maneira como está, corremos o perigo de sermos acusados de perturbar a ordem pública por causa dos acontecimentos de hoje. Nesse caso, não seríamos capazes de justificar este tumulto, visto que não há razão para tal".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27627, 'nvi', 'Atos', 'atos', 19, 41, 'E, tendo dito isso, encerrou a assembléia.');
