INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26951, 'nvi', 'Atos', 'atos', 2, 1, 'Chegando o dia de Pentecoste, estavam todos reunidos num só lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26952, 'nvi', 'Atos', 'atos', 2, 2, 'De repente veio do céu um som, como de um vento muito forte, e encheu toda a casa na qual estavam assentados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26953, 'nvi', 'Atos', 'atos', 2, 3, 'E viram o que parecia línguas de fogo, que se separaram e pousaram sobre cada um deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26954, 'nvi', 'Atos', 'atos', 2, 4, 'Todos ficaram cheios do Espírito Santo e começaram a falar noutras línguas, conforme o Espírito os capacitava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26955, 'nvi', 'Atos', 'atos', 2, 5, 'Havia em Jerusalém judeus, tementes a Deus, vindos de todas as nações do mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26956, 'nvi', 'Atos', 'atos', 2, 6, 'Ouvindo-se este som, ajuntou-se uma multidão que ficou perplexa, pois cada um os ouvia falar em sua própria língua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26957, 'nvi', 'Atos', 'atos', 2, 7, 'Atônitos e maravilhados, eles perguntavam: "Acaso não são galileus todos estes homens que estão falando?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26958, 'nvi', 'Atos', 'atos', 2, 8, 'Então, como os ouvimos, cada um de nós, em nossa própria língua materna?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26959, 'nvi', 'Atos', 'atos', 2, 9, 'Partos, medos e elamitas; habitantes da Mesopotâmia, Judéia e Capadócia, Ponto e da província da Ásia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26960, 'nvi', 'Atos', 'atos', 2, 10, 'Frígia e Panfília, Egito e das partes da Líbia próximas a Cirene; visitantes vindos de Roma,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26961, 'nvi', 'Atos', 'atos', 2, 11, 'tanto judeus como convertidos ao judaísmo; cretenses e árabes. Nós os ouvimos declarar as maravilhas de Deus em nossa própria língua! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26962, 'nvi', 'Atos', 'atos', 2, 12, 'Atônitos e perplexos, todos perguntavam uns aos outros: "Que significa isto? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26963, 'nvi', 'Atos', 'atos', 2, 13, 'Alguns, todavia, zombavam deles e diziam: "Eles beberam vinho demais".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26964, 'nvi', 'Atos', 'atos', 2, 14, 'Então Pedro levantou-se com os Onze e, em alta voz, dirigiu-se à multidão: "Homens da Judéia e todos os que vivem em Jerusalém, deixem-me explicar-lhes isto! Ouçam com atenção:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26965, 'nvi', 'Atos', 'atos', 2, 15, 'estes homens não estão bêbados, como vocês supõem. Ainda são nove horas da manhã!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26966, 'nvi', 'Atos', 'atos', 2, 16, 'Pelo contrário, isto é o que foi predito pelo profeta Joel:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26967, 'nvi', 'Atos', 'atos', 2, 17, '‘Nos últimos dias, diz Deus, derramarei do meu Espírito sobre todos os povos. Os seus filhos e as suas filhas profetizarão, os jovens terão visões, os velhos terão sonhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26968, 'nvi', 'Atos', 'atos', 2, 18, 'Sobre os meus servos e as minhas servas derramarei do meu Espírito naqueles dias, e eles profetizarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26969, 'nvi', 'Atos', 'atos', 2, 19, 'Mostrarei maravilhas em cima no céu e sinais em baixo, na terra, sangue, fogo e nuvens de fumaça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26970, 'nvi', 'Atos', 'atos', 2, 20, 'O sol se tornará em trevas e a lua em sangue, antes que venha o grande e glorioso dia do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26971, 'nvi', 'Atos', 'atos', 2, 21, 'E todo aquele que invocar o nome do Senhor será salvo! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26972, 'nvi', 'Atos', 'atos', 2, 22, '"Israelitas, ouçam estas palavras: Jesus de Nazaré foi aprovado por Deus diante de vocês por meio de milagres, maravilhas e sinais, que Deus fez entre vocês por intermédio dele, como vocês mesmos sabem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26973, 'nvi', 'Atos', 'atos', 2, 23, 'Este homem lhes foi entregue por propósito determinado e pré-conhecimento de Deus; e vocês, com a ajuda de homens perversos, o mataram, pregando-o na cruz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26974, 'nvi', 'Atos', 'atos', 2, 24, 'Mas Deus o ressuscitou dos mortos, rompendo os laços da morte, porque era impossível que a morte o retivesse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26975, 'nvi', 'Atos', 'atos', 2, 25, 'A respeito dele, disse Davi: ‘Eu sempre via o Senhor diante de mim. Porque ele está à minha direita, não serei abalado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26976, 'nvi', 'Atos', 'atos', 2, 26, 'Por isso o meu coração está alegre e a minha língua exulta; o meu corpo também repousará em esperança,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26977, 'nvi', 'Atos', 'atos', 2, 27, 'porque tu não me abandonarás no sepulcro, nem permitirás que o teu Santo sofra decomposição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26978, 'nvi', 'Atos', 'atos', 2, 28, 'Tu me fizeste conhecer os caminhos da vida e me encherás de alegria na tua presença’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26979, 'nvi', 'Atos', 'atos', 2, 29, '"Irmãos, posso dizer-lhes com franqueza que o patriarca Davi morreu e foi sepultado, e o seu túmulo está entre nós até o dia de hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26980, 'nvi', 'Atos', 'atos', 2, 30, 'Mas ele era profeta e sabia que Deus lhe prometera sob juramento que colocaria um dos seus descendentes em seu trono.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26981, 'nvi', 'Atos', 'atos', 2, 31, 'Prevendo isso, falou da ressurreição do Cristo, que não foi abandonado no sepulcro e cujo corpo não sofreu decomposição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26982, 'nvi', 'Atos', 'atos', 2, 32, 'Deus ressuscitou este Jesus, e todos nós somos testemunhas desse fato.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26983, 'nvi', 'Atos', 'atos', 2, 33, 'Exaltado à direita de Deus, ele recebeu do Pai o Espírito Santo prometido e derramou o que vocês agora vêem e ouvem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26984, 'nvi', 'Atos', 'atos', 2, 34, 'Pois Davi não subiu ao céu, mas ele mesmo declarou: ‘O Senhor disse ao meu Senhor: Senta-te à minha direita');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26985, 'nvi', 'Atos', 'atos', 2, 35, 'até que eu ponha os teus inimigos como estrado para os teus pés’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26986, 'nvi', 'Atos', 'atos', 2, 36, '"Portanto, que todo Israel fique certo disto: Este Jesus, a quem vocês crucificaram, Deus o fez Senhor e Cristo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26987, 'nvi', 'Atos', 'atos', 2, 37, 'Quando ouviram isso, os seus corações ficaram aflitos, e eles perguntaram a Pedro e aos outros apóstolos: "Irmãos, que faremos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26988, 'nvi', 'Atos', 'atos', 2, 38, 'Pedro respondeu: "Arrependam-se, e cada um de vocês seja batizado em nome de Jesus Cristo, para perdão dos seus pecados, e receberão o dom do Espírito Santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26989, 'nvi', 'Atos', 'atos', 2, 39, 'Pois a promessa é para vocês, para os seus filhos e para todos os que estão longe, para todos quantos o Senhor, o nosso Deus chamar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26990, 'nvi', 'Atos', 'atos', 2, 40, 'Com muitas outras palavras os advertia e insistia com eles: "Salvem-se desta geração corrompida! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26991, 'nvi', 'Atos', 'atos', 2, 41, 'Os que aceitaram a mensagem foram batizados, e naquele dia houve um acréscimo de cerca de três mil pessoas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26992, 'nvi', 'Atos', 'atos', 2, 42, 'Eles se dedicavam ao ensino dos apóstolos e à comunhão, ao partir do pão e às orações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26993, 'nvi', 'Atos', 'atos', 2, 43, 'Todos estavam cheios de temor, e muitas maravilhas e sinais eram feitos pelos apóstolos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26994, 'nvi', 'Atos', 'atos', 2, 44, 'Todos os que criam mantinham-se unidos e tinham tudo em comum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26995, 'nvi', 'Atos', 'atos', 2, 45, 'Vendendo suas propriedades e bens, distribuíam a cada um conforme a sua necessidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26996, 'nvi', 'Atos', 'atos', 2, 46, 'Todos os dias, continuavam a reunir-se no pátio do templo. Partiam o pão em suas casas, e juntos participavam das refeições, com alegria e sinceridade de coração,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26997, 'nvi', 'Atos', 'atos', 2, 47, 'louvando a Deus e tendo a simpatia de todo o povo. E o Senhor lhes acrescentava todos os dias os que iam sendo salvos.');
