INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27628, 'nvi', 'Atos', 'atos', 20, 1, 'Cessado o tumulto, Paulo mandou chamar os discípulos e, depois de encorajá-los, despediu-se e partiu para a Macedônia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27629, 'nvi', 'Atos', 'atos', 20, 2, 'Viajou por aquela região, encorajando os irmãos com muitas palavras e, por fim, chegou à Grécia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27630, 'nvi', 'Atos', 'atos', 20, 3, 'onde ficou três meses. Quando estava a ponto de embarcar para a Síria, os judeus fizeram uma conspiração contra ele; por isso decidiu voltar pela Macedônia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27631, 'nvi', 'Atos', 'atos', 20, 4, 'sendo acompanhado por Sópatro, filho de Pirro, de Beréia; Aristarco e Secundo, de Tessalônica; Gaio, de Derbe; e Timóteo, além de Tíquico e Trófimo, da província da Ásia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27632, 'nvi', 'Atos', 'atos', 20, 5, 'Esses homens foram adiante e nos esperaram em Trôade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27633, 'nvi', 'Atos', 'atos', 20, 6, 'Navegamos de Filipos, após a festa dos pães sem fermento, e cinco dias depois nos reunimos com os outros em Trôade, onde ficamos sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27634, 'nvi', 'Atos', 'atos', 20, 7, 'No primeiro dia da semana reunimo-nos para partir o pão, e Paulo falou ao povo. Pretendendo partir no dia seguinte, continuou falando até à meia-noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27635, 'nvi', 'Atos', 'atos', 20, 8, 'Havia muitas candeias no piso superior onde estávamos reunidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27636, 'nvi', 'Atos', 'atos', 20, 9, 'Um jovem chamado Êutico, que estava sentado numa janela, adormeceu profundamente durante o longo discurso de Paulo. Vencido pelo sono, caiu do terceiro andar. Quando o levantaram, estava morto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27637, 'nvi', 'Atos', 'atos', 20, 10, 'Paulo desceu, inclinou-se sobre o rapaz e o abraçou, dizendo: "Não fiquem alarmados! Ele está vivo! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27638, 'nvi', 'Atos', 'atos', 20, 11, 'Então subiu novamente, partiu o pão e comeu. Depois, continuou a falar até o amanhecer e foi embora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27639, 'nvi', 'Atos', 'atos', 20, 12, 'Levaram vivo o jovem, o que muito os consolou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27640, 'nvi', 'Atos', 'atos', 20, 13, 'Quanto a nós, fomos até o navio e embarcamos para Assôs, onde iríamos receber Paulo a bordo. Assim ele tinha determinado, tendo preferido ir a pé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27641, 'nvi', 'Atos', 'atos', 20, 14, 'Quando nos encontrou em Assôs, nós o recebemos a bordo e prosseguimos até Mitilene.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27642, 'nvi', 'Atos', 'atos', 20, 15, 'No dia seguinte navegamos dali e chegamos defronte de Quio; no outro dia atravessamos para Samos e, um dia depois, chegamos a Mileto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27643, 'nvi', 'Atos', 'atos', 20, 16, 'Paulo tinha decidido não aportar em Éfeso, para não se demorar na província da Ásia, pois estava com pressa de chegar a Jerusalém, se possível antes do dia de Pentecoste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27644, 'nvi', 'Atos', 'atos', 20, 17, 'De Mileto, Paulo mandou chamar os presbíteros da igreja de Éfeso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27645, 'nvi', 'Atos', 'atos', 20, 18, 'Quando chegaram, ele lhes disse: "Vocês sabem como vivi todo o tempo em que estive com vocês, desde o primeiro dia em que cheguei à província da Ásia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27646, 'nvi', 'Atos', 'atos', 20, 19, 'Servi ao Senhor com toda a humildade e com lágrimas, sendo severamente provado pelas conspirações dos judeus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27647, 'nvi', 'Atos', 'atos', 20, 20, 'Vocês sabem que não deixei de pregar-lhes nada que fosse proveitoso, mas ensinei-lhes tudo publicamente e de casa em casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27648, 'nvi', 'Atos', 'atos', 20, 21, 'Testifiquei, tanto a judeus como a gregos, que eles precisam converter-se a Deus com arrependimento e fé em nosso Senhor Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27649, 'nvi', 'Atos', 'atos', 20, 22, '"Agora, compelido pelo Espírito, estou indo para Jerusalém, sem saber o que me acontecerá ali,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27650, 'nvi', 'Atos', 'atos', 20, 23, 'senão que, em todas as cidades, o Espírito Santo me avisa que prisões e sofrimentos me esperam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27651, 'nvi', 'Atos', 'atos', 20, 24, 'Todavia, não me importo, nem considero a minha vida de valor algum para mim mesmo, se tão-somente puder terminar a corrida e completar o ministério que o Senhor Jesus me confiou, de testemunhar do evangelho da graça de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27652, 'nvi', 'Atos', 'atos', 20, 25, '"Agora sei que nenhum de vocês, entre os quais passei pregando o Reino, verá novamente a minha face.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27653, 'nvi', 'Atos', 'atos', 20, 26, 'Portanto, eu lhes declaro hoje que estou inocente do sangue de todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27654, 'nvi', 'Atos', 'atos', 20, 27, 'Pois não deixei de proclamar-lhes toda a vontade de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27655, 'nvi', 'Atos', 'atos', 20, 28, 'Cuidem de vocês mesmos e de todo o rebanho sobre o qual o Espírito Santo os colocou como bispos, para pastorearem a igreja de Deus, que ele comprou com o seu próprio sangue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27656, 'nvi', 'Atos', 'atos', 20, 29, 'Sei que, depois da minha partida, lobos ferozes penetrarão no meio de vocês e não pouparão o rebanho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27657, 'nvi', 'Atos', 'atos', 20, 30, 'E dentre vocês mesmos se levantarão homens que torcerão a verdade, a fim de atrair os discípulos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27658, 'nvi', 'Atos', 'atos', 20, 31, 'Por isso, vigiem! Lembrem-se de que durante três anos jamais cessei de advertir a cada um de vocês disso, noite e dia, com lágrimas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27659, 'nvi', 'Atos', 'atos', 20, 32, '"Agora, eu os entrego a Deus e à palavra da sua graça, que pode edificá-los e dar-lhes herança entre todos os que são santificados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27660, 'nvi', 'Atos', 'atos', 20, 33, 'Não cobicei a prata nem o ouro nem as roupas de ninguém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27661, 'nvi', 'Atos', 'atos', 20, 34, 'Vocês mesmos sabem que estas minhas mãos supriram minhas necessidades e as de meus companheiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27662, 'nvi', 'Atos', 'atos', 20, 35, 'Em tudo o que fiz, mostrei-lhes que mediante trabalho árduo devemos ajudar os fracos, lembrando as palavras do próprio Senhor Jesus, que disse: ‘Há maior felicidade em dar do que em receber’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27663, 'nvi', 'Atos', 'atos', 20, 36, 'Tendo dito isso, ajoelhou-se com todos eles e orou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27664, 'nvi', 'Atos', 'atos', 20, 37, 'Todos choraram muito e, abraçando-o, o beijavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27665, 'nvi', 'Atos', 'atos', 20, 38, 'O que mais os entristeceu foi a declaração de que nunca mais veriam a sua face. Então o acompanharam até o navio.');
