INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27666, 'nvi', 'Atos', 'atos', 21, 1, 'Depois de nos separarmos deles, embarcamos e navegamos diretamente para Cós. No dia seguinte fomos para Rodes, e dali até Pátara.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27667, 'nvi', 'Atos', 'atos', 21, 2, 'Encontrando um navio que ia fazer a travessia para a Fenícia, embarcamos nele e partimos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27668, 'nvi', 'Atos', 'atos', 21, 3, 'Depois de avistarmos Chipre e seguirmos rumo sul, navegamos para a Síria. Desembarcamos em Tiro, onde o nosso navio deveria deixar sua carga.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27669, 'nvi', 'Atos', 'atos', 21, 4, 'Encontrando os discípulos dali, ficamos com eles sete dias. Eles, pelo Espírito, recomendavam a Paulo que não fosse a Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27670, 'nvi', 'Atos', 'atos', 21, 5, 'Mas quando terminou o nosso tempo ali, partimos e continuamos nossa viagem. Todos os discípulos, com suas mulheres e filhos, nos acompanharam até fora da cidade, e ali na praia nos ajoelhamos e oramos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27671, 'nvi', 'Atos', 'atos', 21, 6, 'Depois de nos despedirmos, embarcamos, e eles voltaram para casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27672, 'nvi', 'Atos', 'atos', 21, 7, 'Demos prosseguimento à nossa viagem partindo de Tiro, e aportamos em Ptolemaida, onde saudamos os irmãos e passamos um dia com eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27673, 'nvi', 'Atos', 'atos', 21, 8, 'Partindo no dia seguinte, chegamos a Cesaréia e ficamos na casa de Filipe, o evangelista, um dos sete.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27674, 'nvi', 'Atos', 'atos', 21, 9, 'Ele tinha quatro filhas virgens, que profetizavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27675, 'nvi', 'Atos', 'atos', 21, 10, 'Depois de passarmos ali vários dias, desceu da Judéia um profeta chamado Ágabo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27676, 'nvi', 'Atos', 'atos', 21, 11, 'Vindo ao nosso encontro, tomou o cinto de Paulo e, amarrando as suas próprias mãos e pés, disse: "Assim diz o Espírito Santo: ‘Desta maneira os judeus amarrarão o dono deste cinto em Jerusalém e o entregarão aos gentios’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27677, 'nvi', 'Atos', 'atos', 21, 12, 'Quando ouvimos isso, nós e o povo dali rogamos a Paulo que não subisse para Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27678, 'nvi', 'Atos', 'atos', 21, 13, 'Então Paulo respondeu: "Por que vocês estão chorando e partindo o meu coração? Estou pronto não apenas para ser amarrado, mas também para morrer em Jerusalém pelo nome do Senhor Jesus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27679, 'nvi', 'Atos', 'atos', 21, 14, 'Como não pudemos dissuadi-lo, desistimos e dissemos: "Seja feita a vontade do Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27680, 'nvi', 'Atos', 'atos', 21, 15, 'Depois disso, preparamo-nos e subimos para Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27681, 'nvi', 'Atos', 'atos', 21, 16, 'Alguns dos discípulos de Cesaréia nos acompanharam e nos levaram à casa de Mnasom, onde devíamos ficar. Ele era natural de Chipre e um dos primeiros discípulos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27682, 'nvi', 'Atos', 'atos', 21, 17, 'Quando chegamos em Jerusalém, os irmãos nos receberam com alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27683, 'nvi', 'Atos', 'atos', 21, 18, 'No dia seguinte Paulo foi conosco encontrar-se com Tiago, e todos os presbíteros estavam presentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27684, 'nvi', 'Atos', 'atos', 21, 19, 'Paulo os saudou e relatou minuciosamente o que Deus havia feito entre os gentios por meio do seu ministério.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27685, 'nvi', 'Atos', 'atos', 21, 20, 'Ouvindo isso, eles louvaram a Deus e disseram a Paulo: "Veja, irmão, quantos milhares de judeus creram, e todos eles são zelosos da lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27686, 'nvi', 'Atos', 'atos', 21, 21, 'Eles foram informados de que você ensina todos os judeus que vivem entre os gentios a se afastarem de Moisés, dizendo-lhes que não circuncidem seus filhos nem vivam de acordo com os nossos costumes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27687, 'nvi', 'Atos', 'atos', 21, 22, 'Que faremos? Certamente eles saberão que você chegou;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27688, 'nvi', 'Atos', 'atos', 21, 23, 'portanto, faça o que lhe dizemos. Estão conosco quatro homens que fizeram um voto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27689, 'nvi', 'Atos', 'atos', 21, 24, 'Participe com esses homens dos rituais de purificação e pague as despesas deles, para que rapem a cabeça. Então todos saberão que não é verdade o que falam de você, mas que você continua vivendo em obediência à lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27690, 'nvi', 'Atos', 'atos', 21, 25, 'Quanto aos gentios convertidos, já lhes escrevemos a nossa decisão de que eles devem abster-se de comida sacrificada aos ídolos, do sangue, da carne de animais estrangulados e da imoralidade sexual".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27691, 'nvi', 'Atos', 'atos', 21, 26, 'No dia seguinte Paulo tomou aqueles homens e purificou-se juntamente com eles. Depois foi ao templo para declarar o prazo do cumprimento dos dias da purificação e da oferta que seria feita individualmente em favor deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27692, 'nvi', 'Atos', 'atos', 21, 27, 'Quando já estavam para terminar os sete dias, alguns judeus da Província da Ásia, vendo Paulo no templo, agitaram toda a multidão e o agarraram,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27693, 'nvi', 'Atos', 'atos', 21, 28, 'gritando: "Israelitas, ajudem-nos! Este é o homem que ensina a todos em toda parte contra o nosso povo, contra a nossa lei e contra este lugar. Além disso, ele fez entrar gregos no templo e profanou este santo lugar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27694, 'nvi', 'Atos', 'atos', 21, 29, 'Anteriormente eles haviam visto o efésio Trófimo na cidade com Paulo e julgaram que Paulo o tinha introduzido no templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27695, 'nvi', 'Atos', 'atos', 21, 30, 'Toda a cidade ficou alvoroçada, e juntou-se uma multidão. Agarrando Paulo, arrastaram-no para fora do templo, e imediatamente as portas foram fechadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27696, 'nvi', 'Atos', 'atos', 21, 31, 'Tentando eles matá-lo, chegaram notícias ao comandante das tropas romanas de que toda a cidade de Jerusalém estava em tumulto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27697, 'nvi', 'Atos', 'atos', 21, 32, 'Ele reuniu imediatamente alguns oficiais e soldados, e com eles correu para o meio da multidão. Quando viram o comandante e os seus soldados, pararam de espancar Paulo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27698, 'nvi', 'Atos', 'atos', 21, 33, 'O comandante chegou, prendeu-o e ordenou que ele fosse amarrado com duas correntes. Então perguntou quem era ele e o que tinha feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27699, 'nvi', 'Atos', 'atos', 21, 34, 'Alguns da multidão gritavam uma coisa, outros gritavam outra; não conseguindo saber ao certo o que havia acontecido, por causa do tumulto, o comandante ordenou que Paulo fosse levado para a fortaleza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27700, 'nvi', 'Atos', 'atos', 21, 35, 'Quando chegou às escadas, a violência do povo era tão grande que ele precisou ser carregado pelos soldados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27701, 'nvi', 'Atos', 'atos', 21, 36, 'A multidão que o seguia continuava gritando: "Acaba com ele! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27702, 'nvi', 'Atos', 'atos', 21, 37, 'Quando os soldados estavam para introduzir Paulo na fortaleza, ele perguntou ao comandante: "Posso dizer-te algo? " "Você fala grego? ", perguntou ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27703, 'nvi', 'Atos', 'atos', 21, 38, '"Não é você o egípcio que iniciou uma revolta e há algum tempo levou quatro mil assassinos para o deserto? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27704, 'nvi', 'Atos', 'atos', 21, 39, 'Paulo respondeu: "Sou judeu, cidadão de Tarso, cidade importante da Cilícia. Permite-me falar ao povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27705, 'nvi', 'Atos', 'atos', 21, 40, 'Tendo recebido permissão do comandante, Paulo levantou-se na escadaria e fez sinal à multidão. Quando todos fizeram silêncio, dirigiu-se a eles em aramaico:');
