INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27706, 'nvi', 'Atos', 'atos', 22, 1, '"Irmãos e pais, ouçam agora a minha defesa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27707, 'nvi', 'Atos', 'atos', 22, 2, 'Quando ouviram que lhes falava em aramaico, ficaram em absoluto silêncio. Então Paulo disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27708, 'nvi', 'Atos', 'atos', 22, 3, '"Sou judeu, nascido em Tarso da Cilícia, mas criado nesta cidade. Fui instruído rigorosamente por Gamaliel na lei de nossos antepassados, sendo tão zeloso por Deus quanto qualquer de vocês hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27709, 'nvi', 'Atos', 'atos', 22, 4, 'Persegui os seguidores deste Caminho até a morte, prendendo tanto homens como mulheres e lançando-os na prisão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27710, 'nvi', 'Atos', 'atos', 22, 5, 'como o podem testemunhar o sumo sacerdote e todo o Conselho, de quem cheguei a obter cartas para seus irmãos em Damasco e fui até lá, a fim de trazer essas pessoas a Jerusalém como prisioneiras, para serem punidas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27711, 'nvi', 'Atos', 'atos', 22, 6, '"Por volta do meio-dia, eu me aproximava de Damasco, quando de repente uma forte luz vinda do céu brilhou ao meu redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27712, 'nvi', 'Atos', 'atos', 22, 7, 'Caí por terra e ouvi uma voz que me dizia: ‘Saulo, Saulo! por que você está me perseguindo? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27713, 'nvi', 'Atos', 'atos', 22, 8, 'Então perguntei: Quem és tu, Senhor? E ele respondeu: ‘Eu sou Jesus, o Nazareno, a quem você persegue’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27714, 'nvi', 'Atos', 'atos', 22, 9, 'Os que me acompanhavam viram a luz, mas não entenderam a voz daquele que falava comigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27715, 'nvi', 'Atos', 'atos', 22, 10, '"Assim perguntei: Que devo fazer, Senhor? Disse o Senhor: ‘Levante-se, entre em Damasco, onde lhe será dito o que você deve fazer’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27716, 'nvi', 'Atos', 'atos', 22, 11, 'Os que estavam comigo me levaram pela mão até Damasco, porque o resplendor da luz me deixara cego.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27717, 'nvi', 'Atos', 'atos', 22, 12, '"Um homem chamado Ananias, piedoso segundo a lei e muito respeitado por todos os judeus que ali viviam,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27718, 'nvi', 'Atos', 'atos', 22, 13, 'veio ver-me e, pondo-se junto a mim, disse: ‘Irmão Saulo, recupere a visão’. Naquele mesmo instante pude vê-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27719, 'nvi', 'Atos', 'atos', 22, 14, '"Então ele disse: ‘O Deus dos nossos antepassados o escolheu para conhecer a sua vontade, ver o Justo e ouvir as palavras de sua boca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27720, 'nvi', 'Atos', 'atos', 22, 15, 'Você será testemunha dele a todos os homens, daquilo que viu e ouviu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27721, 'nvi', 'Atos', 'atos', 22, 16, 'E agora, que está esperando? Levante-se, seja batizado e lave os seus pecados, invocando o nome dele’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27722, 'nvi', 'Atos', 'atos', 22, 17, '"Quando voltei a Jerusalém, estando eu a orar no templo, caí em êxtase e');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27723, 'nvi', 'Atos', 'atos', 22, 18, 'vi o Senhor que me dizia: ‘Depressa! Saia de Jerusalém imediatamente, pois não aceitarão seu testemunho a meu respeito’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27724, 'nvi', 'Atos', 'atos', 22, 19, '"Eu respondi: Senhor, estes homens sabem que eu ia de uma sinagoga a outra, a fim de prender e açoitar os que crêem em ti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27725, 'nvi', 'Atos', 'atos', 22, 20, 'E quando foi derramado o sangue de tua testemunha Estêvão, eu estava lá, dando minha aprovação e cuidando das roupas dos que o matavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27726, 'nvi', 'Atos', 'atos', 22, 21, '"Então o Senhor me disse: ‘Vá, eu o enviarei para longe, aos gentios’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27727, 'nvi', 'Atos', 'atos', 22, 22, 'A multidão ouvia Paulo até que ele disse isso. Então todos levantaram a voz e gritaram: "Tira esse homem da face da terra! Ele não merece viver! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27728, 'nvi', 'Atos', 'atos', 22, 23, 'Estando eles gritando, tirando suas capas e lançando poeira para o ar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27729, 'nvi', 'Atos', 'atos', 22, 24, 'o comandante ordenou que Paulo fosse levado à fortaleza e fosse açoitado e interrogado, para saber por que o povo gritava daquela forma contra ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27730, 'nvi', 'Atos', 'atos', 22, 25, 'Enquanto o amarravam a fim de açoitá-lo, Paulo disse ao centurião que ali estava: "Vocês têm o direito de açoitar um cidadão romano sem que ele tenha sido condenado? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27731, 'nvi', 'Atos', 'atos', 22, 26, 'Ao ouvir isso, o centurião foi prevenir o comandante: "Que vais fazer? Este homem é cidadão romano".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27732, 'nvi', 'Atos', 'atos', 22, 27, 'O comandante dirigiu-se a Paulo e perguntou: "Diga-me, você é cidadão romano? " Ele respondeu: "Sim, sou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27733, 'nvi', 'Atos', 'atos', 22, 28, 'Então o comandante disse: "Eu precisei pagar um elevado preço por minha cidadania". Respondeu Paulo: "Eu a tenho por direito de nascimento".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27734, 'nvi', 'Atos', 'atos', 22, 29, 'Os que iam interrogá-lo retiraram-se imediatamente. O próprio comandante ficou alarmado, ao saber que havia prendido um cidadão romano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27735, 'nvi', 'Atos', 'atos', 22, 30, 'No dia seguinte, visto que o comandante queria descobrir exatamente por que Paulo estava sendo acusado pelos judeus, libertou-o e ordenou que se reunissem os chefes dos sacerdotes e todo o Sinédrio. Então, trazendo Paulo, apresentou-o a eles.');
