INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27736, 'nvi', 'Atos', 'atos', 23, 1, 'Paulo, fixando os olhos no Sinédrio, disse: "Meus irmãos, tenho cumprido meu dever para com Deus com toda a boa consciência, até o dia de hoje".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27737, 'nvi', 'Atos', 'atos', 23, 2, 'Diante disso o sumo sacerdote Ananias deu ordens aos que estavam perto de Paulo para que lhe batessem na boca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27738, 'nvi', 'Atos', 'atos', 23, 3, 'Então Paulo lhe disse: "Deus te ferirá, parede branqueada! Estás aí sentado para me julgar conforme a lei, mas contra a lei me mandas ferir? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27739, 'nvi', 'Atos', 'atos', 23, 4, 'Os que estavam perto de Paulo disseram: "Você ousa insultar o sumo sacerdote de Deus? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27740, 'nvi', 'Atos', 'atos', 23, 5, 'Paulo respondeu: "Irmãos, eu não sabia que ele era o sumo sacerdote, pois está escrito: ‘Não fale mal de uma autoridade do seu povo’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27741, 'nvi', 'Atos', 'atos', 23, 6, 'Então Paulo, sabendo que alguns deles eram saduceus e os outros fariseus, bradou no Sinédrio: "Irmãos, sou fariseu, filho de fariseu. Estou sendo julgado por causa da minha esperança na ressurreição dos mortos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27742, 'nvi', 'Atos', 'atos', 23, 7, 'Dizendo isso, surgiu uma violenta discussão entre os fariseus e os saduceus, e a assembléia ficou dividida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27743, 'nvi', 'Atos', 'atos', 23, 8, '( Os saduceus dizem que não há ressurreição nem anjos nem espíritos, mas os fariseus admitem todas essas coisas. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27744, 'nvi', 'Atos', 'atos', 23, 9, 'Houve um grande alvoroço, e alguns dos mestres da lei que eram fariseus se levantaram e começaram a discutir intensamente, dizendo: "Não encontramos nada de errado neste homem. Quem sabe se algum espírito ou anjo falou com ele? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27745, 'nvi', 'Atos', 'atos', 23, 10, 'A discussão tornou-se tão violenta que o comandante teve medo que Paulo fosse despedaçado por eles. Então ordenou que as tropas descessem e o retirassem à força do meio deles, levando-o para a fortaleza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27746, 'nvi', 'Atos', 'atos', 23, 11, 'Na noite seguinte o Senhor, pondo-se ao lado dele, disse: "Coragem! Assim como você testemunhou a meu respeito em Jerusalém, deverá testemunhar também em Roma".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27747, 'nvi', 'Atos', 'atos', 23, 12, 'Na manhã seguinte os judeus tramaram uma conspiração e juraram solenemente que não comeriam nem beberiam enquanto não matassem Paulo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27748, 'nvi', 'Atos', 'atos', 23, 13, 'Mais de quarenta homens estavam envolvidos nessa conspiração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27749, 'nvi', 'Atos', 'atos', 23, 14, 'E, dirigindo-se aos chefes dos sacerdotes e aos líderes dos judeus, disseram: "Juramos solenemente, sob maldição, que não comeremos nada enquanto não matarmos Paulo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27750, 'nvi', 'Atos', 'atos', 23, 15, 'Agora, portanto, vocês e o Sinédrio peçam ao comandante que o faça comparecer diante de vocês com o pretexto de obter informações mais exatas sobre o seu caso. Estaremos prontos para matá-lo antes que ele chegue aqui".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27751, 'nvi', 'Atos', 'atos', 23, 16, 'Entretanto, o sobrinho de Paulo, filho de sua irmã, teve conhecimento dessa conspiração, foi à fortaleza e contou tudo a Paulo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27752, 'nvi', 'Atos', 'atos', 23, 17, 'que, chamando um dos centuriões, disse: "Leve este rapaz ao comandante; ele tem algo para lhe dizer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27753, 'nvi', 'Atos', 'atos', 23, 18, 'Assim ele o levou ao comandante. Então disse o centurião: "Paulo, o prisioneiro, chamou-me, pediu-me que te trouxesse este rapaz, pois ele tem algo para te falar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27754, 'nvi', 'Atos', 'atos', 23, 19, 'O comandante tomou o rapaz pela mão, levou-o à parte e perguntou: "Que você tem para me dizer? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27755, 'nvi', 'Atos', 'atos', 23, 20, 'Ele respondeu: "Os judeus planejaram pedir-te que apresentes Paulo ao Sinédrio amanhã, sob pretexto de buscar informações mais exatas a respeito dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27756, 'nvi', 'Atos', 'atos', 23, 21, 'Não te deixes convencer, pois mais de quarenta deles estão preparando uma emboscada contra Paulo. Eles juraram solenemente não comer nem beber enquanto não o matarem. Estão preparados agora, esperando que prometas atender-lhes o pedido".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27757, 'nvi', 'Atos', 'atos', 23, 22, 'O comandante despediu o rapaz e recomendou-lhe: "Não diga a ninguém que você me contou isso".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27758, 'nvi', 'Atos', 'atos', 23, 23, 'Então ele chamou dois de seus centuriões e ordenou-lhes: "Preparem um destacamento de duzentos soldados, setenta cavaleiros e duzentos lanceiros a fim de irem para Cesaréia esta noite, às nove horas da noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27759, 'nvi', 'Atos', 'atos', 23, 24, 'Providenciem montarias para Paulo, e levem-no em segurança ao governador Félix".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27760, 'nvi', 'Atos', 'atos', 23, 25, 'O comandante escreveu uma carta nestes termos:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27761, 'nvi', 'Atos', 'atos', 23, 26, 'Cláudio Lísias, ao Excelentíssimo Governador Félix, Saudações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27762, 'nvi', 'Atos', 'atos', 23, 27, 'Este homem foi preso pelos judeus, que estavam prestes a matá-lo quando eu, chegando com minhas tropas, o resgatei, pois soube que ele é cidadão romano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27763, 'nvi', 'Atos', 'atos', 23, 28, 'Querendo saber por que o estavam acusando, levei-o ao Sinédrio deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27764, 'nvi', 'Atos', 'atos', 23, 29, 'Descobri que ele estava sendo acusado em questões acerca da lei deles, mas não havia contra ele nenhuma acusação que merecesse morte ou prisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27765, 'nvi', 'Atos', 'atos', 23, 30, 'Quando fui informado de que estava sendo preparada uma cilada contra ele, enviei-o imediatamente a Vossa Excelência. Também ordenei que os seus acusadores apresentassem a Vossa Excelência aquilo que têm contra ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27766, 'nvi', 'Atos', 'atos', 23, 31, 'Os soldados, cumprindo o seu dever, levaram Paulo durante a noite, e chegaram a Antipátride.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27767, 'nvi', 'Atos', 'atos', 23, 32, 'No dia seguinte deixaram a cavalaria prosseguir com ele, e voltaram para a fortaleza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27768, 'nvi', 'Atos', 'atos', 23, 33, 'Quando a cavalaria chegou a Cesaréia, deu a carta ao governador e lhe entregou Paulo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27769, 'nvi', 'Atos', 'atos', 23, 34, 'O governador leu a carta e perguntou de que província era ele. Informado de que era da Cilícia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27770, 'nvi', 'Atos', 'atos', 23, 35, 'disse: "Ouvirei seu caso quando os seus acusadores chegarem aqui". Então ordenou que Paulo fosse mantido sob custódia no palácio de Herodes.');
