INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27771, 'nvi', 'Atos', 'atos', 24, 1, 'Cinco dias depois, o sumo sacerdote Ananias desceu a Cesaréia com alguns dos líderes dos judeus e um advogado chamado Tértulo, os quais apresentaram ao governador suas acusações contra Paulo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27772, 'nvi', 'Atos', 'atos', 24, 2, 'Quando Paulo foi chamado, Tértulo apresentou sua causa a Félix: "Temos desfrutado de um longo período de paz durante o teu governo, e o teu providente cuidado resultou em reformas nesta nação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27773, 'nvi', 'Atos', 'atos', 24, 3, 'Em tudo e em toda parte, excelentíssimo Félix, reconhecemos estes benefícios com profunda gratidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27774, 'nvi', 'Atos', 'atos', 24, 4, 'Todavia, a fim de não tomar-te mais tempo, peço-te o favor de ouvir-nos apenas por um pouco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27775, 'nvi', 'Atos', 'atos', 24, 5, '"Verificamos que este homem é um perturbador, que promove tumultos entre os judeus pelo mundo todo. Ele é o principal cabeça da seita dos nazarenos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27776, 'nvi', 'Atos', 'atos', 24, 6, 'e tentou até mesmo profanar o templo; então o prendemos e quisemos julgá-lo segundo a nossa lei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27777, 'nvi', 'Atos', 'atos', 24, 7, 'Mas o comandante Lísias interveio, e com muita força o arrebatou de nossas mãos e ordenou que os seus acusadores se apresentassem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27778, 'nvi', 'Atos', 'atos', 24, 8, 'Se tu mesmo o interrogares, poderás verificar a verdade a respeito de todas estas acusações que estamos fazendo contra ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27779, 'nvi', 'Atos', 'atos', 24, 9, 'Os judeus confirmaram a acusação, garantindo que as afirmações eram verdadeiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27780, 'nvi', 'Atos', 'atos', 24, 10, 'Quando o governador lhe deu sinal para que falasse, Paulo declarou: "Sei que há muitos anos tens sido juiz nesta nação; por isso, de bom grado faço minha defesa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27781, 'nvi', 'Atos', 'atos', 24, 11, 'Facilmente poderás verificar que há menos de doze dias subi a Jerusalém para adorar a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27782, 'nvi', 'Atos', 'atos', 24, 12, 'Meus acusadores não me encontraram discutindo com ninguém no templo, nem incitando uma multidão nas sinagogas ou em qualquer outro lugar na cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27783, 'nvi', 'Atos', 'atos', 24, 13, 'Nem tampouco podem provar-te as acusações que agora estão levantando contra mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27784, 'nvi', 'Atos', 'atos', 24, 14, 'Confesso-te, porém, que adoro o Deus dos nossos antepassados como seguidor do Caminho, a que chamam seita. Creio em tudo o que concorda com a Lei e no que está escrito nos Profetas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27785, 'nvi', 'Atos', 'atos', 24, 15, 'e tenho em Deus a mesma esperança desses homens: de que haverá ressurreição tanto de justos como de injustos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27786, 'nvi', 'Atos', 'atos', 24, 16, 'Por isso procuro sempre conservar minha consciência limpa diante de Deus e dos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27787, 'nvi', 'Atos', 'atos', 24, 17, '"Depois de estar ausente por vários anos, vim a Jerusalém para trazer esmolas ao meu povo e apresentar ofertas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27788, 'nvi', 'Atos', 'atos', 24, 18, 'Enquanto fazia isso, já cerimonialmente puro, encontraram-me no templo, sem envolver-me em nenhum ajuntamento ou tumulto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27789, 'nvi', 'Atos', 'atos', 24, 19, 'Mas há alguns judeus da província da Ásia que deveriam estar aqui diante de ti e apresentar acusações, se é que têm algo contra mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27790, 'nvi', 'Atos', 'atos', 24, 20, 'Ou os que aqui se acham deveriam declarar que crime encontraram em mim quando fui levado perante o Sinédrio,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27791, 'nvi', 'Atos', 'atos', 24, 21, 'a não ser que tenha sido este: quando me apresentei a eles, bradei: Por causa da ressurreição dos mortos estou sendo julgado hoje diante de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27792, 'nvi', 'Atos', 'atos', 24, 22, 'Então Félix, que tinha bom conhecimento do Caminho, adiou a causa e disse: "Quando chegar o comandante Lísias, decidirei o caso de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27793, 'nvi', 'Atos', 'atos', 24, 23, 'E ordenou ao centurião que mantivesse Paulo sob custódia, mas que lhe desse certa liberdade e permitisse que os seus amigos o servissem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27794, 'nvi', 'Atos', 'atos', 24, 24, 'Vários dias depois, Félix veio com Drusila sua mulher, que era judia, mandou chamar Paulo e o ouviu falar sobre a fé em Cristo Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27795, 'nvi', 'Atos', 'atos', 24, 25, 'Quando Paulo se pôs a discorrer acerca da justiça, do domínio próprio e do juízo vindouro, Félix teve medo e disse: "Basta, por enquanto! Pode sair. Quando achar conveniente, mandarei chamá-lo de novo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27796, 'nvi', 'Atos', 'atos', 24, 26, 'Ao mesmo tempo esperava que Paulo lhe oferecesse algum dinheiro, pelo que mandava buscá-lo freqüentemente e conversava com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27797, 'nvi', 'Atos', 'atos', 24, 27, 'Passados dois anos, Félix foi sucedido por Pórcio Festo; todavia, porque desejava manter a simpatia dos judeus, Félix deixou Paulo na prisão.');
