INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27798, 'nvi', 'Atos', 'atos', 25, 1, 'Três dias depois de chegar à província, Festo subiu de Cesaréia para Jerusalém,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27799, 'nvi', 'Atos', 'atos', 25, 2, 'onde os chefes dos sacerdotes e os judeus mais importantes compareceram diante dele, apresentando as acusações contra Paulo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27800, 'nvi', 'Atos', 'atos', 25, 3, 'Pediram a Festo o favor de transferir Paulo para Jerusalém, contra os interesses do próprio Paulo, pois estavam preparando uma emboscada para matá-lo no caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27801, 'nvi', 'Atos', 'atos', 25, 4, 'Festo respondeu: "Paulo está preso em Cesaréia, e eu mesmo vou para lá em breve.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27802, 'nvi', 'Atos', 'atos', 25, 5, 'Desçam comigo alguns dos seus líderes e apresentem ali as acusações que têm contra esse homem, se realmente ele fez algo de errado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27803, 'nvi', 'Atos', 'atos', 25, 6, 'Tendo passado com eles oito a dez dias, desceu para Cesaréia e, no dia seguinte, convocou o tribunal e ordenou que Paulo fosse trazido perante ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27804, 'nvi', 'Atos', 'atos', 25, 7, 'Quando Paulo apareceu, os judeus que tinham chegado de Jerusalém se aglomeraram ao seu redor, fazendo contra ele muitas e graves acusações que não podiam provar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27805, 'nvi', 'Atos', 'atos', 25, 8, 'Então Paulo fez sua defesa: "Nada fiz de errado contra a lei dos judeus, contra o templo ou contra César".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27806, 'nvi', 'Atos', 'atos', 25, 9, 'Festo, querendo prestar um favor aos judeus, perguntou a Paulo: "Você está disposto a ir a Jerusalém e ali ser julgado diante de mim, acerca destas acusações? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27807, 'nvi', 'Atos', 'atos', 25, 10, 'Paulo respondeu: "Estou agora diante do tribunal de César, onde devo ser julgado. Não fiz nenhum mal aos judeus, como bem sabes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27808, 'nvi', 'Atos', 'atos', 25, 11, 'Se, de fato, sou culpado de ter feito algo que mereça pena de morte, não me recuso a morrer. Mas se as acusações feitas contra mim por estes judeus não são verdadeiras, ninguém tem o direito de me entregar a eles. Apelo para César! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27809, 'nvi', 'Atos', 'atos', 25, 12, 'Depois de ter consultado seus conselheiros, Festo declarou: "Você apelou para César, para César irá! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27810, 'nvi', 'Atos', 'atos', 25, 13, 'Alguns dias depois, o rei Agripa e Berenice chegaram a Cesaréia para saudar Festo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27811, 'nvi', 'Atos', 'atos', 25, 14, 'Visto que estavam passando muitos dias ali, Festo explicou o caso de Paulo ao rei: "Há aqui um homem que Félix deixou preso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27812, 'nvi', 'Atos', 'atos', 25, 15, 'Quando fui a Jerusalém, os chefes dos sacerdotes e os líderes dos judeus fizeram acusações contra ele, pedindo que fosse condenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27813, 'nvi', 'Atos', 'atos', 25, 16, '"Eu lhes disse que não é costume romano condenar ninguém antes que ele se defronte pessoalmente com seus acusadores e tenha a oportunidade de se defender das acusações que lhe fazem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27814, 'nvi', 'Atos', 'atos', 25, 17, 'Vindo eles comigo para cá, não retardei o caso; convoquei o tribunal no dia seguinte e ordenei que o homem fosse apresentado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27815, 'nvi', 'Atos', 'atos', 25, 18, 'Quando os seus acusadores se levantaram para falar, não o acusaram de nenhum dos crimes que eu esperava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27816, 'nvi', 'Atos', 'atos', 25, 19, 'Pelo contrário, tinham alguns pontos de divergência com ele acerca de sua própria religião e de um certo Jesus, já morto, o qual Paulo insiste que está vivo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27817, 'nvi', 'Atos', 'atos', 25, 20, 'Fiquei sem saber como investigar tais assuntos; por isso perguntei-lhe se ele estaria disposto a ir a Jerusalém e ser julgado ali acerca destas acusações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27818, 'nvi', 'Atos', 'atos', 25, 21, 'Apelando Paulo para que fosse guardado até a decisão do Imperador, ordenei que ficasse sob custódia até que eu pudesse enviá-lo a César".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27819, 'nvi', 'Atos', 'atos', 25, 22, 'Então Agripa disse a Festo: "Eu também gostaria de ouvir esse homem". Ele respondeu: "Amanhã o ouvirás".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27820, 'nvi', 'Atos', 'atos', 25, 23, 'No dia seguinte, Agripa e Berenice vieram com grande pompa e entraram na sala de audiências com os altos oficiais e os homens importantes da cidade. Por ordem de Festo, Paulo foi trazido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27821, 'nvi', 'Atos', 'atos', 25, 24, 'Então Festo disse: "Ó rei Agripa e todos os senhores aqui presentes conosco, vejam este homem! Toda a comunidade judaica me fez petições a respeito dele em Jerusalém e aqui em Cesaréia, gritando que ele não deveria mais viver.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27822, 'nvi', 'Atos', 'atos', 25, 25, 'Mas verifiquei que ele nada fez que mereça pena de morte; todavia, porque apelou para o Imperador, decidi enviá-lo a Roma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27823, 'nvi', 'Atos', 'atos', 25, 26, 'No entanto, não tenho nada definido a respeito dele para escrever a Sua Majestade. Por isso, eu o trouxe diante dos senhores, e especialmente diante de ti, rei Agripa, de forma que, feita esta investigação, eu tenha algo para escrever.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27824, 'nvi', 'Atos', 'atos', 25, 27, 'Pois não me parece razoável enviar um preso sem especificar as acusações contra ele".');
