INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27857, 'nvi', 'Atos', 'atos', 27, 1, 'Quando ficou decidido que navegaríamos para a Itália, Paulo e alguns outros presos foram entregues a um centurião chamado Júlio, que pertencia ao Regimento Imperial.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27858, 'nvi', 'Atos', 'atos', 27, 2, 'Embarcamos num navio de Adramítio, que estava de partida para alguns lugares da província da Ásia, e saímos ao mar, estando conosco Aristarco, um macedônio de Tessalônica.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27859, 'nvi', 'Atos', 'atos', 27, 3, 'No dia seguinte, ancoramos em Sidom; e Júlio, num gesto de bondade para com Paulo, permitiu-lhe que fosse ao encontro dos seus amigos, para que estes suprissem as suas necessidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27860, 'nvi', 'Atos', 'atos', 27, 4, 'Quando partimos de lá, passamos ao norte de Chipre, porque os ventos nos eram contrários.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27861, 'nvi', 'Atos', 'atos', 27, 5, 'Tendo atravessado o mar aberto ao longo da Cilícia e da Panfília, ancoramos em Mirra, na Lícia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27862, 'nvi', 'Atos', 'atos', 27, 6, 'Ali, o centurião encontrou um navio alexandrino que estava de partida para a Itália e nele nos fez embarcar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27863, 'nvi', 'Atos', 'atos', 27, 7, 'Navegamos vagarosamente por muitos dias e tivemos dificuldade para chegar a Cnido. Não sendo possível prosseguir em nossa rota, devido aos ventos contrários, navegamos ao sul de Creta, defronte a Salmona.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27864, 'nvi', 'Atos', 'atos', 27, 8, 'Costeamos a ilha com dificuldade e chegamos a um lugar chamado Bons Portos, perto da cidade de Laséia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27865, 'nvi', 'Atos', 'atos', 27, 9, 'Tínhamos perdido muito tempo, e agora a navegação se tornara perigosa, pois já havia passado o Jejum. Por isso Paulo os advertiu:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27866, 'nvi', 'Atos', 'atos', 27, 10, '"Senhores, vejo que a nossa viagem será desastrosa e acarretará grande prejuízo para o navio, para a carga e também para as nossas vidas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27867, 'nvi', 'Atos', 'atos', 27, 11, 'Mas o centurião, em vez de ouvir o que Paulo falava, seguiu o conselho do piloto e do dono do navio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27868, 'nvi', 'Atos', 'atos', 27, 12, 'Visto que o porto não era próprio para passar o inverno, a maioria decidiu que deveríamos continuar navegando, com a esperança de alcançar Fenice e ali passar o inverno. Este era um porto de Creta, que dava para sudoeste e noroeste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27869, 'nvi', 'Atos', 'atos', 27, 13, 'Começando a soprar suavemente o vento sul, eles pensaram que haviam obtido o que desejavam; por isso levantaram âncoras e foram navegando ao longo da costa de Creta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27870, 'nvi', 'Atos', 'atos', 27, 14, 'Pouco tempo depois, desencadeou-se da ilha um vento muito forte, chamado Nordeste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27871, 'nvi', 'Atos', 'atos', 27, 15, 'O navio foi arrastado pela tempestade, sem poder resistir ao vento; assim, cessamos as manobras e ficamos à deriva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27872, 'nvi', 'Atos', 'atos', 27, 16, 'Passando ao sul de uma pequena ilha chamada Clauda, foi com dificuldade que conseguimos recolher o barco salva-vidas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27873, 'nvi', 'Atos', 'atos', 27, 17, 'Levantando-o, lançaram mão de todos os meios para reforçar o navio com cordas; e temendo que ele encalhasse nos bancos de areia de Sirte, baixaram as velas e deixaram o navio à deriva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27874, 'nvi', 'Atos', 'atos', 27, 18, 'No dia seguinte, sendo violentamente castigados pela tempestade, começaram a lançar fora a carga.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27875, 'nvi', 'Atos', 'atos', 27, 19, 'No terceiro dia, lançaram fora, com as próprias mãos, a armação do navio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27876, 'nvi', 'Atos', 'atos', 27, 20, 'Não aparecendo nem sol nem estrelas por muitos dias, e continuando a abater-se sobre nós grande tempestade, finalmente perdemos toda a esperança de salvamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27877, 'nvi', 'Atos', 'atos', 27, 21, 'Visto que os homens tinham passado muito tempo sem comer, Paulo levantou-se diante deles e disse: "Os senhores deviam ter aceitado o meu conselho de não partir de Creta, pois assim teriam evitado este dano e prejuízo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27878, 'nvi', 'Atos', 'atos', 27, 22, 'Mas agora recomendo-lhes que tenham coragem, pois nenhum de vocês perderá a vida; apenas o navio será destruído.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27879, 'nvi', 'Atos', 'atos', 27, 23, 'Pois ontem à noite apareceu-me um anjo do Deus a quem pertenço e a quem adoro, dizendo-me:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27880, 'nvi', 'Atos', 'atos', 27, 24, '‘Paulo, não tenha medo. É preciso que você compareça perante César; Deus, por sua graça, deu-lhe as vidas de todos os que estão navegando com você’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27881, 'nvi', 'Atos', 'atos', 27, 25, 'Assim, tenham ânimo, senhores! Creio em Deus que acontecerá do modo como me foi dito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27882, 'nvi', 'Atos', 'atos', 27, 26, 'Devemos ser arrastados para alguma ilha".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27883, 'nvi', 'Atos', 'atos', 27, 27, 'Na décima quarta noite, ainda estávamos sendo levados de um lado para outro no mar Adriático, quando, por volta da meia-noite, os marinheiros imaginaram que estávamos próximos da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27884, 'nvi', 'Atos', 'atos', 27, 28, 'Lançando a sonda, verificaram que a profundidade era de trinta e sete metros; pouco tempo depois, lançaram novamente a sonda e encontraram vinte e sete metros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27885, 'nvi', 'Atos', 'atos', 27, 29, 'Temendo que fôssemos jogados contra as pedras, lançaram quatro âncoras da popa e faziam preces para que amanhecesse o dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27886, 'nvi', 'Atos', 'atos', 27, 30, 'Tentando escapar do navio, os marinheiros baixaram o barco salva-vidas ao mar, a pretexto de lançar âncoras da proa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27887, 'nvi', 'Atos', 'atos', 27, 31, 'Então Paulo disse ao centurião e aos soldados: "Se estes homens não ficarem no navio, vocês não poderão salvar-se".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27888, 'nvi', 'Atos', 'atos', 27, 32, 'Com isso os soldados cortaram as cordas que prendiam o barco salva-vidas e o deixaram cair.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27889, 'nvi', 'Atos', 'atos', 27, 33, 'Pouco antes do amanhecer, Paulo insistia que todos se alimentassem, dizendo: "Hoje faz catorze dias que vocês têm estado em vigília constante, sem nada comer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27890, 'nvi', 'Atos', 'atos', 27, 34, 'Agora eu os aconselho a comerem algo, pois só assim poderão sobreviver. Nenhum de vocês perderá um fio de cabelo sequer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27891, 'nvi', 'Atos', 'atos', 27, 35, 'Tendo dito isso, tomou pão e deu graças a Deus diante de todos. Então o partiu e começou a comer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27892, 'nvi', 'Atos', 'atos', 27, 36, 'Todos se reanimaram e também comeram algo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27893, 'nvi', 'Atos', 'atos', 27, 37, 'Estavam a bordo duzentas e setenta e seis pessoas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27894, 'nvi', 'Atos', 'atos', 27, 38, 'Depois de terem comido até ficarem satisfeitos, aliviaram o peso do navio, atirando todo o trigo ao mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27895, 'nvi', 'Atos', 'atos', 27, 39, 'Quando amanheceu não reconheceram a terra, mas viram uma enseada com uma praia, para onde decidiram conduzir o navio, se fosse possível.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27896, 'nvi', 'Atos', 'atos', 27, 40, 'Cortando as âncoras, deixaram-nas no mar, desatando ao mesmo tempo as cordas que prendiam os lemes. Então, alçando a vela da proa ao vento, dirigiram-se para a praia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27897, 'nvi', 'Atos', 'atos', 27, 41, 'Mas o navio encalhou num banco de areia, onde tocou o fundo. A proa encravou-se e ficou imóvel, e a popa foi quebrada pela violência das ondas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27898, 'nvi', 'Atos', 'atos', 27, 42, 'Os soldados resolveram matar os presos para impedir que algum deles fugisse, jogando-se ao mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27899, 'nvi', 'Atos', 'atos', 27, 43, 'Mas o centurião queria poupar a vida de Paulo e os impediu de executar o plano. Então ordenou aos que sabiam nadar que se lançassem primeiro ao mar em direção à terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27900, 'nvi', 'Atos', 'atos', 27, 44, 'Os outros teriam que salvar-se em tábuas ou em pedaços do navio. Dessa forma, todos chegaram a salvo em terra.');
