INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27901, 'nvi', 'Atos', 'atos', 28, 1, 'Uma vez em terra, descobrimos que a ilha se chamava Malta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27902, 'nvi', 'Atos', 'atos', 28, 2, 'Os habitantes da ilha mostraram extraordinária bondade para conosco. Fizeram uma fogueira e receberam bem a todos nós, pois estava chovendo e fazia frio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27903, 'nvi', 'Atos', 'atos', 28, 3, 'Paulo ajuntou um monte de gravetos; quando os colocava no fogo, uma víbora, fugindo do calor, prendeu-se à sua mão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27904, 'nvi', 'Atos', 'atos', 28, 4, 'Quando os habitantes da ilha viram a cobra agarrada na mão de Paulo, disseram uns aos outros: "Certamente este homem é assassino, pois, tendo escapado do mar, a Justiça não lhe permite viver".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27905, 'nvi', 'Atos', 'atos', 28, 5, 'Mas Paulo, sacudindo a cobra no fogo, não sofreu mal nenhum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27906, 'nvi', 'Atos', 'atos', 28, 6, 'Eles, porém, esperavam que ele começasse a inchar ou que caísse morto de repente, mas, tendo esperado muito tempo e vendo que nada de estranho lhe sucedia, mudaram de idéia e passaram a dizer que ele era um deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27907, 'nvi', 'Atos', 'atos', 28, 7, 'Próximo dali havia uma propriedade pertencente a Públio, o homem principal da ilha. Ele nos convidou a ficar em sua casa e, por três dias, bondosamente nos recebeu e nos hospedou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27908, 'nvi', 'Atos', 'atos', 28, 8, 'Seu pai estava doente, acamado, sofrendo de febre e disenteria. Paulo entrou para vê-lo e, depois de orar, impôs-lhe as mãos e o curou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27909, 'nvi', 'Atos', 'atos', 28, 9, 'Tendo acontecido isso, os outros doentes da ilha vieram e foram curados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27910, 'nvi', 'Atos', 'atos', 28, 10, 'Eles nos prestaram muitas honras e, quando estávamos para embarcar, forneceram-nos os suprimentos que necessitávamos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27911, 'nvi', 'Atos', 'atos', 28, 11, 'Passados três meses, embarcamos num navio que tinha passado o inverno na ilha; era um navio alexandrino, que tinha por emblema os deuses gêmeos Castor e Pólux.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27912, 'nvi', 'Atos', 'atos', 28, 12, 'Aportando em Siracusa, ficamos ali três dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27913, 'nvi', 'Atos', 'atos', 28, 13, 'Dali partimos e chegamos a Régio. No dia seguinte, soprando o vento sul, prosseguimos, chegando a Potéoli no segundo dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27914, 'nvi', 'Atos', 'atos', 28, 14, 'Ali encontramos alguns irmãos que nos convidaram a passar uma semana com eles. E depois fomos para Roma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27915, 'nvi', 'Atos', 'atos', 28, 15, 'Os irmãos dali tinham ouvido falar que estávamos chegando e foram até a praça de Ápio e às Três Vendas para nos encontrar. Vendo-os, Paulo deu graças a Deus e sentiu-se encorajado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27916, 'nvi', 'Atos', 'atos', 28, 16, 'Quando chegamos a Roma, Paulo recebeu permissão para morar por conta própria, sob a custódia de um soldado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27917, 'nvi', 'Atos', 'atos', 28, 17, 'Três dias depois, ele convocou os líderes dos judeus. Quando estes se reuniram, Paulo lhes disse: "Meus irmãos, embora eu não tenha feito nada contra o nosso povo nem contra os costumes dos nossos antepassados, fui preso em Jerusalém e entregue aos romanos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27918, 'nvi', 'Atos', 'atos', 28, 18, 'Eles me interrogaram e queriam me soltar, porque eu não era culpado de crime algum que merecesse pena de morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27919, 'nvi', 'Atos', 'atos', 28, 19, 'Todavia, tendo os judeus feito objeção, fui obrigado a apelar para César, não porém, por ter alguma acusação contra o meu próprio povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27920, 'nvi', 'Atos', 'atos', 28, 20, 'Por essa razão pedi para vê-los e conversar com vocês. Por causa da esperança de Israel é que estou preso com estas algemas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27921, 'nvi', 'Atos', 'atos', 28, 21, 'Eles responderam: "Não recebemos nenhuma carta da Judéia a seu respeito, e nenhum dos irmãos que vieram de lá relatou ou disse qualquer coisa de mal contra você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27922, 'nvi', 'Atos', 'atos', 28, 22, 'Todavia, queremos ouvir de sua parte o que você pensa, pois sabemos que por todo lugar há gente falando contra esta seita".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27923, 'nvi', 'Atos', 'atos', 28, 23, 'Assim combinaram encontrar-se com Paulo em dia determinado, indo em grupo ainda mais numeroso ao lugar onde ele estava. Desde a manhã até à tarde ele lhes deu explicações e lhes testemunhou do Reino de Deus, procurando convencê-los a respeito de Jesus, com base na Lei de Moisés e nos Profetas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27924, 'nvi', 'Atos', 'atos', 28, 24, 'Alguns foram convencidos pelo que ele dizia, mas outros não creram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27925, 'nvi', 'Atos', 'atos', 28, 25, 'Discordaram entre si mesmos e começaram a ir embora, depois de Paulo ter feito esta declaração final: "Bem que o Espírito Santo falou aos seus antepassados, por meio do profeta Isaías:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27926, 'nvi', 'Atos', 'atos', 28, 26, '‘Vá a este povo e diga: "Ainda que estejam sempre ouvindo, vocês nunca entenderão; ainda que estejam sempre vendo, jamais perceberão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27927, 'nvi', 'Atos', 'atos', 28, 27, 'Pois o coração deste povo se tornou insensível; de má vontade ouviram com os seus ouvidos, e fecharam os seus olhos. Se assim não fosse, poderiam ver com os olhos, ouvir com os ouvidos, entender com o coração e converter-se, e eu os curaria’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27928, 'nvi', 'Atos', 'atos', 28, 28, '"Portanto, quero que saibam que esta salvação de Deus é enviada aos gentios; eles a ouvirão! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27929, 'nvi', 'Atos', 'atos', 28, 29, 'Depois que ele disse isto, os judeus se retiraram, discutindo intensamente entre si.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27930, 'nvi', 'Atos', 'atos', 28, 30, 'Por dois anos inteiros Paulo permaneceu na casa que havia alugado, e recebia a todos os que iam vê-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27931, 'nvi', 'Atos', 'atos', 28, 31, 'Pregava o Reino de Deus e ensinava a respeito do Senhor Jesus Cristo, abertamente e sem impedimento algum.');
