INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26998, 'nvi', 'Atos', 'atos', 3, 1, 'Certo dia Pedro e João estavam subindo ao templo na hora da oração, às três horas da tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26999, 'nvi', 'Atos', 'atos', 3, 2, 'Estava sendo levado para a porta do templo chamada Formosa um aleijado de nascença, que ali era colocado todos os dias para pedir esmolas aos que entravam no templo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27000, 'nvi', 'Atos', 'atos', 3, 3, 'Vendo que Pedro e João iam entrar no pátio do templo, pediu-lhes esmola.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27001, 'nvi', 'Atos', 'atos', 3, 4, 'Pedro e João olharam bem para ele e, então, Pedro disse: "Olhe para nós! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27002, 'nvi', 'Atos', 'atos', 3, 5, 'O homem olhou para eles com atenção, esperando receber deles alguma coisa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27003, 'nvi', 'Atos', 'atos', 3, 6, 'Disse Pedro: "Não tenho prata nem ouro, mas o que tenho, isto lhe dou. Em nome de Jesus Cristo, o Nazareno, ande".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27004, 'nvi', 'Atos', 'atos', 3, 7, 'Segurando-o pela mão direita, ajudou-o a levantar-se, e imediatamente os pés e os tornozelos do homem ficaram firmes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27005, 'nvi', 'Atos', 'atos', 3, 8, 'E de um salto pôs-se de pé e começou a andar. Depois entrou com eles no pátio do templo, andando, saltando e louvando a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27006, 'nvi', 'Atos', 'atos', 3, 9, 'Quando todo o povo o viu andando e louvando a Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27007, 'nvi', 'Atos', 'atos', 3, 10, 'reconheceu que era ele o mesmo homem que costumava mendigar sentado à porta do templo chamada Formosa. Todos ficaram perplexos e muito admirados com o que lhe tinha acontecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27008, 'nvi', 'Atos', 'atos', 3, 11, 'Apegando-se o mendigo a Pedro e João, todo o povo ficou maravilhado e correu até eles, ao lugar chamado Pórtico de Salomão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27009, 'nvi', 'Atos', 'atos', 3, 12, 'Vendo isso, Pedro lhes disse: "Israelitas, por que isto os surpreende? Por que vocês estão olhando para nós, como se tivéssemos feito este homem andar por nosso próprio poder ou piedade?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27010, 'nvi', 'Atos', 'atos', 3, 13, 'O Deus de Abraão, de Isaque e de Jacó, o Deus dos nossos antepassados, glorificou seu servo Jesus, a quem vocês entregaram para ser morto e negaram perante Pilatos, embora ele tivesse decidido soltá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27011, 'nvi', 'Atos', 'atos', 3, 14, 'Vocês negaram publicamente o Santo e Justo e pediram que lhes fosse libertado um assassino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27012, 'nvi', 'Atos', 'atos', 3, 15, 'Vocês mataram o autor da vida, mas Deus o ressuscitou dos mortos. E nós somos testemunhas disso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27013, 'nvi', 'Atos', 'atos', 3, 16, 'Pela fé no nome de Jesus, o Nome curou este homem que vocês vêem e conhecem. A fé que vem por meio dele lhe deu esta saúde perfeita, como todos podem ver.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27014, 'nvi', 'Atos', 'atos', 3, 17, '"Agora, irmãos, eu sei que vocês agiram por ignorância, bem como os seus líderes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27015, 'nvi', 'Atos', 'atos', 3, 18, 'Mas foi assim que Deus cumpriu o que tinha predito por todos os profetas, dizendo que o seu Cristo haveria de sofrer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27016, 'nvi', 'Atos', 'atos', 3, 19, 'Arrependam-se, pois, e voltem-se para Deus, para que os seus pecados sejam cancelados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27017, 'nvi', 'Atos', 'atos', 3, 20, 'para que venham tempos de descanso da parte do Senhor, e ele mande o Cristo, o qual lhes foi designado, Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27018, 'nvi', 'Atos', 'atos', 3, 21, 'É necessário que ele permaneça no céu até que chegue o tempo em que Deus restaurará todas as coisas, como falou há muito tempo, por meio dos seus santos profetas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27019, 'nvi', 'Atos', 'atos', 3, 22, 'Pois disse Moisés: ‘O Senhor Deus lhes levantará dentre seus irmãos um profeta como eu; ouçam-no em tudo o que ele lhes disser.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27020, 'nvi', 'Atos', 'atos', 3, 23, 'Quem não ouvir esse profeta, será eliminado do meio do seu povo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27021, 'nvi', 'Atos', 'atos', 3, 24, '"De fato, todos os profetas, de Samuel em diante, um por um, falaram e predisseram estes dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27022, 'nvi', 'Atos', 'atos', 3, 25, 'E vocês são herdeiros dos profetas e da aliança que Deus fez com os seus antepassados. Ele disse a Abraão: ‘Por meio da sua descendência todos os povos da terra serão abençoados’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27023, 'nvi', 'Atos', 'atos', 3, 26, 'Tendo Deus ressuscitado o seu Servo, enviou-o primeiramente a vocês, para abençoá-los, convertendo cada um de vocês das suas maldades".');
