INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27024, 'nvi', 'Atos', 'atos', 4, 1, 'Enquanto Pedro e João falavam ao povo, chegaram os sacerdotes, o capitão da guarda do templo e os saduceus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27025, 'nvi', 'Atos', 'atos', 4, 2, 'Eles estavam muito perturbados porque os apóstolos estavam ensinando o povo e proclamando em Jesus a ressurreição dos mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27026, 'nvi', 'Atos', 'atos', 4, 3, 'Agarraram Pedro e João e, como já estava anoitecendo, os colocaram na prisão até o dia seguinte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27027, 'nvi', 'Atos', 'atos', 4, 4, 'Mas, muitos dos que tinham ouvido a mensagem creram, chegando o número dos homens que creram a perto de cinco mil.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27028, 'nvi', 'Atos', 'atos', 4, 5, 'No dia seguinte, as autoridades, os líderes religiosos e os mestres da lei reuniram-se em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27029, 'nvi', 'Atos', 'atos', 4, 6, 'Estavam ali Anás, o sumo sacerdote, bem como Caifás, João, Alexandre e todos os que eram da família do sumo sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27030, 'nvi', 'Atos', 'atos', 4, 7, 'Mandaram trazer Pedro e João diante deles e começaram a interrogá-los: "Com que poder ou em nome de quem vocês fizeram isso? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27031, 'nvi', 'Atos', 'atos', 4, 8, 'Então Pedro, cheio do Espírito Santo, disse-lhes: "Autoridades e líderes do povo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27032, 'nvi', 'Atos', 'atos', 4, 9, 'Visto que hoje somos chamados para prestar contas de um ato de bondade em favor de um aleijado, sendo interrogados acerca de como ele foi curado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27033, 'nvi', 'Atos', 'atos', 4, 10, 'saibam os senhores e todo o povo de Israel que por meio do nome de Jesus Cristo, o Nazareno, a quem os senhores crucificaram, mas a quem Deus ressuscitou dos mortos, este homem está aí curado diante dos senhores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27034, 'nvi', 'Atos', 'atos', 4, 11, 'Este Jesus é ‘a pedra que vocês, construtores, rejeitaram, e que se tornou a pedra angular’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27035, 'nvi', 'Atos', 'atos', 4, 12, 'Não há salvação em nenhum outro, pois, debaixo do céu não há nenhum outro nome dado aos homens pelo qual devamos ser salvos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27036, 'nvi', 'Atos', 'atos', 4, 13, 'Vendo a coragem de Pedro e de João, e percebendo que eram homens comuns e sem instrução, ficaram admirados e reconheceram que eles haviam estado com Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27037, 'nvi', 'Atos', 'atos', 4, 14, 'E como podiam ver ali com eles o homem que fora curado, nada podiam dizer contra eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27038, 'nvi', 'Atos', 'atos', 4, 15, 'Assim, ordenaram que se retirassem do Sinédrio e começaram a discutir,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27039, 'nvi', 'Atos', 'atos', 4, 16, 'perguntando: "Que faremos com esses homens? Todos os que moram em Jerusalém sabem que eles realizaram um milagre notório que não podemos negar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27040, 'nvi', 'Atos', 'atos', 4, 17, 'Todavia, para impedir que isso se espalhe ainda mais entre o povo, precisamos adverti-los de que não falem mais com ninguém sobre esse nome".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27041, 'nvi', 'Atos', 'atos', 4, 18, 'Então, chamando-os novamente, ordenaram-lhes que não falassem nem ensinassem em nome de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27042, 'nvi', 'Atos', 'atos', 4, 19, 'Mas Pedro e João responderam: "Julguem os senhores mesmos se é justo aos olhos de Deus obedecer aos senhores e não a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27043, 'nvi', 'Atos', 'atos', 4, 20, 'Pois não podemos deixar de falar do que vimos e ouvimos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27044, 'nvi', 'Atos', 'atos', 4, 21, 'Depois de mais ameaças, eles os deixaram ir. Não tinham como castigá-los, porque todo o povo estava louvando a Deus pelo que acontecera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27045, 'nvi', 'Atos', 'atos', 4, 22, 'Pois o homem que fora curado milagrosamente tinha mais de quarenta anos de idade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27046, 'nvi', 'Atos', 'atos', 4, 23, 'Quando foram soltos, Pedro e João voltaram para os seus e contaram tudo o que os chefes dos sacerdotes e os líderes religiosos lhes tinham dito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27047, 'nvi', 'Atos', 'atos', 4, 24, 'Ouvindo isso, levantaram juntos a voz a Deus, dizendo: "Ó Soberano, tu fizeste o céu, a terra, o mar e tudo o que neles há!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27048, 'nvi', 'Atos', 'atos', 4, 25, 'Tu falaste pelo Espírito Santo por boca do teu servo, nosso pai Davi: ‘Por que se enfurecem as nações, e os povos conspiram em vão?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27049, 'nvi', 'Atos', 'atos', 4, 26, 'Os reis da terra se levantam, e os governantes se reúnem contra o Senhor e contra o seu Ungido’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27050, 'nvi', 'Atos', 'atos', 4, 27, 'De fato, Herodes e Pôncio Pilatos reuniram-se com os gentios e com os povos de Israel nesta cidade, para conspirar contra o teu santo servo Jesus, a quem ungiste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27051, 'nvi', 'Atos', 'atos', 4, 28, 'Fizeram o que o teu poder e a tua vontade haviam decidido de antemão que acontecesse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27052, 'nvi', 'Atos', 'atos', 4, 29, 'Agora, Senhor, considera as ameaças deles e capacita os teus servos para anunciarem a tua palavra corajosamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27053, 'nvi', 'Atos', 'atos', 4, 30, 'Estende a tua mão para curar e realizar sinais e maravilhas por meio do nome do teu santo servo Jesus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27054, 'nvi', 'Atos', 'atos', 4, 31, 'Depois de orarem, tremeu o lugar em que estavam reunidos; todos ficaram cheios do Espírito Santo e anunciavam corajosamente a palavra de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27055, 'nvi', 'Atos', 'atos', 4, 32, 'Da multidão dos que creram, uma era a mente e um o coração. Ninguém considerava unicamente sua coisa alguma que possuísse, mas compartilhavam tudo o que tinham.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27056, 'nvi', 'Atos', 'atos', 4, 33, 'Com grande poder os apóstolos continuavam a testemunhar da ressurreição do Senhor Jesus, e grandiosa graça estava sobre todos eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27057, 'nvi', 'Atos', 'atos', 4, 34, 'Não havia pessoas necessitadas entre eles, pois os que possuíam terras ou casas as vendiam, traziam o dinheiro da venda');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27058, 'nvi', 'Atos', 'atos', 4, 35, 'e o colocavam aos pés dos apóstolos, que o distribuíam segundo a necessidade de cada um.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27059, 'nvi', 'Atos', 'atos', 4, 36, 'José, um levita de Chipre a quem os apóstolos deram o nome de Barnabé, que significa encorajador,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27060, 'nvi', 'Atos', 'atos', 4, 37, 'vendeu um campo que possuía, trouxe o dinheiro e o colocou aos pés dos apóstolos.');
