INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27118, 'nvi', 'Atos', 'atos', 7, 1, 'Então o sumo sacerdote perguntou a Estêvão: "São verdadeiras estas acusações? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27119, 'nvi', 'Atos', 'atos', 7, 2, 'A isso ele respondeu: "Irmãos e pais, ouçam-me! O Deus glorioso apareceu a Abraão, nosso pai, estando ele ainda na Mesopotâmia, antes de morar em Harã, e lhe disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27120, 'nvi', 'Atos', 'atos', 7, 3, '‘Saia da sua terra e do meio dos seus parentes e vá para a terra que eu lhe mostrarei’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27121, 'nvi', 'Atos', 'atos', 7, 4, '"Então, ele saiu da terra dos caldeus e se estabeleceu em Harã. Depois da morte de seu pai, Deus o trouxe a esta terra, onde vocês agora vivem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27122, 'nvi', 'Atos', 'atos', 7, 5, 'Deus não lhe deu nenhuma herança aqui, nem mesmo o espaço de um pé. Mas lhe prometeu que ele e, depois dele, seus descendentes, possuiriam a terra, embora, naquele tempo, Abraão não tivesse filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27123, 'nvi', 'Atos', 'atos', 7, 6, 'Deus lhe falou desta forma: ‘Seus descendentes serão peregrinos numa terra estrangeira, e serão escravizados e maltratados por quatrocentos anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27124, 'nvi', 'Atos', 'atos', 7, 7, 'Mas eu castigarei a nação a quem servirão como escravos, e depois sairão dali e me adorarão neste lugar’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27125, 'nvi', 'Atos', 'atos', 7, 8, 'E deu a Abraão a aliança da circuncisão. Por isso, Abraão gerou Isaque e o circuncidou oito dias depois do seu nascimento. Mais tarde, Isaque gerou Jacó, e este os doze patriarcas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27126, 'nvi', 'Atos', 'atos', 7, 9, '"Os patriarcas, tendo inveja de José, venderam-no como escravo para o Egito. Mas Deus estava com ele');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27127, 'nvi', 'Atos', 'atos', 7, 10, 'e o libertou de todas as suas tribulações, dando a José favor e sabedoria diante do faraó, rei do Egito; este o tornou governador do Egito e de todo o seu palácio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27128, 'nvi', 'Atos', 'atos', 7, 11, '"Depois houve fome em todo o Egito e em Canaã, trazendo consigo grande sofrimento, e os nossos antepassados não encontravam alimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27129, 'nvi', 'Atos', 'atos', 7, 12, 'Ouvindo que havia trigo no Egito, Jacó enviou nossos antepassados em sua primeira viagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27130, 'nvi', 'Atos', 'atos', 7, 13, 'Na segunda viagem deles, José fez-se reconhecer por seus irmãos, e o faraó pôde conhecer a família de José.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27131, 'nvi', 'Atos', 'atos', 7, 14, 'Depois disso, José mandou buscar seu pai Jacó e toda a sua família, que eram setenta e cinco pessoas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27132, 'nvi', 'Atos', 'atos', 7, 15, 'Então Jacó desceu ao Egito, onde faleceram ele e os nossos antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27133, 'nvi', 'Atos', 'atos', 7, 16, 'Seus corpos foram levados de volta a Siquém e colocados no túmulo que Abraão havia comprado ali dos filhos de Hamor, por certa quantia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27134, 'nvi', 'Atos', 'atos', 7, 17, '"Ao se aproximar o tempo em que Deus cumpriria sua promessa a Abraão, aumentou muito o número do nosso povo no Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27135, 'nvi', 'Atos', 'atos', 7, 18, 'Então outro rei, que nada sabia a respeito de José, passou a governar o Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27136, 'nvi', 'Atos', 'atos', 7, 19, 'Ele agiu traiçoeiramente para com o nosso povo e oprimiu os nossos antepassados, obrigando-os a abandonar os seus recém-nascidos, para que não sobrevivessem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27137, 'nvi', 'Atos', 'atos', 7, 20, '"Naquele tempo nasceu Moisés, que era um menino extraordinário. Por três meses ele foi criado na casa de seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27138, 'nvi', 'Atos', 'atos', 7, 21, 'Quando foi abandonado, a filha do faraó o tomou e o criou como seu próprio filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27139, 'nvi', 'Atos', 'atos', 7, 22, 'Moisés foi educado em toda a sabedoria dos egípcios e veio a ser poderoso em palavras e obras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27140, 'nvi', 'Atos', 'atos', 7, 23, '"Ao completar quarenta anos, Moisés decidiu visitar seus irmãos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27141, 'nvi', 'Atos', 'atos', 7, 24, 'Ao ver um deles sendo maltratado por um egípcio, saiu em defesa do oprimido e o vingou, matando o egípcio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27142, 'nvi', 'Atos', 'atos', 7, 25, 'Ele pensava que seus irmãos compreenderiam que Deus o estava usando para salvá-los, mas eles não o compreenderam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27143, 'nvi', 'Atos', 'atos', 7, 26, 'No dia seguinte, Moisés dirigiu-se a dois israelitas que estavam brigando, e tentou reconciliá-los, dizendo: ‘Homens, vocês são irmãos; por que ferem um ao outro? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27144, 'nvi', 'Atos', 'atos', 7, 27, '"Mas o homem que maltratava o outro empurrou Moisés e disse: ‘Quem o nomeou líder e juiz sobre nós?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27145, 'nvi', 'Atos', 'atos', 7, 28, 'Quer matar-me como matou o egípcio ontem? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27146, 'nvi', 'Atos', 'atos', 7, 29, 'Ouvindo isso, Moisés fugiu para Midiã, onde ficou morando como estrangeiro e teve dois filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27147, 'nvi', 'Atos', 'atos', 7, 30, '"Passados quarenta anos, apareceu a Moisés um anjo nas labaredas de uma sarça em chamas no deserto, perto do monte Sinai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27148, 'nvi', 'Atos', 'atos', 7, 31, 'Vendo aquilo, ficou atônito. E, aproximando-se para observar, ouviu a voz do Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27149, 'nvi', 'Atos', 'atos', 7, 32, '‘Eu sou o Deus dos seus antepassados, o Deus de Abraão, o Deus de Isaque e o Deus de Jacó’. Moisés, tremendo de medo, não ousava olhar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27150, 'nvi', 'Atos', 'atos', 7, 33, '"Então o Senhor lhe disse: ‘Tire as sandálias dos pés, porque o lugar em que você está é terra santa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27151, 'nvi', 'Atos', 'atos', 7, 34, 'De fato tenho visto a opressão sobre o meu povo no Egito. Ouvi seus gemidos e desci para livrá-lo. Venha agora, e eu o enviarei de volta ao Egito’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27152, 'nvi', 'Atos', 'atos', 7, 35, '"Este é o mesmo Moisés que tinham rejeitado com estas palavras: ‘Quem o nomeou líder e juiz? ’ Ele foi enviado pelo próprio Deus para ser líder e libertador deles, por meio do anjo que lhe tinha aparecido na sarça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27153, 'nvi', 'Atos', 'atos', 7, 36, 'Ele os tirou de lá, fazendo maravilhas e sinais no Egito, no mar Vermelho e no deserto durante quarenta anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27154, 'nvi', 'Atos', 'atos', 7, 37, '"Este é aquele Moisés que disse aos israelitas: ‘Deus lhes levantará dentre seus irmãos um profeta como eu’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27155, 'nvi', 'Atos', 'atos', 7, 38, 'Ele estava na congregação, no deserto, com o anjo que lhe falava no monte Sinai e com os nossos antepassados, e recebeu palavras vivas, para transmiti-las a nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27156, 'nvi', 'Atos', 'atos', 7, 39, '"Mas nossos antepassados se recusaram a obedecer-lhe; pelo contrário, rejeitaram-no, e em seus corações voltaram para o Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27157, 'nvi', 'Atos', 'atos', 7, 40, 'Disseram a Arão: ‘Faça para nós deuses que nos conduzam, pois a esse Moisés que nos tirou do Egito, não sabemos o que lhe aconteceu! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27158, 'nvi', 'Atos', 'atos', 7, 41, 'Naquela ocasião fizeram um ídolo em forma de bezerro. Trouxeram-lhe sacrifícios e fizeram uma celebração em honra ao que suas mãos tinham feito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27159, 'nvi', 'Atos', 'atos', 7, 42, 'Mas Deus afastou-se deles e os entregou à adoração dos astros, conforme o que foi escrito no livro dos profetas: ‘Foi a mim que vocês apresentaram sacrifícios e ofertas durante os quarenta anos no deserto, Ó nação de Israel?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27160, 'nvi', 'Atos', 'atos', 7, 43, 'Ao invés disso, levantaram o santuário de Moloque e a estrela do seu deus Renfã, ídolos que vocês fizeram para adorar! Portanto, eu os enviarei para o exílio, para além da Babilônia’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27161, 'nvi', 'Atos', 'atos', 7, 44, '"No deserto os nossos antepassados tinham o tabernáculo da aliança, que fora feito segundo a ordem de Deus a Moisés, de acordo com o modelo que ele tinha visto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27162, 'nvi', 'Atos', 'atos', 7, 45, 'Tendo recebido o tabernáculo, nossos antepassados o levaram, sob a liderança de Josué, quando tomaram a terra das nações que Deus expulsou de diante deles. Esse tabernáculo permaneceu na terra até a época de Davi,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27163, 'nvi', 'Atos', 'atos', 7, 46, 'que encontrou graça diante de Deus e pediu que ele lhe permitisse providenciar uma habitação para o Deus de Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27164, 'nvi', 'Atos', 'atos', 7, 47, 'Mas foi Salomão quem lhe construiu a casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27165, 'nvi', 'Atos', 'atos', 7, 48, '"Todavia, o Altíssimo não habita em casas feitas por homens. Como diz o profeta:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27166, 'nvi', 'Atos', 'atos', 7, 49, '‘O céu é o meu trono, e a terra, o estrado dos meus pés. Que espécie de casa vocês me edificarão? diz o Senhor, ou onde seria meu lugar de descanso?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27167, 'nvi', 'Atos', 'atos', 7, 50, 'Não foram as minhas mãos que fizeram todas estas coisas? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27168, 'nvi', 'Atos', 'atos', 7, 51, '"Povo rebelde, obstinado de coração e de ouvidos! Vocês são iguais aos seus antepassados: sempre resistem ao Espírito Santo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27169, 'nvi', 'Atos', 'atos', 7, 52, 'Qual dos profetas os seus antepassados não perseguiram? Eles mataram aqueles que prediziam a vinda do Justo, de quem agora vocês se tornaram traidores e assassinos —');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27170, 'nvi', 'Atos', 'atos', 7, 53, 'vocês, que receberam a Lei por intermédio de anjos, mas não lhe obedeceram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27171, 'nvi', 'Atos', 'atos', 7, 54, 'Ouvindo isso, ficavam furiosos e rangiam os dentes contra ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27172, 'nvi', 'Atos', 'atos', 7, 55, 'Mas Estêvão, cheio do Espírito Santo, levantou os olhos para o céu e viu a glória de Deus, e Jesus de pé, à direita de Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27173, 'nvi', 'Atos', 'atos', 7, 56, 'e disse: "Vejo o céu aberto e o Filho do homem de pé, à direita de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27174, 'nvi', 'Atos', 'atos', 7, 57, 'Mas eles taparam os ouvidos e, gritando bem alto, lançaram-se todos juntos contra ele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27175, 'nvi', 'Atos', 'atos', 7, 58, 'arrastaram-no para fora da cidade e começaram a apedrejá-lo. As testemunhas deixaram seus mantos aos pés de um jovem chamado Saulo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27176, 'nvi', 'Atos', 'atos', 7, 59, 'Enquanto apedrejavam Estêvão, este orava: "Senhor Jesus, recebe o meu espírito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27177, 'nvi', 'Atos', 'atos', 7, 60, 'Então caiu de joelhos e bradou: "Senhor, não os consideres culpados deste pecado". E, dizendo isso, adormeceu.');
