INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27178, 'nvi', 'Atos', 'atos', 8, 1, 'E Saulo estava ali, consentindo na morte de Estêvão. Naquela ocasião desencadeou-se grande perseguição contra a igreja em Jerusalém. Todos, exceto os apóstolos, foram dispersos pelas regiões da Judéia e de Samaria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27179, 'nvi', 'Atos', 'atos', 8, 2, 'Alguns homens piedosos sepultaram Estêvão e fizeram por ele grande lamentação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27180, 'nvi', 'Atos', 'atos', 8, 3, 'Saulo, por sua vez, devastava a igreja. Indo de casa em casa, arrastava homens e mulheres e os lançava na prisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27181, 'nvi', 'Atos', 'atos', 8, 4, 'Os que haviam sido dispersos pregavam a palavra por onde quer que fossem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27182, 'nvi', 'Atos', 'atos', 8, 5, 'Indo Filipe para uma cidade de Samaria, ali lhes anunciava o Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27183, 'nvi', 'Atos', 'atos', 8, 6, 'Quando a multidão ouviu Filipe e viu os sinais miraculosos que ele realizava, deu unânime atenção ao que ele dizia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27184, 'nvi', 'Atos', 'atos', 8, 7, 'Os espíritos imundos saíam de muitos, dando gritos, e muitos paralíticos e mancos foram curados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27185, 'nvi', 'Atos', 'atos', 8, 8, 'Assim, houve grande alegria naquela cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27186, 'nvi', 'Atos', 'atos', 8, 9, 'Um homem chamado Simão vinha praticando feitiçaria durante algum tempo naquela cidade, impressionando todo o povo de Samaria. Ele se dizia muito importante,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27187, 'nvi', 'Atos', 'atos', 8, 10, 'e todo o povo, do mais simples ao mais rico, dava-lhe atenção e exclamava: "Este homem é o poder divino conhecido como Grande Poder".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27188, 'nvi', 'Atos', 'atos', 8, 11, 'Eles o seguiam, pois ele os havia iludido com sua mágica durante muito tempo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27189, 'nvi', 'Atos', 'atos', 8, 12, 'No entanto, quando Filipe lhes pregou as boas novas do Reino de Deus e do nome de Jesus Cristo, creram nele, e foram batizados, tanto homens como mulheres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27190, 'nvi', 'Atos', 'atos', 8, 13, 'O próprio Simão também creu e foi batizado, e seguia a Filipe por toda parte, observando maravilhado os grandes sinais e milagres que eram realizados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27191, 'nvi', 'Atos', 'atos', 8, 14, 'Os apóstolos em Jerusalém, ouvindo que Samaria havia aceitado a palavra de Deus, enviaram para lá Pedro e João.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27192, 'nvi', 'Atos', 'atos', 8, 15, 'Estes, ao chegarem, oraram para que eles recebessem o Espírito Santo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27193, 'nvi', 'Atos', 'atos', 8, 16, 'pois o Espírito ainda não havia descido sobre nenhum deles; tinham apenas sido batizados em nome do Senhor Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27194, 'nvi', 'Atos', 'atos', 8, 17, 'Então Pedro e João lhes impuseram as mãos, e eles receberam o Espírito Santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27195, 'nvi', 'Atos', 'atos', 8, 18, 'Vendo Simão que o Espírito era dado com a imposição das mãos dos apóstolos, ofereceu-lhes dinheiro');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27196, 'nvi', 'Atos', 'atos', 8, 19, 'e disse: "Dêem-me também este poder, para que a pessoa sobre quem eu impuser as mãos receba o Espírito Santo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27197, 'nvi', 'Atos', 'atos', 8, 20, 'Pedro respondeu: "Pereça com você o seu dinheiro! Você pensa que pode comprar o dom de Deus com dinheiro?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27198, 'nvi', 'Atos', 'atos', 8, 21, 'Você não tem parte nem direito algum neste ministério, porque o seu coração não é reto diante de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27199, 'nvi', 'Atos', 'atos', 8, 22, 'Arrependa-se dessa maldade e ore ao Senhor. Talvez ele lhe perdoe tal pensamento do seu coração,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27200, 'nvi', 'Atos', 'atos', 8, 23, 'pois vejo que você está cheio de amargura e preso pelo pecado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27201, 'nvi', 'Atos', 'atos', 8, 24, 'Simão, porém, respondeu: "Orem vocês ao Senhor por mim, para que não me aconteça nada do que vocês disseram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27202, 'nvi', 'Atos', 'atos', 8, 25, 'Tendo testemunhado e proclamado a palavra do Senhor, Pedro e João voltaram a Jerusalém, pregando o evangelho em muitos povoados samaritanos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27203, 'nvi', 'Atos', 'atos', 8, 26, 'Um anjo do Senhor disse a Filipe: "Vá para o sul, para a estrada deserta que desce de Jerusalém a Gaza".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27204, 'nvi', 'Atos', 'atos', 8, 27, 'Ele se levantou e partiu. No caminho encontrou um eunuco etíope, um oficial importante, encarregado de todos os tesouros de Candace, rainha dos etíopes. Esse homem viera a Jerusalém para adorar a Deus e,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27205, 'nvi', 'Atos', 'atos', 8, 28, 'de volta para casa, sentado em sua carruagem, lia o livro do profeta Isaías.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27206, 'nvi', 'Atos', 'atos', 8, 29, 'E o Espírito disse a Filipe: "Aproxime-se dessa carruagem e acompanhe-a".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27207, 'nvi', 'Atos', 'atos', 8, 30, 'Então Filipe correu para a carruagem, ouviu o homem lendo o profeta Isaías e lhe perguntou: "O senhor entende o que está lendo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27208, 'nvi', 'Atos', 'atos', 8, 31, 'Ele respondeu: "Como posso entender se alguém não me explicar? " Assim, convidou Filipe para subir e sentar-se ao seu lado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27209, 'nvi', 'Atos', 'atos', 8, 32, 'O eunuco estava lendo esta passagem da Escritura: "Ele foi levado como ovelha para o matadouro, e como cordeiro mudo diante do tosquiador, ele não abriu a sua boca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27210, 'nvi', 'Atos', 'atos', 8, 33, 'Em sua humilhação foi privado de justiça. Quem pode falar dos seus descendentes? Pois a sua vida foi tirada da terra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27211, 'nvi', 'Atos', 'atos', 8, 34, 'O eunuco perguntou a Filipe: "Diga-me, por favor: de quem o profeta está falando? De si próprio ou de outro? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27212, 'nvi', 'Atos', 'atos', 8, 35, 'Então Filipe, começando com aquela passagem da Escritura, anunciou-lhe as boas novas de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27213, 'nvi', 'Atos', 'atos', 8, 36, 'Prosseguindo pela estrada, chegaram a um lugar onde havia água. O eunuco disse: "Olhe, aqui há água. Que me impede de ser batizado? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27214, 'nvi', 'Atos', 'atos', 8, 37, 'Disse Filipe: "Você pode, se crê de todo o coração". O eunuco respondeu: "Creio que Jesus Cristo é o Filho de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27215, 'nvi', 'Atos', 'atos', 8, 38, 'Assim, deu ordem para parar a carruagem. Então Filipe e o eunuco desceram à água, e Filipe o batizou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27216, 'nvi', 'Atos', 'atos', 8, 39, 'Quando saíram da água, o Espírito do Senhor arrebatou Filipe repentinamente. O eunuco não o viu mais e, cheio de alegria, seguiu o seu caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27217, 'nvi', 'Atos', 'atos', 8, 40, 'Filipe, porém, apareceu em Azoto e, indo para Cesaréia, pregava o evangelho em todas as cidades pelas quais passava.');
