INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27218, 'nvi', 'Atos', 'atos', 9, 1, 'Enquanto isso, Saulo ainda respirava ameaças de morte contra os discípulos do Senhor. Dirigindo-se ao sumo sacerdote,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27219, 'nvi', 'Atos', 'atos', 9, 2, 'pediu-lhe cartas para as sinagogas de Damasco, de maneira que, caso encontrasse ali homens ou mulheres que pertencessem ao Caminho, pudesse levá-los presos para Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27220, 'nvi', 'Atos', 'atos', 9, 3, 'Em sua viagem, quando se aproximava de Damasco, de repente brilhou ao seu redor uma luz vinda do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27221, 'nvi', 'Atos', 'atos', 9, 4, 'Ele caiu por terra e ouviu uma voz que lhe dizia: "Saulo, Saulo, por que você me persegue? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27222, 'nvi', 'Atos', 'atos', 9, 5, 'Saulo perguntou: "Quem és tu, Senhor? " Ele respondeu: "Eu sou Jesus, a quem você persegue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27223, 'nvi', 'Atos', 'atos', 9, 6, 'Levante-se, entre na cidade; alguém lhe dirá o que você deve fazer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27224, 'nvi', 'Atos', 'atos', 9, 7, 'Os homens que viajavam com Saulo pararam emudecidos; ouviam a voz mas não viam ninguém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27225, 'nvi', 'Atos', 'atos', 9, 8, 'Saulo levantou-se do chão e, abrindo os olhos, não conseguia ver nada. E eles o levaram pela mão até Damasco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27226, 'nvi', 'Atos', 'atos', 9, 9, 'Por três dias ele esteve cego, não comeu nem bebeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27227, 'nvi', 'Atos', 'atos', 9, 10, 'Em Damasco havia um discípulo chamado Ananias. O Senhor o chamou numa visão: "Ananias! " "Eis-me aqui, Senhor", respondeu ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27228, 'nvi', 'Atos', 'atos', 9, 11, 'O Senhor lhe disse: "Vá à casa de Judas, na rua chamada Direita, e pergunte por um homem de Tarso chamado Saulo. Ele está orando;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27229, 'nvi', 'Atos', 'atos', 9, 12, 'numa visão viu um homem chamado Ananias chegar e impor-lhe as mãos para que voltasse a ver".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27230, 'nvi', 'Atos', 'atos', 9, 13, 'Respondeu Ananias: "Senhor, tenho ouvido muita coisa a respeito desse homem e de todo o mal que ele tem feito aos teus santos em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27231, 'nvi', 'Atos', 'atos', 9, 14, 'Ele chegou aqui com autorização dos chefes dos sacerdotes para prender todos os que invocam o teu nome".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27232, 'nvi', 'Atos', 'atos', 9, 15, 'Mas o Senhor disse a Ananias: "Vá! Este homem é meu instrumento escolhido para levar o meu nome perante os gentios e seus reis, e perante o povo de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27233, 'nvi', 'Atos', 'atos', 9, 16, 'Mostrarei a ele o quanto deve sofrer pelo meu nome".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27234, 'nvi', 'Atos', 'atos', 9, 17, 'Então Ananias foi, entrou na casa, impôs as mãos sobre Saulo e disse: "Irmão Saulo, o Senhor Jesus, que lhe apareceu no caminho por onde você vinha, enviou-me para que você volte a ver e seja cheio do Espírito Santo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27235, 'nvi', 'Atos', 'atos', 9, 18, 'Imediatamente, algo como escamas caiu dos olhos de Saulo e ele passou a ver novamente. Levantando-se, foi batizado');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27236, 'nvi', 'Atos', 'atos', 9, 19, 'e, depois de comer, recuperou as forças. Saulo passou vários dias com os discípulos em Damasco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27237, 'nvi', 'Atos', 'atos', 9, 20, 'Logo começou a pregar nas sinagogas que Jesus é o Filho de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27238, 'nvi', 'Atos', 'atos', 9, 21, 'Todos os que o ouviam ficavam perplexos e perguntavam: "Não é ele o homem que procurava destruir em Jerusalém aqueles que invocam este nome? E não veio para cá justamente para levá-los presos aos chefes dos sacerdotes? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27239, 'nvi', 'Atos', 'atos', 9, 22, 'Todavia, Saulo se fortalecia cada vez mais e confundia os judeus que viviam em Damasco, demonstrando que Jesus é o Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27240, 'nvi', 'Atos', 'atos', 9, 23, 'Decorridos muitos dias, os judeus decidiram de comum acordo matá-lo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27241, 'nvi', 'Atos', 'atos', 9, 24, 'mas Saulo ficou sabendo do plano deles. Dia e noite eles vigiavam as portas da cidade a fim de matá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27242, 'nvi', 'Atos', 'atos', 9, 25, 'Mas os seus discípulos o levaram de noite e o fizeram descer num cesto, através de uma abertura na muralha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27243, 'nvi', 'Atos', 'atos', 9, 26, 'Quando chegou a Jerusalém, tentou reunir-se aos discípulos, mas todos estavam com medo dele, não acreditando que fosse realmente um discípulo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27244, 'nvi', 'Atos', 'atos', 9, 27, 'Então Barnabé o levou aos apóstolos e lhes contou como, no caminho, Saulo vira o Senhor, que lhe falara, e como em Damasco ele havia pregado corajosamente em nome de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27245, 'nvi', 'Atos', 'atos', 9, 28, 'Assim, Saulo ficou com eles, e andava com liberdade em Jerusalém, pregando corajosamente em nome do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27246, 'nvi', 'Atos', 'atos', 9, 29, 'Falava e discutia com os judeus de fala grega, mas estes tentavam matá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27247, 'nvi', 'Atos', 'atos', 9, 30, 'Sabendo disso, os irmãos o levaram para Cesaréia e o enviaram para Tarso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27248, 'nvi', 'Atos', 'atos', 9, 31, 'A igreja passava por um período de paz em toda a Judéia, Galiléia e Samaria. Ela se edificava e, encorajada pelo Espírito Santo, crescia em número, vivendo no temor do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27249, 'nvi', 'Atos', 'atos', 9, 32, 'Viajando por toda parte, Pedro foi visitar os santos que viviam em Lida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27250, 'nvi', 'Atos', 'atos', 9, 33, 'Ali encontrou um paralítico chamado Enéias, que estava acamado fazia oito anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27251, 'nvi', 'Atos', 'atos', 9, 34, 'Disse-lhe Pedro: "Enéias, Jesus Cristo vai curá-lo! Levante-se e arrume a sua cama". Ele se levantou imediatamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27252, 'nvi', 'Atos', 'atos', 9, 35, 'Todos os que viviam em Lida e Sarona o viram e se converteram ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27253, 'nvi', 'Atos', 'atos', 9, 36, 'Em Jope havia uma discípula chamada Tabita, que em grego é Dorcas, que se dedicava a praticar boas obras e dar esmolas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27254, 'nvi', 'Atos', 'atos', 9, 37, 'Naqueles dias ela ficou doente e morreu, e seu corpo foi lavado e colocado num quarto do andar superior.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27255, 'nvi', 'Atos', 'atos', 9, 38, 'Lida ficava perto de Jope, e quando os discípulos ouviram falar que Pedro estava em Lida, mandaram-lhe dois homens dizer-lhe: "Não se demore em vir até nós".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27256, 'nvi', 'Atos', 'atos', 9, 39, 'Pedro foi com eles e, quando chegou, foi levado para o quarto do andar superior. Todas as viúvas o rodearam, chorando e mostrando-lhe os vestidos e outras roupas que Dorcas tinha feito quando ainda estava com elas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27257, 'nvi', 'Atos', 'atos', 9, 40, 'Pedro mandou que todos saíssem do quarto; depois, ajoelhou-se e orou. Voltando-se para a mulher morta, disse: "Tabita, levante-se". Ela abriu os olhos e, vendo Pedro, sentou-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27258, 'nvi', 'Atos', 'atos', 9, 41, 'Tomando-a pela mão, ajudou-a a pôr-se de pé. Então, chamando os santos e as viúvas, apresentou-a viva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27259, 'nvi', 'Atos', 'atos', 9, 42, 'Este fato se tornou conhecido em toda a cidade de Jope, e muitos creram no Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (27260, 'nvi', 'Atos', 'atos', 9, 43, 'Pedro ficou em Jope durante algum tempo, com um curtidor de couro chamado Simão.');
