INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29467, 'nvi', 'Colossenses', 'cl', 1, 1, 'Paulo, apóstolo de Cristo Jesus pela vontade de Deus, e o irmão Timóteo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29468, 'nvi', 'Colossenses', 'cl', 1, 2, 'aos santos e fiéis irmãos em Cristo que estão em Colossos: A vocês, graça e paz da parte de Deus nosso Pai e do Senhor Jesus Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29469, 'nvi', 'Colossenses', 'cl', 1, 3, 'Sempre agradecemos a Deus, o Pai de nosso Senhor Jesus Cristo, quando oramos por vocês,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29470, 'nvi', 'Colossenses', 'cl', 1, 4, 'pois temos ouvido falar da fé que vocês têm em Cristo Jesus e do amor por todos os santos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29471, 'nvi', 'Colossenses', 'cl', 1, 5, 'por causa da esperança que lhes está reservada nos céus, a respeito da qual vocês ouviram por meio da palavra da verdade, o evangelho');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29472, 'nvi', 'Colossenses', 'cl', 1, 6, 'que chegou até vocês. Por todo o mundo este evangelho vai frutificando e crescendo, como também ocorre entre vocês, desde o dia em que o ouviram e entenderam a graça de Deus em toda a sua verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29473, 'nvi', 'Colossenses', 'cl', 1, 7, 'Vocês o aprenderam de Epafras, nosso amado cooperador, fiel ministro de Cristo para conosco,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29474, 'nvi', 'Colossenses', 'cl', 1, 8, 'que também nos falou do amor que vocês têm no Espírito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29475, 'nvi', 'Colossenses', 'cl', 1, 9, 'Por essa razão, desde o dia em que o ouvimos, não deixamos de orar por vocês e de pedir que sejam cheios do pleno conhecimento da vontade de Deus, com toda a sabedoria e entendimento espiritual.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29476, 'nvi', 'Colossenses', 'cl', 1, 10, 'E isso para que vocês vivam de maneira digna do Senhor e em tudo possam agradá-lo, frutificando em toda boa obra, crescendo no conhecimento de Deus e');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29477, 'nvi', 'Colossenses', 'cl', 1, 11, 'sendo fortalecidos com todo o poder, de acordo com a força da sua glória, para que tenham toda a perseverança e paciência com alegria,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29478, 'nvi', 'Colossenses', 'cl', 1, 12, 'dando graças ao Pai, que nos tornou dignos de participar da herança dos santos no reino da luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29479, 'nvi', 'Colossenses', 'cl', 1, 13, 'Pois ele nos resgatou do domínio das trevas e nos transportou para o Reino do seu Filho amado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29480, 'nvi', 'Colossenses', 'cl', 1, 14, 'em quem temos a redenção, a saber, o perdão dos pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29481, 'nvi', 'Colossenses', 'cl', 1, 15, 'Ele é a imagem do Deus invisível, o primogênito de toda a criação,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29482, 'nvi', 'Colossenses', 'cl', 1, 16, 'pois nele foram criadas todas as coisas nos céus e na terra, as visíveis e as invisíveis, sejam tronos ou soberanias, poderes ou autoridades; todas as coisas foram criadas por ele e para ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29483, 'nvi', 'Colossenses', 'cl', 1, 17, 'Ele é antes de todas as coisas, e nele tudo subsiste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29484, 'nvi', 'Colossenses', 'cl', 1, 18, 'Ele é a cabeça do corpo, que é a igreja; é o princípio e o primogênito dentre os mortos, para que em tudo tenha a supremacia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29485, 'nvi', 'Colossenses', 'cl', 1, 19, 'Pois foi do agrado de Deus que nele habitasse toda a plenitude,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29486, 'nvi', 'Colossenses', 'cl', 1, 20, 'e por meio dele reconciliasse consigo todas as coisas, tanto as que estão na terra quanto as que estão no céu, estabelecendo a paz pelo seu sangue derramado na cruz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29487, 'nvi', 'Colossenses', 'cl', 1, 21, 'Antes vocês estavam separados de Deus e, em suas mentes, eram inimigos por causa do mau procedimento de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29488, 'nvi', 'Colossenses', 'cl', 1, 22, 'Mas agora ele os reconciliou pelo corpo físico de Cristo, mediante a morte, para apresentá-los diante dele santos, inculpáveis e livres de qualquer acusação,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29489, 'nvi', 'Colossenses', 'cl', 1, 23, 'desde que continuem alicerçados e firmes na fé, sem se afastarem da esperança do evangelho, que vocês ouviram e que tem sido proclamado a todos os que estão debaixo do céu. Esse é o evangelho do qual eu, Paulo, me tornei ministro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29490, 'nvi', 'Colossenses', 'cl', 1, 24, 'Agora me alegro em meus sofrimentos por vocês, e completo no meu corpo o que resta das aflições de Cristo, em favor do seu corpo, que é a igreja.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29491, 'nvi', 'Colossenses', 'cl', 1, 25, 'Dela me tornei ministro de acordo com a responsabilidade por Deus a mim atribuída de apresentar-lhes plenamente a palavra de Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29492, 'nvi', 'Colossenses', 'cl', 1, 26, 'o mistério que esteve oculto durante épocas e gerações, mas que agora foi manifestado a seus santos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29493, 'nvi', 'Colossenses', 'cl', 1, 27, 'A eles quis Deus dar a conhecer entre os gentios a gloriosa riqueza deste mistério, que é Cristo em vocês, a esperança da glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29494, 'nvi', 'Colossenses', 'cl', 1, 28, 'Nós o proclamamos, advertindo e ensinando a cada um com toda a sabedoria, a fim de que apresentemos todo homem perfeito em Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29495, 'nvi', 'Colossenses', 'cl', 1, 29, 'Para isso eu me esforço, lutando conforme a sua força, que atua poderosamente em mim.');
