INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29496, 'nvi', 'Colossenses', 'cl', 2, 1, 'Quero que vocês saibam quanto estou lutando por vocês, pelos que estão em Laodicéia e por todos os que ainda não me conhecem pessoalmente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29497, 'nvi', 'Colossenses', 'cl', 2, 2, 'Esforço-me para que eles sejam fortalecidos em seus corações, estejam unidos em amor e alcancem toda a riqueza do pleno entendimento, a fim de conhecerem plenamente o mistério de Deus, a saber, Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29498, 'nvi', 'Colossenses', 'cl', 2, 3, 'Nele estão escondidos todos os tesouros da sabedoria e do conhecimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29499, 'nvi', 'Colossenses', 'cl', 2, 4, 'Eu lhes digo isso para que ninguém os engane com argumentos aparentemente convincentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29500, 'nvi', 'Colossenses', 'cl', 2, 5, 'Porque, embora esteja fisicamente ausente, estou presente com vocês em espírito, e alegro-me em ver como estão vivendo em ordem e como está firme a fé que vocês têm em Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29501, 'nvi', 'Colossenses', 'cl', 2, 6, 'Portanto, assim como vocês receberam a Cristo Jesus, o Senhor, continuem a viver nele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29502, 'nvi', 'Colossenses', 'cl', 2, 7, 'enraizados e edificados nele, firmados na fé, como foram ensinados, transbordando de gratidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29503, 'nvi', 'Colossenses', 'cl', 2, 8, 'Tenham cuidado para que ninguém os escravize a filosofias vãs e enganosas, que se fundamentam nas tradições humanas e nos princípios elementares deste mundo, e não em Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29504, 'nvi', 'Colossenses', 'cl', 2, 9, 'Pois em Cristo habita corporalmente toda a plenitude da divindade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29505, 'nvi', 'Colossenses', 'cl', 2, 10, 'e, por estarem nele, que é o Cabeça de todo poder e autoridade, vocês receberam a plenitude.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29506, 'nvi', 'Colossenses', 'cl', 2, 11, 'Nele também vocês foram circuncidados, não com uma circuncisão feita por mãos humanas, mas com a circuncisão feita por Cristo, que é o despojar do corpo da carne.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29507, 'nvi', 'Colossenses', 'cl', 2, 12, 'Isso aconteceu quando vocês foram sepultados com ele no batismo, e com ele foram ressuscitados mediante a fé no poder de Deus que o ressuscitou dentre os mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29508, 'nvi', 'Colossenses', 'cl', 2, 13, 'Quando vocês estavam mortos em pecados e na incircuncisão da sua carne, Deus os vivificou juntamente com Cristo. Ele nos perdoou todas as transgressões,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29509, 'nvi', 'Colossenses', 'cl', 2, 14, 'e cancelou a escrita de dívida, que consistia em ordenanças, e que nos era contrária. Ele a removeu, pregando-a na cruz,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29510, 'nvi', 'Colossenses', 'cl', 2, 15, 'e, tendo despojado os poderes e as autoridades, fez deles um espetáculo público, triunfando sobre eles na cruz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29511, 'nvi', 'Colossenses', 'cl', 2, 16, 'Portanto, não permitam que ninguém os julgue pelo que vocês comem ou bebem, ou com relação a alguma festividade religiosa ou à celebração das luas novas ou dos dias de sábado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29512, 'nvi', 'Colossenses', 'cl', 2, 17, 'Estas coisas são sombras do que haveria de vir; a realidade, porém, encontra-se em Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29513, 'nvi', 'Colossenses', 'cl', 2, 18, 'Não permitam que ninguém que tenha prazer numa falsa humildade e na adoração de anjos os impeça de alcançar o prêmio. Tal pessoa conta detalhadamente suas visões, e sua mente carnal a torna orgulhosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29514, 'nvi', 'Colossenses', 'cl', 2, 19, 'Trata-se de alguém que não está unido à Cabeça, a partir da qual todo o corpo, sustentado e unido por seus ligamentos e juntas, efetua o crescimento dado por Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29515, 'nvi', 'Colossenses', 'cl', 2, 20, 'Já que vocês morreram com Cristo para os princípios elementares deste mundo, por que é que vocês, então, como se ainda pertencessem a ele, se submetem a regras:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29516, 'nvi', 'Colossenses', 'cl', 2, 21, '"Não manuseie! " "Não prove! " "Não toque! "?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29517, 'nvi', 'Colossenses', 'cl', 2, 22, 'Todas essas coisas estão destinadas a perecer pelo uso, pois se baseiam em mandamentos e ensinos humanos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29518, 'nvi', 'Colossenses', 'cl', 2, 23, 'Essas regras têm, de fato, aparência de sabedoria, com sua pretensa religiosidade, falsa humildade e severidade com o corpo, mas não têm valor algum para refrear os impulsos da carne.');
