INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29519, 'nvi', 'Colossenses', 'cl', 3, 1, 'Portanto, já que vocês ressuscitaram com Cristo, procurem as coisas que são do alto, onde Cristo está assentado à direita de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29520, 'nvi', 'Colossenses', 'cl', 3, 2, 'Mantenham o pensamento nas coisas do alto, e não nas coisas terrenas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29521, 'nvi', 'Colossenses', 'cl', 3, 3, 'Pois vocês morreram, e agora a sua vida está escondida com Cristo em Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29522, 'nvi', 'Colossenses', 'cl', 3, 4, 'Quando Cristo, que é a sua vida, for manifestado, então vocês também serão manifestados com ele em glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29523, 'nvi', 'Colossenses', 'cl', 3, 5, 'Assim, façam morrer tudo o que pertence à natureza terrena de vocês: imoralidade sexual, impureza, paixão, desejos maus e a ganância, que é idolatria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29524, 'nvi', 'Colossenses', 'cl', 3, 6, 'É por causa dessas coisas que vem a ira de Deus sobre os que vivem na desobediência,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29525, 'nvi', 'Colossenses', 'cl', 3, 7, 'as quais vocês praticaram no passado, quando costumavam viver nelas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29526, 'nvi', 'Colossenses', 'cl', 3, 8, 'Mas agora, abandonem todas estas coisas: ira, indignação, maldade, maledicência e linguagem indecente no falar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29527, 'nvi', 'Colossenses', 'cl', 3, 9, 'Não mintam uns aos outros, visto que vocês já se despiram do velho homem com suas práticas');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29528, 'nvi', 'Colossenses', 'cl', 3, 10, 'e se revestiram do novo, o qual está sendo renovado em conhecimento, à imagem do seu Criador.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29529, 'nvi', 'Colossenses', 'cl', 3, 11, 'Nessa nova vida já não há diferença entre grego e judeu, circunciso e incircunciso, bárbaro e cita, escravo e livre, mas Cristo é tudo e está em todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29530, 'nvi', 'Colossenses', 'cl', 3, 12, 'Portanto, como povo escolhido de Deus, santo e amado, revistam-se de profunda compaixão, bondade, humildade, mansidão e paciência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29531, 'nvi', 'Colossenses', 'cl', 3, 13, 'Suportem-se uns aos outros e perdoem as queixas que tiverem uns contra os outros. Perdoem como o Senhor lhes perdoou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29532, 'nvi', 'Colossenses', 'cl', 3, 14, 'Acima de tudo, porém, revistam-se do amor, que é o elo perfeito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29533, 'nvi', 'Colossenses', 'cl', 3, 15, 'Que a paz de Cristo seja o juiz em seus corações, visto que vocês foram chamados a viver em paz, como membros de um só corpo. E sejam agradecidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29534, 'nvi', 'Colossenses', 'cl', 3, 16, 'Habite ricamente em vocês a palavra de Cristo; ensinem e aconselhem-se uns aos outros com toda a sabedoria, e cantem salmos, hinos e cânticos espirituais com gratidão a Deus em seus corações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29535, 'nvi', 'Colossenses', 'cl', 3, 17, 'Tudo o que fizerem, seja em palavra ou em ação, façam-no em nome do Senhor Jesus, dando por meio dele graças a Deus Pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29536, 'nvi', 'Colossenses', 'cl', 3, 18, 'Mulheres, sujeitem-se a seus maridos, como convém a quem está no Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29537, 'nvi', 'Colossenses', 'cl', 3, 19, 'Maridos, amem suas mulheres e não as tratem com amargura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29538, 'nvi', 'Colossenses', 'cl', 3, 20, 'Filhos, obedeçam a seus pais em tudo, pois isso agrada ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29539, 'nvi', 'Colossenses', 'cl', 3, 21, 'Pais, não irritem seus filhos, para que eles não se desanimem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29540, 'nvi', 'Colossenses', 'cl', 3, 22, 'Escravos, obedeçam em tudo a seus senhores terrenos, não somente para agradar os homens quando eles estão observando, mas com sinceridade de coração, pelo fato de vocês temerem ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29541, 'nvi', 'Colossenses', 'cl', 3, 23, 'Tudo o que fizerem, façam de todo o coração, como para o Senhor, e não para os homens,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29542, 'nvi', 'Colossenses', 'cl', 3, 24, 'sabendo que receberão do Senhor a recompensa da herança. É a Cristo, o Senhor, que vocês estão servindo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (29543, 'nvi', 'Colossenses', 'cl', 3, 25, 'Quem cometer injustiça receberá de volta injustiça, e não haverá exceção para ninguém.');
