INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21739, 'nvi', 'Daniel', 'dn', 1, 1, 'No terceiro ano do reinado de Jeoaquim, rei de Judá, Nabucodonosor, rei da Babilônia, veio a Jerusalém e a sitiou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21740, 'nvi', 'Daniel', 'dn', 1, 2, 'E o Senhor entregou Jeoaquim, rei de Judá, nas suas mãos, e também alguns dos utensílios do templo de Deus. Ele levou os utensílios para o templo do seu deus na terra de Sinear e os colocou na casa do tesouro do seu deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21741, 'nvi', 'Daniel', 'dn', 1, 3, 'Então o rei ordenou que Aspenaz, o chefe dos oficiais da sua corte, trouxesse alguns dos israelitas da família real e da nobreza;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21742, 'nvi', 'Daniel', 'dn', 1, 4, 'jovens sem defeito físico, de boa aparência, cultos, inteligentes, que dominassem os vários campos do conhecimento e fossem capacitados para servir no palácio do rei. Ele devia ensinar-lhes a língua e a literatura dos babilônios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21743, 'nvi', 'Daniel', 'dn', 1, 5, 'O rei designou-lhes uma porção diária de comida e de vinho da própria mesa do rei. Eles receberiam um treinamento durante três anos, e depois disso passariam a servir o rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21744, 'nvi', 'Daniel', 'dn', 1, 6, 'Entre esses estavam alguns que vieram de Judá: Daniel, Hananias, Misael e Azarias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21745, 'nvi', 'Daniel', 'dn', 1, 7, 'O chefe dos oficiais deu-lhes novos nomes: a Daniel deu o nome de Beltessazar; a Hananias, Sadraque; a Misael, Mesaque; e a Azarias, Abede-Nego.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21746, 'nvi', 'Daniel', 'dn', 1, 8, 'Daniel, contudo, decidiu não se tornar impuro com a comida e com o vinho do rei, e pediu ao chefe dos oficiais permissão para se abster deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21747, 'nvi', 'Daniel', 'dn', 1, 9, 'E Deus fez com que o homem fosse bondoso para com Daniel e tivesse simpatia por ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21748, 'nvi', 'Daniel', 'dn', 1, 10, 'Apesar disso, ele disse a Daniel: "Tenho medo do rei, o meu senhor, que determinou a comida e a bebida de vocês. E se ele os achar menos saudáveis que os outros jovens da mesma idade? O rei poderia pedir a minha cabeça por causa de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21749, 'nvi', 'Daniel', 'dn', 1, 11, 'Daniel disse então ao homem que o chefe dos oficiais tinha encarregado de cuidar de Daniel, Hananias, Misael e Azarias:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21750, 'nvi', 'Daniel', 'dn', 1, 12, '"Peço-lhe que faça uma experiência com os seus servos durante dez dias: Não nos dê nada além de vegetais para comer e água para beber.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21751, 'nvi', 'Daniel', 'dn', 1, 13, 'Depois compare a nossa aparência com a dos jovens que comem a comida do rei, e trate os seus servos de acordo com o que você concluir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21752, 'nvi', 'Daniel', 'dn', 1, 14, 'Ele concordou e fez a experiência com eles durante dez dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21753, 'nvi', 'Daniel', 'dn', 1, 15, 'Passados os dez dias eles pareciam mais saudáveis e mais fortes do que todos os jovens que comiam a comida da mesa do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21754, 'nvi', 'Daniel', 'dn', 1, 16, 'Assim o encarregado tirou a comida especial e o vinho que haviam sido designados e em lugar disso lhes dava vegetais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21755, 'nvi', 'Daniel', 'dn', 1, 17, 'A esses quatro jovens Deus deu sabedoria e inteligência para conhecerem todos os aspectos da cultura e da ciência. E Daniel, além disso, sabia interpretar todo tipo de visões e sonhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21756, 'nvi', 'Daniel', 'dn', 1, 18, 'Ao final do tempo estabelecido pelo rei para que os jovens fossem trazidos à sua presença, o chefe dos oficiais os apresentou a Nabucodonosor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21757, 'nvi', 'Daniel', 'dn', 1, 19, 'O rei conversou com eles, e não encontrou ninguém comparável a Daniel, Hananias, Misael e Azarias; de modo que eles passaram a servir o rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21758, 'nvi', 'Daniel', 'dn', 1, 20, 'O rei lhes fez perguntas sobre todos os assuntos nos quais se exigia sabedoria e conhecimento, e descobriu que eram dez vezes mais sábios do que todos os magos e encantadores de todo o seu reino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21759, 'nvi', 'Daniel', 'dn', 1, 21, 'E Daniel permaneceu ali até o primeiro ano do rei Ciro.');
