INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22017, 'nvi', 'Daniel', 'dn', 10, 1, 'No terceiro ano de Ciro, rei da Pérsia, Daniel, chamado Beltessazar, recebeu uma revelação. A mensagem era verdadeira e falava de uma grande guerra. E numa visão veio-lhe o entendimento da mensagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22018, 'nvi', 'Daniel', 'dn', 10, 2, 'Naquela ocasião eu, Daniel, passei três semanas chorando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22019, 'nvi', 'Daniel', 'dn', 10, 3, 'Não comi nada saboroso; carne e vinho nem provei; e não usei nenhuma fragrância perfumada, até se passarem as três semanas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22020, 'nvi', 'Daniel', 'dn', 10, 4, 'No vigésimo quarto dia do primeiro mês, estava eu de pé junto à margem do grande rio, o Tigre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22021, 'nvi', 'Daniel', 'dn', 10, 5, 'Olhei para cima, e diante de mim estava um homem vestido de linho, com um cinto de ouro puríssimo na cintura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22022, 'nvi', 'Daniel', 'dn', 10, 6, 'Seu corpo era como o berilo, o rosto como o relâmpago, os olhos como tochas acesas, os braços e pernas como o reflexo do bronze polido, e a sua voz era como o som de uma multidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22023, 'nvi', 'Daniel', 'dn', 10, 7, 'Somente eu, Daniel, vi a visão; os que me acompanhavam nada viram, mas apoderou-se deles tanto pavor que eles fugiram e se esconderam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22024, 'nvi', 'Daniel', 'dn', 10, 8, 'Assim fiquei sozinho, olhando para aquela grande visão; fiquei sem forças, muito pálido, e quase desfaleci.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22025, 'nvi', 'Daniel', 'dn', 10, 9, 'Então eu o ouvi falando, e, ao ouvi-lo, caí prostrado, rosto em terra, e perdi os sentidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22026, 'nvi', 'Daniel', 'dn', 10, 10, 'Em seguida, a mão de alguém tocou em mim e me pôs sobre as minhas mãos e os meus joelhos vacilantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22027, 'nvi', 'Daniel', 'dn', 10, 11, 'E ele disse: "Daniel, você é muito amado. Preste bem atenção ao que vou lhe falar; levante-se, pois eu fui enviado a você". Quando ele me disse isso, pus-me de pé, tremendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22028, 'nvi', 'Daniel', 'dn', 10, 12, 'E ele prosseguiu: "Não tenha medo, Daniel. Desde o primeiro dia em que você decidiu buscar entendimento e humilhar-se diante do seu Deus, suas palavras foram ouvidas, e eu vim em resposta a elas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22029, 'nvi', 'Daniel', 'dn', 10, 13, 'Mas o príncipe do reino da Pérsia me resistiu vinte e um dias. Então Miguel, um dos príncipes supremos, veio em minha ajuda, pois eu fui impedido de prosseguir ali com os reis da Pérsia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22030, 'nvi', 'Daniel', 'dn', 10, 14, 'Agora vim explicar-lhe o que acontecerá ao seu povo no futuro, pois a visão se refere a uma época futura".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22031, 'nvi', 'Daniel', 'dn', 10, 15, 'Enquanto ele me dizia isso, prostrei-me, rosto em terra, sem conseguir falar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22032, 'nvi', 'Daniel', 'dn', 10, 16, 'Então um ser que parecia homem tocou nos meus lábios, e eu abri a minha boca e comecei a falar. Eu disse àquele que estava de pé diante de mim: Estou angustiado por causa da visão, meu senhor, e quase desfaleço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22033, 'nvi', 'Daniel', 'dn', 10, 17, 'Como posso eu, teu servo, conversar contigo, meu senhor? Minhas forças se foram, e mal posso respirar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22034, 'nvi', 'Daniel', 'dn', 10, 18, 'O ser que parecia homem tocou em mim outra vez e me deu forças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22035, 'nvi', 'Daniel', 'dn', 10, 19, 'Ele disse: "Não tenha medo, você, que é muito amado. Que a paz seja com você! Seja forte! Seja forte! " Ditas essas palavras, senti-me fortalecido e disse: "Fala, meu senhor, visto que me deste forças".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22036, 'nvi', 'Daniel', 'dn', 10, 20, 'Então ele me disse: "Você sabe por que vim? Vou ter que voltar para lutar contra o príncipe da Pérsia, e, logo que eu for, chegará o príncipe da Grécia;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22037, 'nvi', 'Daniel', 'dn', 10, 21, 'mas antes lhe revelarei o que está escrito no Livro da Verdade. E nessa luta ninguém me ajuda contra eles, senão Miguel, o príncipe de vocês,');
