INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21907, 'nvi', 'Daniel', 'dn', 6, 1, 'Dario achou por bem nomear cento e vinte sátrapas para governarem todo o reino;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21908, 'nvi', 'Daniel', 'dn', 6, 2, 'e colocou três supervisores sobre eles, um dos quais era Daniel. Os sátrapas tinham que prestar contas a eles para que o rei não sofresse nenhuma perda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21909, 'nvi', 'Daniel', 'dn', 6, 3, 'Ora, Daniel se destacou tanto entre os supervisores e os sátrapas por suas grandes qualidades, que o rei planejava colocá-lo à frente do governo de todo o império.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21910, 'nvi', 'Daniel', 'dn', 6, 4, 'Diante disso, os supervisores e os sátrapas procuraram motivos para acusar Daniel em sua administração governamental, mas nada conseguiram. Não puderam achar falta alguma nele, pois ele era fiel; não era desonesto nem negligente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21911, 'nvi', 'Daniel', 'dn', 6, 5, 'Finalmente esses homens disseram: "Jamais encontraremos algum motivo para acusar esse Daniel, a menos que seja algo relacionado com a lei do Deus dele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21912, 'nvi', 'Daniel', 'dn', 6, 6, 'E assim os supervisores e os sátrapas de comum acordo foram falar com o rei: "Ó rei Dario, vive para sempre!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21913, 'nvi', 'Daniel', 'dn', 6, 7, 'Todos os supervisores reais, os prefeitos, os sátrapas, os conselheiros e os governadores concordaram em que o rei deve emitir um decreto ordenando que todo aquele que orar a qualquer deus ou a qualquer homem nos próximos trinta dias, exceto a ti, ó rei, seja atirado na cova dos leões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21914, 'nvi', 'Daniel', 'dn', 6, 8, 'Agora, ó rei, emite o decreto e assina-o para que não seja alterado, conforme a lei dos medos e dos persas, que não pode ser revogada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21915, 'nvi', 'Daniel', 'dn', 6, 9, 'E o rei Dario assinou o decreto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21916, 'nvi', 'Daniel', 'dn', 6, 10, 'Quando Daniel soube que o decreto tinha sido publicado, foi para casa, para o seu quarto, no andar de cima, onde as janelas davam para Jerusalém. Três vezes por dia ele se ajoelhava e orava, agradecendo ao seu Deus, como costumava fazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21917, 'nvi', 'Daniel', 'dn', 6, 11, 'Então aqueles homens foram ver e encontraram Daniel orando, pedindo ajuda a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21918, 'nvi', 'Daniel', 'dn', 6, 12, 'Assim foram falar com o rei acerca do decreto real: "Tu não publicaste um decreto ordenando que nos próximos trinta dias todo aquele que fizesse algum pedido a qualquer deus ou a qualquer homem, exceto a ti, ó rei, seria lançado na cova dos leões? " O rei respondeu: "O decreto está em vigor, conforme a lei dos medos e dos persas, que não pode ser revogada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21919, 'nvi', 'Daniel', 'dn', 6, 13, 'Então disseram ao rei: "Daniel, um dos exilados de Judá, não te dá ouvidos, ó rei, nem ao decreto que assinaste. Ele continua orando três vezes por dia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21920, 'nvi', 'Daniel', 'dn', 6, 14, 'Quando o rei ouviu isso, ficou muito contrariado, e como estava decidido a salvar Daniel, até o pôr-do-sol fez todo o esforço que pôde para livrá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21921, 'nvi', 'Daniel', 'dn', 6, 15, 'Mas os homens lhe disseram: "Lembra-te, ó rei, que, conforme a lei dos medos e dos persas, nenhum decreto ou edito do rei pode ser modificado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21922, 'nvi', 'Daniel', 'dn', 6, 16, 'Então o rei deu ordens, e eles trouxeram Daniel e o jogaram na cova dos leões. O rei, porém, disse a Daniel: "Que o seu Deus, a quem você serve continuamente, o livre! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21923, 'nvi', 'Daniel', 'dn', 6, 17, 'Taparam a cova com uma pedra, e o rei a selou com o seu próprio anel-selo e com os anéis dos seus nobres, para que a situação de Daniel não se modificasse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21924, 'nvi', 'Daniel', 'dn', 6, 18, 'Tendo voltado ao palácio, o rei passou a noite sem comer e não aceitou nenhum divertimento em sua presença. Além disso, não conseguiu dormir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21925, 'nvi', 'Daniel', 'dn', 6, 19, 'Logo ao alvorecer, o rei se levantou e correu para a cova dos leões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21926, 'nvi', 'Daniel', 'dn', 6, 20, 'Quando ia se aproximando da cova, chamou Daniel com voz aflita: "Daniel, servo do Deus vivo, será que o seu Deus, a quem você serve continuamente, pôde livrá-lo dos leões? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21927, 'nvi', 'Daniel', 'dn', 6, 21, 'Daniel respondeu: "Ó rei, vive para sempre!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21928, 'nvi', 'Daniel', 'dn', 6, 22, 'O meu Deus enviou o seu anjo, que fechou a boca dos leões. Eles não me fizeram mal algum, pois fui considerado inocente à vista de Deus. Também contra ti não cometi mal algum, ó rei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21929, 'nvi', 'Daniel', 'dn', 6, 23, 'O rei muito se alegrou e ordenou que tirassem Daniel da cova. Quando o tiraram da cova, viram que não havia nele nenhum ferimento, pois ele tinha confiado no seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21930, 'nvi', 'Daniel', 'dn', 6, 24, 'E por ordem do rei, os homens que tinham acusado Daniel foram atirados na cova dos leões, juntamente com as suas mulheres e os seus filhos. E, antes de chegarem ao fundo, os leões os atacaram e despedaçaram todos os seus ossos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21931, 'nvi', 'Daniel', 'dn', 6, 25, 'Então o rei Dario escreveu aos homens de todas as nações, povos e línguas de toda a terra: "Paz e prosperidade!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21932, 'nvi', 'Daniel', 'dn', 6, 26, '"Estou editando um decreto para que nos domínios do império os homens temam e reverenciem o Deus de Daniel. "Pois ele é o Deus vivo e permanece para sempre; o seu reino não será destruído, o seu domínio jamais acabará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21933, 'nvi', 'Daniel', 'dn', 6, 27, 'Ele livra e salva; faz sinais e maravilhas nos céus e na terra. Ele livrou Daniel do poder dos leões".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21934, 'nvi', 'Daniel', 'dn', 6, 28, 'Assim Daniel prosperou durante os reinados de Dario e de Ciro, o persa.');
