INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21963, 'nvi', 'Daniel', 'dn', 8, 1, 'No terceiro ano do reinado do rei Belsazar, eu, Daniel, tive outra visão, depois da primeira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21964, 'nvi', 'Daniel', 'dn', 8, 2, 'Na minha visão eu me vi na cidadela de Susã, na província de Elão; na visão eu estava junto do canal de Ulai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21965, 'nvi', 'Daniel', 'dn', 8, 3, 'Olhei para cima, e diante de mim, junto ao canal, estava um carneiro, seus dois chifres eram compridos, um mais que o outro, mas o mais comprido cresceu depois do outro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21966, 'nvi', 'Daniel', 'dn', 8, 4, 'Observei o carneiro enquanto ele avançava para o oeste, para o norte e para o sul. Nenhum animal conseguia resistir-lhe, e ninguém podia livrar-se do seu poder. Ele fazia o que bem queria e foi ficando cada vez maior.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21967, 'nvi', 'Daniel', 'dn', 8, 5, 'Enquanto eu estava considerando isso, de repente um bode, com um chifre enorme entre os olhos, veio do oeste, percorrendo toda a extensão da terra sem encostar no chão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21968, 'nvi', 'Daniel', 'dn', 8, 6, 'Ele veio na direção do carneiro de dois chifres que eu tinha visto ao lado do canal, e avançou contra ele com grande fúria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21969, 'nvi', 'Daniel', 'dn', 8, 7, 'Eu o vi atacar furiosamente o carneiro, atingi-lo e quebrar os seus dois chifres. O carneiro não teve forças para resistir-lhe; o bode o derrubou no chão e o pisoteou, e ninguém foi capaz de livrar o carneiro do seu poder.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21970, 'nvi', 'Daniel', 'dn', 8, 8, 'O bode tornou-se muito grande, mas no auge da sua força o seu grande chifre foi quebrado, e em seu lugar cresceram quatro chifres enormes, na direção dos quatro ventos da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21971, 'nvi', 'Daniel', 'dn', 8, 9, 'De um deles saiu um outro chifre, que começou pequeno, mas cresceu em poder na direção do sul, do leste e da Terra Magnífica.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21972, 'nvi', 'Daniel', 'dn', 8, 10, 'Cresceu até alcançar o exército dos céus, e atirou na terra alguns componentes do exército das estrelas e os pisoteou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21973, 'nvi', 'Daniel', 'dn', 8, 11, 'Tanto cresceu que chegou a desafiar o príncipe do exército; suprimiu o sacrifício diário oferecido ao príncipe, e o local do santuário foi destruído.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21974, 'nvi', 'Daniel', 'dn', 8, 12, 'Por causa da rebelião, o exército dos santos e o sacrifício diário foram dados ao chifre. Ele tinha êxito em tudo o que fazia, e a verdade foi lançada por terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21975, 'nvi', 'Daniel', 'dn', 8, 13, 'Então ouvi dois anjos conversando, e um deles perguntou ao outro: "Quanto tempo durarão os acontecimentos anunciados por essa visão? Até quando será suprimido o sacrifício diário e a rebelião devastadora prevalecerá? Até quando o santuário e o exército ficarão entregues ao poder do chifre e serão pisoteados? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21976, 'nvi', 'Daniel', 'dn', 8, 14, 'Ele me disse: "Isso tudo levará duas mil e trezentas tardes e manhãs; então o santuário será reconsagrado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21977, 'nvi', 'Daniel', 'dn', 8, 15, 'Enquanto eu, Daniel, observava a visão e tentava entendê-la, diante de mim apareceu um ser que parecia homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21978, 'nvi', 'Daniel', 'dn', 8, 16, 'E ouvi a voz de um homem que vinha do Ulai: "Gabriel, dê a esse homem o significado da visão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21979, 'nvi', 'Daniel', 'dn', 8, 17, 'Quando ele se aproximou de mim, fiquei aterrorizado e caí prostrado. E ele me disse: "Filho do homem, saiba que a visão refere-se aos tempos do fim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21980, 'nvi', 'Daniel', 'dn', 8, 18, 'Enquanto ele falava comigo, caí prostrado, rosto em terra, e perdi os sentidos. Então ele tocou em mim e me pôs de pé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21981, 'nvi', 'Daniel', 'dn', 8, 19, 'E disse: "Vou contar-lhe o que acontecerá depois, no tempo da ira, pois a visão se refere ao tempo do fim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21982, 'nvi', 'Daniel', 'dn', 8, 20, 'O carneiro de dois chifres que você viu representa os reis da Média e da Pérsia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21983, 'nvi', 'Daniel', 'dn', 8, 21, 'O bode peludo é o rei da Grécia, e o grande chifre entre os seus olhos é o primeiro rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21984, 'nvi', 'Daniel', 'dn', 8, 22, 'Os quatro chifres que tomaram o lugar do chifre que foi quebrado são quatro reinos que surgirão da nação daquele rei, mas não terão o mesmo poder.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21985, 'nvi', 'Daniel', 'dn', 8, 23, '"No final do reinado deles, quando a rebelião dos ímpios tiver chegado ao máximo, surgirá um rei de duro semblante, mestre em astúcias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21986, 'nvi', 'Daniel', 'dn', 8, 24, 'Ele se tornará muito forte, mas não pelo seu próprio poder. Provocará devastações terríveis e será bem sucedido em tudo o que fizer. Destruirá os homens poderosos e o povo santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21987, 'nvi', 'Daniel', 'dn', 8, 25, 'Com o intuito de prosperar, ele enganará a muitos, e se considerará superior aos outros. Destruirá muitos que nele confiam e se insurgirá contra o Príncipe dos príncipes. Apesar disso, ele será destruído, mas não pelo poder dos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21988, 'nvi', 'Daniel', 'dn', 8, 26, '"A visão das tardes e das manhãs que você recebeu é verdadeira; sela porém a visão, pois refere-se ao futuro distante".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (21989, 'nvi', 'Daniel', 'dn', 8, 27, 'Eu, Daniel, fiquei exausto e doente por vários dias. Depois levantei-me e voltei a cuidar dos negócios do rei. Fiquei assustado com a visão; estava além da compreensão.');
