INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4893, 'nvi', 'Deuteronômio', 'dt', 1, 1, 'Estas são as palavras ditas por Moisés a todo o Israel no deserto, a leste do Jordão, na Arabá, defronte de Sufe, entre Parã e Tofel, Labã, Hazerote e Di-Zaabe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4894, 'nvi', 'Deuteronômio', 'dt', 1, 2, 'Em onze dias se vai de Horebe a Cades-Barnéia pelo caminho dos montes de Seir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4895, 'nvi', 'Deuteronômio', 'dt', 1, 3, 'No quadragésimo ano, no primeiro dia do décimo primeiro mês, Moisés proclamou aos israelitas todas as ordens do Senhor acerca deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4896, 'nvi', 'Deuteronômio', 'dt', 1, 4, 'Isso foi depois que ele derrotou Seom, rei dos amorreus, que habitava em Hesbom, e em Edrei derrotou Ogue, rei de Basã, que habitava em Asterote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4897, 'nvi', 'Deuteronômio', 'dt', 1, 5, 'A leste do Jordão, na terra de Moabe, Moisés tomou sobre si a responsabilidade de expor esta lei:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4898, 'nvi', 'Deuteronômio', 'dt', 1, 6, 'O Senhor, o nosso Deus, disse-nos em Horebe: "Vocês já ficaram bastante tempo nesta montanha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4899, 'nvi', 'Deuteronômio', 'dt', 1, 7, 'Levantem acampamento e avancem para a serra dos amorreus; vão a todos os povos vizinhos na Arabá, nas montanhas, na Sefelá, no Neguebe e ao longo do litoral, à terra dos cananeus e ao Líbano, até o grande rio, o Eufrates.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4900, 'nvi', 'Deuteronômio', 'dt', 1, 8, '"Ponho esta terra diante de vocês. Entrem e tomem posse da terra que o Senhor prometeu sob juramento dar aos seus antepassados, Abraão, Isaque e Jacó, e aos seus descendentes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4901, 'nvi', 'Deuteronômio', 'dt', 1, 9, 'Naquela ocasião eu lhes disse: "Não posso levá-los sozinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4902, 'nvi', 'Deuteronômio', 'dt', 1, 10, 'O Senhor, o seu Deus, os fez multiplicar de tal modo que hoje vocês são tão numerosos quanto as estrelas do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4903, 'nvi', 'Deuteronômio', 'dt', 1, 11, 'Que o Senhor, o Deus dos seus antepassados, os multiplique mil vezes mais e os abençoe, conforme lhes prometeu!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4904, 'nvi', 'Deuteronômio', 'dt', 1, 12, 'Mas como poderei levar sozinho os seus problemas, as suas cargas e as suas disputas?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4905, 'nvi', 'Deuteronômio', 'dt', 1, 13, 'Escolham homens sábios, criteriosos e experientes de cada uma de suas tribos, e eu os colocarei como chefes de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4906, 'nvi', 'Deuteronômio', 'dt', 1, 14, 'Vocês me disseram que essa era uma boa proposta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4907, 'nvi', 'Deuteronômio', 'dt', 1, 15, 'Então convoquei os chefes das tribos, homens sábios e experientes, e os designei como chefes de mil, de cem, de cinqüenta e de dez, além de oficiais para cada tribo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4908, 'nvi', 'Deuteronômio', 'dt', 1, 16, 'Naquela ocasião ordenei aos juízes de vocês: "Atendam as questões de seus irmãos e julguem com justiça, não só as questões entre os seus compatriotas como também entre um israelita e um estrangeiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4909, 'nvi', 'Deuteronômio', 'dt', 1, 17, 'Não sejam parciais no julgamento! Atendam tanto o pequeno como o grande. Não se deixem intimidar por ninguém, pois o veredicto pertence a Deus. Tragam-me os casos mais difíceis e eu os ouvirei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4910, 'nvi', 'Deuteronômio', 'dt', 1, 18, 'Naquela ocasião eu lhes ordenei tudo o que deveriam fazer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4911, 'nvi', 'Deuteronômio', 'dt', 1, 19, 'Depois, conforme o Senhor, o nosso Deus, nos tinha ordenado, partimos de Horebe e fomos para a serra dos amorreus, passando por todo aquele imenso e terrível deserto que vocês viram, e assim chegamos a Cades-Barnéia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4912, 'nvi', 'Deuteronômio', 'dt', 1, 20, 'Então eu lhes disse: "Vocês chegaram à serra dos amorreus, a qual o Senhor, o nosso Deus, nos dá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4913, 'nvi', 'Deuteronômio', 'dt', 1, 21, 'Vejam, o Senhor, o seu Deus, põe diante de vocês esta terra. Entrem na terra e tomem posse dela, conforme o Senhor, o Deus dos seus antepassados, lhes disse. Não tenham medo nem se desanimem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4914, 'nvi', 'Deuteronômio', 'dt', 1, 22, 'Vocês todos vieram dizer-me: "Mandemos alguns homens à nossa frente em missão de reconhecimento da região, para que nos indiquem por qual caminho subiremos e a quais cidades iremos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4915, 'nvi', 'Deuteronômio', 'dt', 1, 23, 'A sugestão pareceu-me boa; por isso escolhi doze de vocês, um homem de cada tribo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4916, 'nvi', 'Deuteronômio', 'dt', 1, 24, 'Eles subiram a região montanhosa, chegaram ao vale de Escol e o exploraram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4917, 'nvi', 'Deuteronômio', 'dt', 1, 25, 'Trouxeram alguns frutos da região, com o seguinte relato: "Essa terra que o Senhor, o nosso Deus, nos dá é boa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4918, 'nvi', 'Deuteronômio', 'dt', 1, 26, 'Vocês, contudo, não quiseram ir, e se rebelaram contra a ordem do Senhor, o seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4919, 'nvi', 'Deuteronômio', 'dt', 1, 27, 'Queixaram-se em suas tendas, dizendo: "O Senhor nos odeia; por isso nos trouxe do Egito para nos entregar nas mãos dos amorreus e destruir-nos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4920, 'nvi', 'Deuteronômio', 'dt', 1, 28, 'Para onde iremos? Nossos compatriotas nos desanimaram quando disseram: O povo é mais forte e mais alto do que nós; as cidades são grandes, com muros que vão até o céu. Vimos ali os enaquins".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4921, 'nvi', 'Deuteronômio', 'dt', 1, 29, 'Então eu lhes disse: Não fiquem apavorados; não tenham medo deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4922, 'nvi', 'Deuteronômio', 'dt', 1, 30, 'O Senhor, o seu Deus, que está indo à frente de vocês, lutará por vocês, como fez no Egito, diante de seus próprios olhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4923, 'nvi', 'Deuteronômio', 'dt', 1, 31, 'Também no deserto vocês viram como o Senhor, o seu Deus, os carregou, como um pai carrega seu filho, por todo o caminho que percorreram até chegarem a este lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4924, 'nvi', 'Deuteronômio', 'dt', 1, 32, 'Apesar disso, vocês não confiaram no Senhor, o seu Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4925, 'nvi', 'Deuteronômio', 'dt', 1, 33, 'que foi à frente de vocês, numa coluna de fogo de noite e numa nuvem de dia, para procurar lugares para vocês acamparem e para mostrar-lhes o caminho que vocês deviam seguir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4926, 'nvi', 'Deuteronômio', 'dt', 1, 34, 'Quando o Senhor ouviu o que vocês diziam, irou-se e jurou:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4927, 'nvi', 'Deuteronômio', 'dt', 1, 35, '"Ninguém desta geração má verá a boa terra que jurei dar aos seus antepassados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4928, 'nvi', 'Deuteronômio', 'dt', 1, 36, 'exceto Calebe, filho de Jefoné. Ele a verá, e eu darei a ele e a seus descendentes a terra em que pisou, pois seguiu o Senhor de todo o coração".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4929, 'nvi', 'Deuteronômio', 'dt', 1, 37, 'Por causa de vocês o Senhor irou-se contra mim e me disse: "Você também não entrará na terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4930, 'nvi', 'Deuteronômio', 'dt', 1, 38, 'Mas o seu auxiliar, Josué, filho de Num, entrará. Encoraje-o, pois ele fará com que Israel tome posse dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4931, 'nvi', 'Deuteronômio', 'dt', 1, 39, 'Mas as crianças que vocês disseram que seriam levadas como despojo, os seus filhos que ainda não distinguem entre o bem e o mal, eles entrarão na terra. Eu a darei a eles, e eles tomarão posse dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4932, 'nvi', 'Deuteronômio', 'dt', 1, 40, 'Mas quanto a vocês, dêem meia-volta e partam para o deserto pelo caminho do mar Vermelho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4933, 'nvi', 'Deuteronômio', 'dt', 1, 41, 'Então vocês responderam: "Pecamos contra o Senhor. Nós subiremos e lutaremos, conforme tudo o que o Senhor, o nosso Deus, nos ordenou". Cada um de vocês preparou-se com as suas armas de guerra, achando que seria fácil subir a região montanhosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4934, 'nvi', 'Deuteronômio', 'dt', 1, 42, 'Mas o Senhor me disse: "Diga-lhes que não subam nem lutem, porque não estarei com vocês. Vocês serão derrotados pelos seus inimigos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4935, 'nvi', 'Deuteronômio', 'dt', 1, 43, 'Eu lhes disse isso, mas vocês não me deram ouvidos, rebelaram-se contra o Senhor e, com presunção, subiram a região montanhosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4936, 'nvi', 'Deuteronômio', 'dt', 1, 44, 'Os amorreus que lá viviam os atacaram, os perseguiram como um enxame de abelhas e os arrasaram desde Seir até Hormá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4937, 'nvi', 'Deuteronômio', 'dt', 1, 45, 'Vocês voltaram e choraram perante o Senhor, mas ele não ouviu o seu clamor nem lhes deu atenção.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4938, 'nvi', 'Deuteronômio', 'dt', 1, 46, 'Então vocês ficaram em Cades, onde permaneceram muito tempo.');
