INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5187, 'nvi', 'Deuteronômio', 'dt', 10, 1, 'Naquela ocasião o Senhor me ordenou: "Corte duas tábuas de pedra, como as primeiras, e suba para encontrar-se comigo no monte. Faça também uma arca de madeira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5188, 'nvi', 'Deuteronômio', 'dt', 10, 2, 'Eu escreverei nas tábuas as palavras que estavam nas primeiras, que você quebrou, e você as colocará na arca".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5189, 'nvi', 'Deuteronômio', 'dt', 10, 3, 'Então fiz a arca de madeira de acácia, cortei duas tábuas de pedra como as primeiras e subi o monte com as duas tábuas nas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5190, 'nvi', 'Deuteronômio', 'dt', 10, 4, 'O Senhor escreveu nelas o que tinha escrito anteriormente, os Dez Mandamentos que havia proclamado a vocês no monte, do meio do fogo, no dia em que estavam todos reunidos. O Senhor as entregou a mim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5191, 'nvi', 'Deuteronômio', 'dt', 10, 5, 'e eu voltei, desci do monte e coloquei as tábuas na arca que eu tinha feito. E lá ficaram, conforme o Senhor tinha ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5192, 'nvi', 'Deuteronômio', 'dt', 10, 6, '( Os israelitas partiram dos poços dos jaacanitas e foram até Moserá. Ali Arão morreu e foi sepultado, e o seu filho Eleazar foi o seu sucessor como sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5193, 'nvi', 'Deuteronômio', 'dt', 10, 7, 'Dali foram para Gudgodá e de lá para Jotbatá, terra de riachos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5194, 'nvi', 'Deuteronômio', 'dt', 10, 8, 'Naquela ocasião o Senhor separou a tribo de Levi para carregar a arca da aliança do Senhor, para estar perante o Senhor a fim de ministrar e pronunciar bênçãos em seu nome, como se faz ainda hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5195, 'nvi', 'Deuteronômio', 'dt', 10, 9, 'É por isso que os levitas não têm nenhuma porção de terra ou herança entre os seus irmãos; o Senhor é a sua herança, conforme o Senhor, o seu Deus, lhes havia prometido. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5196, 'nvi', 'Deuteronômio', 'dt', 10, 10, 'Assim eu fiquei no monte quarenta dias e quarenta noites, como da primeira vez; e também desta vez o Senhor me atendeu e não quis destruí-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5197, 'nvi', 'Deuteronômio', 'dt', 10, 11, '"Vá", o Senhor me disse. "Conduza o povo em seu caminho, para que tomem posse da terra que jurei aos seus antepassados que lhes daria. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5198, 'nvi', 'Deuteronômio', 'dt', 10, 12, 'E agora, ó Israel, que é que o Senhor seu Deus pede de você, senão que tema o Senhor, o seu Deus, que ande em todos os seus caminhos, que o ame e que sirva ao Senhor, ao seu Deus, de todo o seu coração e de toda a sua alma,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5199, 'nvi', 'Deuteronômio', 'dt', 10, 13, 'e que obedeça aos mandamentos e aos decretos do Senhor, que hoje lhe dou para o seu próprio bem?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5200, 'nvi', 'Deuteronômio', 'dt', 10, 14, 'Ao Senhor, ao seu Deus, pertencem os céus e até os mais altos céus, a terra e tudo o que nela existe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5201, 'nvi', 'Deuteronômio', 'dt', 10, 15, 'No entanto, o Senhor se afeiçoou aos seus antepassados e os amou, e a vocês, descendentes deles, escolheu entre todas as nações, como hoje se vê.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5202, 'nvi', 'Deuteronômio', 'dt', 10, 16, 'Sejam fiéis à sua aliança em seus corações, e deixem de ser obstinados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5203, 'nvi', 'Deuteronômio', 'dt', 10, 17, 'Pois o Senhor, o seu Deus, é o Deus dos deuses e o Soberano dos soberanos, o grande Deus, poderoso e temível, que não age com parcialidade nem aceita suborno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5204, 'nvi', 'Deuteronômio', 'dt', 10, 18, 'Ele defende a causa do órfão e da viúva e ama o estrangeiro, dando-lhe alimento e roupa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5205, 'nvi', 'Deuteronômio', 'dt', 10, 19, 'Amem os estrangeiros, pois vocês mesmos foram estrangeiros no Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5206, 'nvi', 'Deuteronômio', 'dt', 10, 20, 'Temam o Senhor, o seu Deus, e sirvam-no. Apeguem-se a ele e façam os seus juramentos somente em nome dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5207, 'nvi', 'Deuteronômio', 'dt', 10, 21, 'Seja ele o motivo do seu louvor, pois ele é o seu Deus, que por vocês fez aquelas grandes e temíveis maravilhas que vocês viram com os próprios olhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5208, 'nvi', 'Deuteronômio', 'dt', 10, 22, 'Os seus antepassados que desceram ao Egito eram setenta ao todo, mas agora o Senhor, o seu Deus, os tornou tão numerosos quanto as estrelas do céu.');
