INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5209, 'nvi', 'Deuteronômio', 'dt', 11, 1, 'Amem o Senhor, o seu Deus e obedeçam sempre aos seus preceitos, aos seus decretos, às suas ordenanças e aos seus mandamentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5210, 'nvi', 'Deuteronômio', 'dt', 11, 2, 'Lembrem-se hoje de que não foram os seus filhos que experimentaram e viram a disciplina do Senhor, o seu Deus, a sua majestade, a sua mão poderosa, o seu braço forte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5211, 'nvi', 'Deuteronômio', 'dt', 11, 3, 'Vocês viram os sinais que ele realizou e tudo o que fez no coração do Egito, tanto com o faraó, rei do Egito, quanto com toda a sua terra;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5212, 'nvi', 'Deuteronômio', 'dt', 11, 4, 'o que fez com o exército egípcio, com os seus cavalos e carros, como os surpreendeu com as águas do mar Vermelho, quando estavam perseguindo vocês, e como o Senhor os destruiu para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5213, 'nvi', 'Deuteronômio', 'dt', 11, 5, 'Vocês também viram o que ele fez por vocês no deserto até chegarem a este lugar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5214, 'nvi', 'Deuteronômio', 'dt', 11, 6, 'e o que fez a Datã e a Abirão, filhos de Eliabe, da tribo de Rúben, quando a terra abriu a boca no meio de todo o Israel e os engoliu com suas famílias, suas tendas e tudo o que lhes pertencia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5215, 'nvi', 'Deuteronômio', 'dt', 11, 7, 'Vocês mesmos viram com os próprios olhos todas essas coisas grandiosas que o Senhor fez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5216, 'nvi', 'Deuteronômio', 'dt', 11, 8, 'Obedeçam, portanto, a toda a lei que hoje lhes estou dando, para que tenham forças para invadir e conquistar a terra para onde estão indo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5217, 'nvi', 'Deuteronômio', 'dt', 11, 9, 'e para que vivam muito tempo na terra que o Senhor jurou dar aos seus antepassados e aos descendentes deles, terra onde manam leite e mel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5218, 'nvi', 'Deuteronômio', 'dt', 11, 10, 'A terra da qual vocês vão tomar posse não é como a terra do Egito, de onde vocês vieram e onde plantavam as sementes e tinham que fazer a irrigação a pé, como numa horta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5219, 'nvi', 'Deuteronômio', 'dt', 11, 11, 'Mas a terra em que vocês, atravessando o Jordão, vão entrar para dela tomar posse, é terra de montes e vales, que bebe chuva do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5220, 'nvi', 'Deuteronômio', 'dt', 11, 12, 'É uma terra da qual o Senhor, o seu Deus, cuida; os olhos do Senhor, do seu Deus, estão continuamente sobre ela, do início ao fim do ano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5221, 'nvi', 'Deuteronômio', 'dt', 11, 13, 'Portanto, se vocês obedecerem fielmente aos mandamentos que hoje lhes dou, amando o Senhor, o seu Deus, e servindo-o de todo o coração e de toda a alma,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5222, 'nvi', 'Deuteronômio', 'dt', 11, 14, 'então, no devido tempo, enviarei chuva sobre a sua terra, chuva de outono e de primavera, para que vocês recolham o seu cereal, e tenham vinho novo e azeite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5223, 'nvi', 'Deuteronômio', 'dt', 11, 15, 'Ela dará pasto nos campos para os seus rebanhos, e quanto a vocês, terão o que comer e ficarão satisfeitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5224, 'nvi', 'Deuteronômio', 'dt', 11, 16, 'Por isso, tenham cuidado para não serem enganados e levados a desviar-se para adorar outros deuses e a prostrar-se perante eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5225, 'nvi', 'Deuteronômio', 'dt', 11, 17, 'Caso contrário, a ira do Senhor se acenderá contra vocês e ele fechará o céu para que não chova e para que a terra nada produza e assim vocês logo desaparecerão da boa terra que o Senhor lhes está dando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5226, 'nvi', 'Deuteronômio', 'dt', 11, 18, 'Gravem estas minhas palavras no coração e na mente; amarrem-nas como símbolos nas mãos e prendam-nas na testa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5227, 'nvi', 'Deuteronômio', 'dt', 11, 19, 'Ensinem-nas a seus filhos, conversando a respeito delas quando estiverem sentados em casa e quando estiverem andando pelo caminho, quando se deitarem e quando se levantarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5228, 'nvi', 'Deuteronômio', 'dt', 11, 20, 'Escrevam-nas nos batentes das portas de suas casas, e nos seus portões,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5229, 'nvi', 'Deuteronômio', 'dt', 11, 21, 'para que, na terra que o Senhor jurou que daria aos seus antepassados, os seus dias e os dias dos seus filhos sejam muitos, sejam tantos como os dias durante os quais o céu está acima da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5230, 'nvi', 'Deuteronômio', 'dt', 11, 22, 'Se vocês obedecerem cuidadosamente todo o mandamento que lhes mando cumprir, amando o Senhor, o seu Deus, andando em todos os seus caminhos e apegando-se a ele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5231, 'nvi', 'Deuteronômio', 'dt', 11, 23, 'então o Senhor expulsará todas essas nações da presença de vocês, e vocês despojarão nações maiores e mais fortes do que vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5232, 'nvi', 'Deuteronômio', 'dt', 11, 24, 'Todo lugar onde vocês puserem os pés será de vocês. O seu território se estenderá do deserto do Líbano e do rio Eufrates ao mar Ocidental.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5233, 'nvi', 'Deuteronômio', 'dt', 11, 25, 'Ninguém conseguirá resisti-los. O Senhor, o seu Deus, conforme lhes prometeu, trará pavor e medo de vocês a todos os povos daquela terra, aonde quer que vocês forem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5234, 'nvi', 'Deuteronômio', 'dt', 11, 26, 'Prestem atenção! Hoje estou pondo diante de vocês a bênção e a maldição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5235, 'nvi', 'Deuteronômio', 'dt', 11, 27, 'Vocês terão bênção, se obedecerem aos mandamentos do Senhor, o seu Deus, que hoje lhes estou dando;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5236, 'nvi', 'Deuteronômio', 'dt', 11, 28, 'mas terão maldição, se desobedecerem aos mandamentos do Senhor, o seu Deus, e se afastarem do caminho que hoje lhes ordeno, para seguir deuses desconhecidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5237, 'nvi', 'Deuteronômio', 'dt', 11, 29, 'Quando o Senhor, o seu Deus, os tiver levado para a terra da qual vão tomar posse, vocês terão que proclamar a bênção no monte Gerizim, e a maldição no monte Ebal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5238, 'nvi', 'Deuteronômio', 'dt', 11, 30, 'Como sabem, esses montes estão do outro lado do Jordão, a oeste da estrada, na direção do poente, perto dos carvalhos de Moré, no território dos cananeus que vivem na Arabá, próximos de Gilgal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5239, 'nvi', 'Deuteronômio', 'dt', 11, 31, 'Vocês estão a ponto de atravessar o Jordão e de tomar posse da terra que o Senhor, o seu Deus, lhes está dando. Quando vocês a tiverem conquistado e estiverem vivendo ali,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5240, 'nvi', 'Deuteronômio', 'dt', 11, 32, 'tenham o cuidado de obedecer a todos os decretos e ordenanças que hoje estou dando a vocês.');
