INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5291, 'nvi', 'Deuteronômio', 'dt', 14, 1, 'Vocês são os filhos do Senhor, do seu Deus. Não façam cortes no corpo nem rapem a frente da cabeça por causa dos mortos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5292, 'nvi', 'Deuteronômio', 'dt', 14, 2, 'pois vocês são povo consagrado ao Senhor, ao seu Deus. Dentre todos os povos da face da terra, o Senhor os escolheu para serem o seu tesouro pessoal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5293, 'nvi', 'Deuteronômio', 'dt', 14, 3, 'Não comam nada que seja proibido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5294, 'nvi', 'Deuteronômio', 'dt', 14, 4, 'São estes os animais que vocês podem comer: o boi, a ovelha, o bode,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5295, 'nvi', 'Deuteronômio', 'dt', 14, 5, 'o veado, a gazela, a corça, o bode montês, o antílope, o bode selvagem e a ovelha montês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5296, 'nvi', 'Deuteronômio', 'dt', 14, 6, 'Vocês poderão comer qualquer animal que tenha o casco fendido e dividido em duas unhas e que rumine.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5297, 'nvi', 'Deuteronômio', 'dt', 14, 7, 'Contudo, dos que ruminam ou têm o casco fendido, vocês não poderão comer o camelo, o coelho e o rato silvestre. Embora ruminem, não têm casco fendido; são impuros para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5298, 'nvi', 'Deuteronômio', 'dt', 14, 8, 'O porco também é impuro; embora tenha casco fendido, não rumina. Vocês não poderão comer a carne deles nem tocar em seus cadáveres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5299, 'nvi', 'Deuteronômio', 'dt', 14, 9, 'De todas as criaturas que vivem nas águas vocês poderão comer as que possuem barbatanas e escamas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5300, 'nvi', 'Deuteronômio', 'dt', 14, 10, 'Mas não poderão comer nenhuma criatura que não tiver barbatanas nem escamas; é impura para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5301, 'nvi', 'Deuteronômio', 'dt', 14, 11, 'Vocês poderão comer qualquer ave pura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5302, 'nvi', 'Deuteronômio', 'dt', 14, 12, 'Mas estas vocês não poderão comer: a águia, o urubu, a águia-marinha,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5303, 'nvi', 'Deuteronômio', 'dt', 14, 13, 'o milhafre, qualquer espécie de falcão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5304, 'nvi', 'Deuteronômio', 'dt', 14, 14, 'qualquer espécie de corvo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5305, 'nvi', 'Deuteronômio', 'dt', 14, 15, 'a coruja-de-chifre, a coruja-de-orelha-pequena, a coruja-orelhuda, qualquer espécie de gavião,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5306, 'nvi', 'Deuteronômio', 'dt', 14, 16, 'o mocho, o corujão, a coruja-branca,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5307, 'nvi', 'Deuteronômio', 'dt', 14, 17, 'a coruja-do-deserto, o abutre, a coruja-pescadora,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5308, 'nvi', 'Deuteronômio', 'dt', 14, 18, 'a cegonha, qualquer tipo de garça, a poupa e o morcego.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5309, 'nvi', 'Deuteronômio', 'dt', 14, 19, 'Todas as pequenas criaturas que enxameiam e têm asas são impuras para vocês; não as comam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5310, 'nvi', 'Deuteronômio', 'dt', 14, 20, 'Mas qualquer criatura que têm asas, sendo pura, vocês poderão comer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5311, 'nvi', 'Deuteronômio', 'dt', 14, 21, 'Não comam nada que encontrarem morto. Vocês poderão dá-lo a um estrangeiro residente de qualquer cidade de vocês, e ele poderá comê-lo, ou vocês poderão vendê-lo a outros estrangeiros. Mas vocês são povo consagrado ao Senhor, ao seu Deus. Não cozinhem o cabrito no leite da própria mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5312, 'nvi', 'Deuteronômio', 'dt', 14, 22, 'Separem o dízimo de tudo o que a terra produzir anualmente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5313, 'nvi', 'Deuteronômio', 'dt', 14, 23, 'Comam o dízimo do cereal, do vinho novo e do azeite, e a primeira cria de todos os seus rebanhos na presença do Senhor, o seu Deus, no local que ele escolher como habitação do seu Nome, para que aprendam a temer sempre o Senhor, o seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5314, 'nvi', 'Deuteronômio', 'dt', 14, 24, 'Mas, se o local for longe demais e vocês tiverem sido abençoados pelo Senhor, pelo seu Deus, e não puderem carregar o dízimo, pois o local escolhido pelo Senhor para ali pôr o seu Nome é longe demais,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5315, 'nvi', 'Deuteronômio', 'dt', 14, 25, 'troquem o dízimo por prata, e levem a prata ao local que o Senhor, o seu Deus, tiver escolhido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5316, 'nvi', 'Deuteronômio', 'dt', 14, 26, 'Com prata comprem o que quiserem: bois, ovelhas, vinho ou outra bebida fermentada, ou qualquer outra coisa que desejarem. Então juntamente com suas famílias comam e alegrem-se ali, na presença do Senhor, do seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5317, 'nvi', 'Deuteronômio', 'dt', 14, 27, 'E nunca se esqueçam dos levitas que vivem em suas cidades, pois eles não possuem propriedade nem herança próprias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5318, 'nvi', 'Deuteronômio', 'dt', 14, 28, 'Ao final de cada três anos, tragam todos os dízimos da colheita do terceiro ano e armazene-os em sua própria cidade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5319, 'nvi', 'Deuteronômio', 'dt', 14, 29, 'para que os levitas, que não possuem propriedade nem herança, e os estrangeiros, os órfãos e as viúvas que vivem na sua cidade venham comer e saciar-se, e para que o Senhor, o seu Deus, o abençoe em todo o trabalho das suas mãos.');
