INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5343, 'nvi', 'Deuteronômio', 'dt', 16, 1, 'Observem o mês de abibe e celebrem a Páscoa do Senhor, do seu Deus, pois no mês de abibe, de noite, ele os tirou do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5344, 'nvi', 'Deuteronômio', 'dt', 16, 2, 'Ofereçam como sacrifício da Páscoa ao Senhor, ao seu Deus, um animal dos rebanhos de bois ou de ovelhas no local que o Senhor escolher para habitação do seu Nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5345, 'nvi', 'Deuteronômio', 'dt', 16, 3, 'Não o comam com pão fermentado, mas durante sete dias comam pães sem fermento, o pão da aflição, pois foi às pressas que vocês saíram do Egito, para que todos os dias da sua vida vocês se lembrem da época em que saíram do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5346, 'nvi', 'Deuteronômio', 'dt', 16, 4, 'Durante sete dias, não permitam que seja encontrado fermento com vocês em toda a sua terra. Tampouco permitam que alguma carne sacrificada à tarde do primeiro dia permaneça até a manhã seguinte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5347, 'nvi', 'Deuteronômio', 'dt', 16, 5, 'Não ofereçam o sacrifício da Páscoa em nenhuma das cidades que o Senhor, o seu Deus, lhe der;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5348, 'nvi', 'Deuteronômio', 'dt', 16, 6, 'sacrifique-a apenas no local que ele escolher para habitação do seu Nome. Ali vocês oferecerão o sacrifício da Páscoa à tarde, ao pôr-do-sol, na data da sua partida do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5349, 'nvi', 'Deuteronômio', 'dt', 16, 7, 'Vocês cozinharão a carne do animal e a comerão no local que o Senhor, o seu Deus, escolher. E, pela manhã, cada um de vocês voltará para a sua tenda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5350, 'nvi', 'Deuteronômio', 'dt', 16, 8, 'Durante seis dias comam pão sem fermento, e no sétimo dia façam uma assembléia em honra do Senhor, ao seu Deus; não façam trabalho algum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5351, 'nvi', 'Deuteronômio', 'dt', 16, 9, 'Contem sete semanas a partir da época em que vocês começarem a colheita do cereal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5352, 'nvi', 'Deuteronômio', 'dt', 16, 10, 'Celebrem então a festa das semanas ao Senhor, ao seu Deus, e tragam uma oferta voluntária conforme às bênçãos recebidas do Senhor, do seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5353, 'nvi', 'Deuteronômio', 'dt', 16, 11, 'E alegrem-se perante o Senhor, o seu Deus, no local que ele escolher para habitação do seu Nome, junto com os seus filhos e as suas filhas, os seus servos e as suas servas, os levitas que vivem na sua cidade, os estrangeiros, os órfãos e as viúvas que vivem com vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5354, 'nvi', 'Deuteronômio', 'dt', 16, 12, 'Lembrem-se de que vocês foram escravos no Egito e obedeçam fielmente a estes decretos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5355, 'nvi', 'Deuteronômio', 'dt', 16, 13, 'Celebrem também a festa das cabanas durante sete dias, depois que ajuntarem o produto da eira e do lagar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5356, 'nvi', 'Deuteronômio', 'dt', 16, 14, 'Alegrem-se nessa festa com os seus filhos e as suas filhas, os seus servos e as suas servas, os levitas, os estrangeiros, os órfãos e as viúvas que vivem na sua cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5357, 'nvi', 'Deuteronômio', 'dt', 16, 15, 'Durante sete dias celebrem a festa, dedicada ao Senhor, ao seu Deus, no local que o Senhor escolher. Pois o Senhor, o seu Deus, os abençoará em toda a sua colheita e em todo o trabalho de suas mãos, e a sua alegria será completa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5358, 'nvi', 'Deuteronômio', 'dt', 16, 16, 'Três vezes por ano todos os seus homens se apresentarão ao Senhor, ao seu Deus, no local que ele escolher, por ocasião da festa dos pães sem fermento, da festa das semanas e da festa das cabanas. Nenhum deles deverá apresentar-se ao Senhor de mãos vazias:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5359, 'nvi', 'Deuteronômio', 'dt', 16, 17, 'cada um de vocês trará uma dádiva conforme as bênçãos recebidas do Senhor, do seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5360, 'nvi', 'Deuteronômio', 'dt', 16, 18, 'Nomeiem juízes e oficiais para cada uma de suas tribos em todas as cidades que o Senhor, o seu Deus, lhes dá, para que eles julguem o povo com justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5361, 'nvi', 'Deuteronômio', 'dt', 16, 19, 'Não pervertam a justiça nem mostrem parcialidade. Não aceitem suborno, pois o suborno cega até os sábios e prejudica a causa dos justos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5362, 'nvi', 'Deuteronômio', 'dt', 16, 20, 'Siga única e exclusivamente a justiça, para que tenham vida e tomem posse da terra que o Senhor, o seu Deus, lhes dá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5363, 'nvi', 'Deuteronômio', 'dt', 16, 21, 'Não ergam nenhum poste sagrado além do altar que construírem em honra do Senhor, do seu Deus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5364, 'nvi', 'Deuteronômio', 'dt', 16, 22, 'e não levantem nenhuma coluna sagrada, pois isto é detestável para o Senhor, o seu Deus.');
