INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5385, 'nvi', 'Deuteronômio', 'dt', 18, 1, 'Os sacerdotes levitas e todo o restante da tribo de Levi, não terão posse nem herança em Israel. Viverão das ofertas sacrificadas para o Senhor, preparadas no fogo, pois esta é a sua herança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5386, 'nvi', 'Deuteronômio', 'dt', 18, 2, 'Não terão herança alguma entre os seus compatriotas; o Senhor é a sua herança, conforme lhes prometeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5387, 'nvi', 'Deuteronômio', 'dt', 18, 3, 'Quando o povo sacrificar um novilho ou uma ovelha, os sacerdotes receberão a porção devida: o espádua, as queixadas e o estômago.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5388, 'nvi', 'Deuteronômio', 'dt', 18, 4, 'Vocês terão que dar-lhes as primícias do trigo, do vinho e do azeite, e a primeira lã da tosquia das ovelhas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5389, 'nvi', 'Deuteronômio', 'dt', 18, 5, 'pois, de todas as tribos, o Senhor, o seu Deus, escolheu os levitas e os seus descendentes para estarem na presença do Senhor, e para ministrarem sempre em seu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5390, 'nvi', 'Deuteronômio', 'dt', 18, 6, 'Se um levita que estiver morando em qualquer cidade de Israel, desejar ir ao local escolhido pelo Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5391, 'nvi', 'Deuteronômio', 'dt', 18, 7, 'poderá ministrar em nome do Senhor, do seu Deus, à semelhança de todos os outros levitas que ali servem na presença do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5392, 'nvi', 'Deuteronômio', 'dt', 18, 8, 'Ele receberá uma porção de alimento igual à dos outros levitas; além disso, ficará com o que receber com a venda dos bens da sua família.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5393, 'nvi', 'Deuteronômio', 'dt', 18, 9, 'Quando entrarem na terra que o Senhor, o seu Deus, lhes dá, não procurem imitar as coisas repugnantes que as nações de lá praticam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5394, 'nvi', 'Deuteronômio', 'dt', 18, 10, 'Não permitam que se ache alguém entre vocês que queime em sacrifício o seu filho ou a sua filha; que pratique adivinhação, ou dedique-se à magia, ou faça presságios, ou pratique feitiçaria');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5395, 'nvi', 'Deuteronômio', 'dt', 18, 11, 'ou faça encantamentos; que seja médium ou espírita ou que consulte os mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5396, 'nvi', 'Deuteronômio', 'dt', 18, 12, 'O Senhor têm repugnância por quem pratica essas coisas, e é por causa dessas abominações que o Senhor, o seu Deus, vai expulsar aquelas nações da presença de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5397, 'nvi', 'Deuteronômio', 'dt', 18, 13, 'Permaneçam inculpáveis perante o Senhor, o seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5398, 'nvi', 'Deuteronômio', 'dt', 18, 14, 'As nações que vocês vão expulsar dão ouvidos aos que praticam magia e adivinhação. Mas, a vocês, o Senhor, o seu Deus, não permitiu tais práticas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5399, 'nvi', 'Deuteronômio', 'dt', 18, 15, 'O Senhor, o seu Deus, levantará do meio de seus próprios irmãos um profeta como eu; ouçam-no.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5400, 'nvi', 'Deuteronômio', 'dt', 18, 16, 'Pois foi isso que pediram ao Senhor, ao seu Deus, em Horebe, no dia em que se reuniram, quando disseram: "Não queremos ouvir a voz do Senhor, do nosso Deus, nem ver o seu grande fogo, se não morreremos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5401, 'nvi', 'Deuteronômio', 'dt', 18, 17, 'O Senhor me disse: "Eles têm razão!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5402, 'nvi', 'Deuteronômio', 'dt', 18, 18, 'Levantarei do meio dos seus irmãos um profeta como você; porei minhas palavras na sua boca, e ele lhes dirá tudo o que eu lhe ordenar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5403, 'nvi', 'Deuteronômio', 'dt', 18, 19, 'Se alguém não ouvir as minhas palavras, que o profeta falará em meu nome, eu mesmo lhe pedirei contas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5404, 'nvi', 'Deuteronômio', 'dt', 18, 20, 'Mas o profeta que ousar falar em meu nome alguma coisa que não lhe ordenei, ou que falar em nome de outros deuses, terá que ser morto".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5405, 'nvi', 'Deuteronômio', 'dt', 18, 21, 'Mas vocês perguntem a si mesmos: "Como saberemos se uma mensagem não vem do Senhor? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5406, 'nvi', 'Deuteronômio', 'dt', 18, 22, 'Se o que o profeta proclamar em nome do Senhor não acontecer nem se cumprir, essa mensagem não vem do Senhor. Aquele profeta falou com presunção. Não tenham medo dele.');
