INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5407, 'nvi', 'Deuteronômio', 'dt', 19, 1, 'Quando o Senhor, o seu Deus, tiver destruído as nações cuja terra lhes dá, e quando vocês as expulsarem e ocuparem as cidades e as casas dessas nações,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5408, 'nvi', 'Deuteronômio', 'dt', 19, 2, 'separem três cidades de refúgio na parte central da terra que o Senhor, o seu Deus, está dando a vocês para que dela tomem posse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5409, 'nvi', 'Deuteronômio', 'dt', 19, 3, 'Dividam em três partes a terra que o Senhor, o seu Deus, lhe está dando como herança e façam nela acessos, para que aquele que matar alguém possa fugir para lá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5410, 'nvi', 'Deuteronômio', 'dt', 19, 4, 'Este é o caso em que um homem que matar outro poderá fugir para lá para salvar a vida: se matar o seu próximo sem intenção, sem que houvesse inimizade entre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5411, 'nvi', 'Deuteronômio', 'dt', 19, 5, 'Por exemplo, se um homem for com o seu amigo cortar lenha na floresta e, ao levantar o machado para derrubar uma árvore, o ferro escapar e atingir o seu amigo e matá-lo, ele poderá fugir para uma daquelas cidades para salvar a vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5412, 'nvi', 'Deuteronômio', 'dt', 19, 6, 'Do contrário, o vingador da vítima poderia persegui-lo enfurecido e alcançá-lo, caso a distância fosse grande demais, e poderia matá-lo, muito embora este não merecesse morrer, pois não havia inimizade entre ele e o seu próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5413, 'nvi', 'Deuteronômio', 'dt', 19, 7, 'É por isso que lhe ordeno que separe três cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5414, 'nvi', 'Deuteronômio', 'dt', 19, 8, 'Se o Senhor, o seu Deus, aumentar o seu território, como prometeu sob juramento aos seus antepassados, e lhe der toda a terra que prometeu a eles,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5415, 'nvi', 'Deuteronômio', 'dt', 19, 9, 'separem então mais três cidades. Isso acontecerá se vocês obedecerem fielmente a toda esta lei que hoje lhes ordeno: amar o Senhor, o seu Deus, e sempre andar nos seus caminhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5416, 'nvi', 'Deuteronômio', 'dt', 19, 10, 'Façam isso para que não se derrame sangue inocente na sua terra, a qual o Senhor, o seu Deus, lhes dá por herança, e para que não sejam culpados de derramamento de sangue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5417, 'nvi', 'Deuteronômio', 'dt', 19, 11, 'Mas, se alguém odiar o seu próximo, ficar à espreita dele, atacá-lo e matá-lo, e fugir para uma dessas cidades,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5418, 'nvi', 'Deuteronômio', 'dt', 19, 12, 'as autoridades da sua cidade mandarão buscá-lo nas cidade de refúgio, e o entregarão nas mãos do vingador da vítima, para que morra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5419, 'nvi', 'Deuteronômio', 'dt', 19, 13, 'Não tenham piedade dele. Eliminem de Israel a culpa pelo derramamento de sangue inocente, para que tudo lhes vá bem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5420, 'nvi', 'Deuteronômio', 'dt', 19, 14, 'Não mudem os marcos de divisa da propriedade do seu vizinho, que os seus colocaram na herança que receberão na terra que o Senhor, o seu Deus, lhes dá para que dela tomem posse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5421, 'nvi', 'Deuteronômio', 'dt', 19, 15, 'Uma só testemunha não é suficiente para condenar alguém de algum crime ou delito. Qualquer acusação precisa ser confirmada pelo depoimento de duas ou três testemunhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5422, 'nvi', 'Deuteronômio', 'dt', 19, 16, 'Se uma testemunha falsa quiser acusar um homem de algum crime,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5423, 'nvi', 'Deuteronômio', 'dt', 19, 17, 'os dois envolvidos na questão deverão apresentar-se ao Senhor, diante dos sacerdotes e juízes que estiverem exercendo o cargo naquela ocasião.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5424, 'nvi', 'Deuteronômio', 'dt', 19, 18, 'Os juízes investigarão o caso e, se ficar provado que a testemunha mentiu e deu falso testemunho contra o seu próximo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5425, 'nvi', 'Deuteronômio', 'dt', 19, 19, 'dêem-lhe a punição que ele planejava para o seu irmão. Eliminem o mal do meio de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5426, 'nvi', 'Deuteronômio', 'dt', 19, 20, 'O restante do povo saberá disso e terá medo, e nunca mais se fará uma coisa dessas entre vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5427, 'nvi', 'Deuteronômio', 'dt', 19, 21, 'Não tenham piedade. Exijam vida por vida, olho por olho, dente por dente, mão por mão, pé por pé.');
