INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4939, 'nvi', 'Deuteronômio', 'dt', 2, 1, 'Então demos meia-volta e partimos para o deserto pelo caminho do mar Vermelho, como o Senhor me havia ordenado. E por muitos anos caminhamos em redor dos montes de Seir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4940, 'nvi', 'Deuteronômio', 'dt', 2, 2, 'Então o Senhor me disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4941, 'nvi', 'Deuteronômio', 'dt', 2, 3, '"Vocês já caminharam bastante tempo ao redor destas montanhas; agora andem para o norte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4942, 'nvi', 'Deuteronômio', 'dt', 2, 4, 'E diga ao povo: Vocês estão passando pelo território de seus irmãos, os descendentes de Esaú, que vivem em Seir. Eles terão medo de vocês, mas tenham muito cuidado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4943, 'nvi', 'Deuteronômio', 'dt', 2, 5, 'Não os provoquem, pois não darei a vocês parte alguma da terra deles, nem mesmo o espaço de um pé. Já dei a Esaú a posse dos montes de Seir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4944, 'nvi', 'Deuteronômio', 'dt', 2, 6, 'Vocês lhes pagarão com prata a comida que comerem e a água que beberem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4945, 'nvi', 'Deuteronômio', 'dt', 2, 7, 'Pois o Senhor, o seu Deus, os tem abençoado em tudo o que vocês têm feito. Ele cuidou de vocês em sua jornada por este grande deserto. Nestes quarenta anos o Senhor, o seu Deus, tem estado com vocês, e não lhes tem faltado coisa alguma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4946, 'nvi', 'Deuteronômio', 'dt', 2, 8, 'Assim, passamos ao largo de nossos irmãos, os descendentes de Esaú, que habitam em Seir. Saímos da rota da Arabá, de Elate e de Eziom-Geber. Voltamos e fomos pela rota do deserto de Moabe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4947, 'nvi', 'Deuteronômio', 'dt', 2, 9, 'Então o Senhor me disse: "Não perturbem os moabitas nem os provoquem à guerra, pois não darei a vocês parte alguma da terra deles, pois já entreguei a região de Ar aos descendentes de Ló".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4948, 'nvi', 'Deuteronômio', 'dt', 2, 10, '( Antigamente os emins habitavam nessa terra; era um povo forte e numeroso, alto como os enaquins.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4949, 'nvi', 'Deuteronômio', 'dt', 2, 11, 'Como os enaquins, eles também eram considerados refains, mas os moabitas os chamavam emins.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4950, 'nvi', 'Deuteronômio', 'dt', 2, 12, 'Também em Seir antigamente habitavam os horeus. Mas os descendentes de Esaú os expulsaram e os exterminaram e se estabeleceram no seu lugar, tal como Israel fez com a terra que o Senhor lhe deu. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4951, 'nvi', 'Deuteronômio', 'dt', 2, 13, '"Agora levantem-se! Atravessem o vale de Zerede". Assim atravessamos o vale.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4952, 'nvi', 'Deuteronômio', 'dt', 2, 14, 'Passaram-se trinta e oito anos entre a época em que partimos de Cades-Barnéia, até quando atravessamos o vale de Zerede, até que pereceu do acampamento toda aquela geração de homens de guerra, conforme o Senhor lhes havia jurado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4953, 'nvi', 'Deuteronômio', 'dt', 2, 15, 'A mão do Senhor caiu sobre eles e por fim os eliminou completamente do acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4954, 'nvi', 'Deuteronômio', 'dt', 2, 16, 'Depois que todos os guerreiros do povo tinham morrido,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4955, 'nvi', 'Deuteronômio', 'dt', 2, 17, 'o Senhor me disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4956, 'nvi', 'Deuteronômio', 'dt', 2, 18, '"Vocês estão prestes a passar pelo território de Moabe, pela região de Ar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4957, 'nvi', 'Deuteronômio', 'dt', 2, 19, 'e vão chegar perto da fronteira dos amonitas. Não sejam hostis a eles, pois não darei a vocês parte alguma da terra dos amonitas, pois eu a entreguei aos descendentes de Ló".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4958, 'nvi', 'Deuteronômio', 'dt', 2, 20, '( Essa região também era considerada terra dos refains, que ali habitaram no passado. Os amonitas os chamavam zanzumins.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4959, 'nvi', 'Deuteronômio', 'dt', 2, 21, 'Eram fortes, numerosos e altos como os enaquins. O Senhor os exterminou, e os amonitas os expulsaram e se estabeleceram em seu lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4960, 'nvi', 'Deuteronômio', 'dt', 2, 22, 'O Senhor fez o mesmo com os descendentes de Esaú que vivem em Seir, quando exterminou os horeus diante deles. Os descendentes de Esaú os expulsaram e se estabeleceram em seu lugar até hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4961, 'nvi', 'Deuteronômio', 'dt', 2, 23, 'Foi o que também aconteceu aos aveus, que viviam em povoados próximos de Gaza; os caftoritas, vindos de Caftor, os destruíram e se estabeleceram em seu lugar. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4962, 'nvi', 'Deuteronômio', 'dt', 2, 24, '"Vão agora e atravessem o ribeiro do Arnom. Vejam que eu entreguei em suas mãos Seom o amorreu, rei de Hesbom, e a terra dele. Comecem a ocupação, entrem em guerra contra ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4963, 'nvi', 'Deuteronômio', 'dt', 2, 25, 'Hoje mesmo começarei a infundir pavor e medo de vocês em todos os povos debaixo do céu. Quando ouvirem da fama de vocês, tremerão e ficarão angustiados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4964, 'nvi', 'Deuteronômio', 'dt', 2, 26, 'Do deserto de Quedemote enviei mensageiros a Seom, rei de Hesbom, oferecendo paz e dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4965, 'nvi', 'Deuteronômio', 'dt', 2, 27, 'Deixa-nos passar pela tua terra. Iremos somente pela estrada; não nos desviaremos nem para a direita nem para a esquerda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4966, 'nvi', 'Deuteronômio', 'dt', 2, 28, 'Por prata nos venderás tanto a comida que comermos como a água que bebermos. Apenas deixe-nos passar a pé,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4967, 'nvi', 'Deuteronômio', 'dt', 2, 29, 'como fizeram os descendentes de Esaú, que habitam em Seir, e os moabitas, que habitam em Ar. Assim chegarei ao Jordão, e, através dele, à terra que o Senhor, o nosso Deus, nos dá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4968, 'nvi', 'Deuteronômio', 'dt', 2, 30, 'Mas Seom, rei de Hesbom, não quis deixar-nos passar; pois o Senhor, o Deus de vocês, tornou-lhe obstinado o espírito e endureceu-lhe o coração, para entregá-lo nas mãos de vocês, como hoje se vê.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4969, 'nvi', 'Deuteronômio', 'dt', 2, 31, 'O Senhor me disse: "Estou entregando a você Seom e sua terra. Comece a ocupação, tome posse da terra dele! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4970, 'nvi', 'Deuteronômio', 'dt', 2, 32, 'Então Seom saiu à batalha contra nós em Jaza, com todo o seu exército.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4971, 'nvi', 'Deuteronômio', 'dt', 2, 33, 'Mas o Senhor, o nosso Deus, entregou-o a nós, e o derrotamos, a ele, aos seus filhos e a todo o seu exército.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4972, 'nvi', 'Deuteronômio', 'dt', 2, 34, 'Naquela ocasião conquistamos todas as suas cidades e as destruímos totalmente, matando homens, mulheres e crianças, sem deixar nenhum sobrevivente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4973, 'nvi', 'Deuteronômio', 'dt', 2, 35, 'Tomamos como presa somente os animais e o despojo das cidades que conquistamos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4974, 'nvi', 'Deuteronômio', 'dt', 2, 36, 'Desde Aroer, junto ao ribeiro do Arnom, e a cidade que fica no mesmo vale, até Gileade, não houve cidade de muros altos demais para nós. O Senhor, o nosso Deus, entregou-nos tudo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4975, 'nvi', 'Deuteronômio', 'dt', 2, 37, 'Somente da terra dos amonitas vocês não se aproximaram, ou seja, toda a extensão do vale do Rio Jaboque, e as cidades da região montanhosa, conforme o Senhor, o nosso Deus, tinha ordenado.');
