INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5428, 'nvi', 'Deuteronômio', 'dt', 20, 1, 'Quando vocês forem à guerra contra os seus inimigos e virem cavalos e carros, e um exército maior do que o seu, não tenham medo, pois o Senhor, o seu Deus, que os tirou do Egito, estará com vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5429, 'nvi', 'Deuteronômio', 'dt', 20, 2, 'Quando chegar a hora da batalha, o sacerdote virá à frente e dirá ao exército:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5430, 'nvi', 'Deuteronômio', 'dt', 20, 3, '"Ouça, ó Israel. Hoje vocês vão lutar contra os inimigos. Não se desanimem nem tenham medo; não fiquem apavorados nem aterrorizados por causa deles,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5431, 'nvi', 'Deuteronômio', 'dt', 20, 4, 'pois o Senhor, o seu Deus, os acompanhará e lutará por vocês contra os inimigos, para lhes dar a vitória".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5432, 'nvi', 'Deuteronômio', 'dt', 20, 5, 'Os oficiais dirão ao exército: "Há alguém que construiu uma casa e ainda não a dedicou? Volte ele para sua casa, para que não morra na guerra e outro a dedique.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5433, 'nvi', 'Deuteronômio', 'dt', 20, 6, 'Há alguém que plantou uma vinha e ainda não desfrutou dela? Volte ele para sua casa, para que não morra na guerra e outro desfrute da vinha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5434, 'nvi', 'Deuteronômio', 'dt', 20, 7, 'Há alguém comprometido para casar-se que ainda não recebeu sua mulher? Volte ele para sua casa, para que não morra na guerra e outro case-se com ela".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5435, 'nvi', 'Deuteronômio', 'dt', 20, 8, 'Por fim os oficiais acrescentarão: "Alguém está com medo e não tem coragem? Volte ele para sua casa, para que os seus irmãos israelitas também não fiquem desanimados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5436, 'nvi', 'Deuteronômio', 'dt', 20, 9, 'Quando os oficiais terminarem de falar ao exército, designarão chefes para comandar as tropas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5437, 'nvi', 'Deuteronômio', 'dt', 20, 10, 'Quando vocês avançarem para atacar uma cidade, enviem-lhe primeiro uma proposta de paz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5438, 'nvi', 'Deuteronômio', 'dt', 20, 11, 'Se os seus habitantes aceitarem, e abrirem suas portas, serão seus escravos e se sujeitarão a trabalhos forçados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5439, 'nvi', 'Deuteronômio', 'dt', 20, 12, 'Mas se eles recusarem a paz e entrarem em guerra contra vocês, sitiem a cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5440, 'nvi', 'Deuteronômio', 'dt', 20, 13, 'Quando o Senhor, o seu Deus, entregá-la em suas mãos, matem ao fio da espada todos os homens que nela houver.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5441, 'nvi', 'Deuteronômio', 'dt', 20, 14, 'Mas as mulheres, as crianças, os rebanhos e tudo o que acharem na cidade, será de vocês; vocês poderão ficar com os despojos dos seus inimigos dados pelo Senhor, o seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5442, 'nvi', 'Deuteronômio', 'dt', 20, 15, 'É assim que vocês tratarão todas as cidades distantes que não pertencem às nações vizinhas de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5443, 'nvi', 'Deuteronômio', 'dt', 20, 16, 'Contudo, nas cidades das nações que o Senhor, o seu Deus, lhes dá por herança, não deixem vivo nenhuma alma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5444, 'nvi', 'Deuteronômio', 'dt', 20, 17, 'Conforme a ordem do Senhor, o seu Deus, destruam totalmente os hititas, os amorreus, os cananeus, os ferezeus, os heveus e os jebuseus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5445, 'nvi', 'Deuteronômio', 'dt', 20, 18, 'Se não, eles os ensinarão a praticar todas as coisas repugnantes que eles fazem quando adoram os seus deuses, e vocês pecarão contra o Senhor, contra o seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5446, 'nvi', 'Deuteronômio', 'dt', 20, 19, 'Quando sitiarem uma cidade por um longo período, lutando contra ela para conquistá-la, não destruam as árvores dessa cidade a golpes de machado, pois vocês poderão comer as suas frutas. Não as derrubem. Por acaso as árvores são gente, para que vocês as sitiem?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5447, 'nvi', 'Deuteronômio', 'dt', 20, 20, 'Entretanto, poderão derrubar as árvores que vocês sabem que não são frutíferas, para utilizá-las em obras que ajudem o cerco, até que caia a cidade que está em guerra contra vocês.');
