INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5448, 'nvi', 'Deuteronômio', 'dt', 21, 1, 'Se alguém for encontrado morto no campo, na terra que o Senhor, o seu Deus, lhes dá para dela tomarem posse, sem que se saiba quem o matou,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5449, 'nvi', 'Deuteronômio', 'dt', 21, 2, 'as autoridades e os juízes sairão e medirão a distância do corpo até as cidades vizinhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5450, 'nvi', 'Deuteronômio', 'dt', 21, 3, 'Então as autoridades da cidade mais próxima do corpo apanharão uma novilha que nunca foi usada no trabalho e sobre a qual nunca foi posto jugo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5451, 'nvi', 'Deuteronômio', 'dt', 21, 4, 'e a levarão a um vale de terras nunca aradas nem semeadas e onde haja um ribeiro de águas perenes. Vocês quebrarão o pescoço da novilha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5452, 'nvi', 'Deuteronômio', 'dt', 21, 5, 'Depois, os sacerdotes, descendentes de Levi, se aproximarão, pois o Senhor, o seu Deus, os escolheu para ministrarem e para pronunciarem bênçãos em nome do Senhor e resolverem todos os casos de litígio e de violência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5453, 'nvi', 'Deuteronômio', 'dt', 21, 6, 'Então todas as autoridades da cidade mais próxima do corpo lavarão as mãos sobre a novilha cujo pescoço foi quebrado no vale,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5454, 'nvi', 'Deuteronômio', 'dt', 21, 7, 'e declararão: "As nossas mãos não derramaram este sangue, nem os nossos olhos viram quem fez isso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5455, 'nvi', 'Deuteronômio', 'dt', 21, 8, 'Aceita, Senhor, esta propiciação em favor do teu povo Israel, a quem resgataste, e não consideres o teu povo culpado do sangue de um inocente". Assim a culpa do derramamento de sangue será propiciada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5456, 'nvi', 'Deuteronômio', 'dt', 21, 9, 'Desse modo vocês eliminarão de vocês mesmos a culpa pelo derramamento de sangue inocente, pois fizeram o que o Senhor aprova.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5457, 'nvi', 'Deuteronômio', 'dt', 21, 10, 'Quando vocês guerrearem contra os seus inimigos e o Senhor, o seu Deus, os entregar em suas mãos e vocês fizerem prisioneiros,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5458, 'nvi', 'Deuteronômio', 'dt', 21, 11, 'um de vocês poderá ver entre eles uma mulher muito bonita, agradar-se dela e tomá-la como esposa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5459, 'nvi', 'Deuteronômio', 'dt', 21, 12, 'Leve-a para casa; ela rapará a cabeça, cortará as unhas');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5460, 'nvi', 'Deuteronômio', 'dt', 21, 13, 'e se desfará das roupas que estava usando quando foi capturada. Ficará em casa e pranteará seu pai e sua mãe um mês inteiro. Depois você poderá chegar-se a ela e ser o seu marido, e ela será sua mulher.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5461, 'nvi', 'Deuteronômio', 'dt', 21, 14, 'Se você já não se agradar dela, deixe-a ir para onde quiser, mas não poderá vendê-la nem tratá-la como escrava, pois você a desonrou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5462, 'nvi', 'Deuteronômio', 'dt', 21, 15, 'Se um homem tiver duas mulheres e preferir uma delas, e ambas lhe derem filhos, e o filho mais velho for filho da mulher que ele não prefere,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5463, 'nvi', 'Deuteronômio', 'dt', 21, 16, 'quando der a herança de sua propriedade aos filhos, não poderá dar os direitos do filho mais velho ao filho da mulher preferida, se o filho da mulher que ele não prefere for de fato o mais velho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5464, 'nvi', 'Deuteronômio', 'dt', 21, 17, 'Ele terá que reconhecer o filho da mulher que ele não prefere como filho mais velho, dando-lhe porção dupla de tudo o que possui. Aquele filho é o primeiro sinal da força de seu pai e o direito do filho mais velho lhe pertence.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5465, 'nvi', 'Deuteronômio', 'dt', 21, 18, 'Se um homem tiver um filho obstinado e rebelde que não obedece a seu pai nem à sua mãe e não os escuta quando o disciplinam,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5466, 'nvi', 'Deuteronômio', 'dt', 21, 19, 'o pai e a mãe o levarão aos líderes da sua comunidade, à porta da cidade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5467, 'nvi', 'Deuteronômio', 'dt', 21, 20, 'e dirão aos líderes: "Este nosso filho é obstinado e rebelde. Não nos obedece! É devasso e vive bêbado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5468, 'nvi', 'Deuteronômio', 'dt', 21, 21, 'Então todos os homens da cidade o apedrejarão até à morte. Eliminem o mal do meio de vocês. Todo o Israel saberá disso e temerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5469, 'nvi', 'Deuteronômio', 'dt', 21, 22, 'Se um homem culpado de um crime que mereça a morte for morto e pendurado num madeiro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5470, 'nvi', 'Deuteronômio', 'dt', 21, 23, 'não deixem o corpo no madeiro durante a noite. Enterrem-no naquele mesmo dia, porque qualquer que for pendurado num madeiro está debaixo da maldição de Deus. Não contaminem a terra que o Senhor, o seu Deus, lhes dá por herança.');
