INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5501, 'nvi', 'Deuteronômio', 'dt', 23, 1, 'Qualquer que tenha os testículos esmagados ou tenha amputado o membro viril, não poderá entrar na assembléia do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5502, 'nvi', 'Deuteronômio', 'dt', 23, 2, 'Quem nasceu de união ilícita não poderá entrar na assembléia do Senhor, como também os seus descendentes, até a décima geração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5503, 'nvi', 'Deuteronômio', 'dt', 23, 3, 'Nenhum amonita ou moabita ou qualquer dos seus descendentes, até a décima geração, poderá entrar na assembléia do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5504, 'nvi', 'Deuteronômio', 'dt', 23, 4, 'Pois eles não vieram encontrar-se com vocês com pão e água no caminho, quando vocês saíram do Egito; além disso convocaram Balaão, filho de Beor, para vir de Petor, na Mesopotâmia, para pronunciar maldição contra vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5505, 'nvi', 'Deuteronômio', 'dt', 23, 5, 'No entanto, o Senhor, o seu Deus, não atendeu Balaão, e transformou a maldição em bênção para vocês, pois o Senhor, o seu Deus, os ama.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5506, 'nvi', 'Deuteronômio', 'dt', 23, 6, 'Não façam um tratado de amizade com eles enquanto vocês viverem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5507, 'nvi', 'Deuteronômio', 'dt', 23, 7, 'Não rejeitem o edomita, pois ele é seu irmão. Também não rejeitem o egípcio, pois vocês viveram como estrangeiros na terra deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5508, 'nvi', 'Deuteronômio', 'dt', 23, 8, 'A terceira geração dos filhos deles poderá entrar na assembléia do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5509, 'nvi', 'Deuteronômio', 'dt', 23, 9, 'Quando estiverem acampados, em guerra contra os seus inimigos, mantenham-se afastados de todas as coisas impuras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5510, 'nvi', 'Deuteronômio', 'dt', 23, 10, 'Se um de seus homens estiver impuro devido à polução noturna, ele terá que sair do acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5511, 'nvi', 'Deuteronômio', 'dt', 23, 11, 'Mas ao entardecer ele se lavará, e ao pôr-do-sol poderá voltar ao acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5512, 'nvi', 'Deuteronômio', 'dt', 23, 12, 'Determinem um local fora do acampamento onde se possa evacuar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5513, 'nvi', 'Deuteronômio', 'dt', 23, 13, 'Como parte do seu equipamento, tenham algo com que cavar, e quando evacuarem, façam um buraco e cubram as fezes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5514, 'nvi', 'Deuteronômio', 'dt', 23, 14, 'Pois o Senhor, o seu Deus, anda pelo seu acampamento para protegê-los e entregar-lhes os seus inimigos. O acampamento terá que ser santo, para que ele não veja no meio de vocês alguma coisa desagradável e se afaste de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5515, 'nvi', 'Deuteronômio', 'dt', 23, 15, 'Se um escravo refugiar-se entre vocês, não o entreguem nas mãos do seu senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5516, 'nvi', 'Deuteronômio', 'dt', 23, 16, 'Deixem-no viver no meio de vocês pelo tempo que ele desejar e em qualquer cidade que ele escolher. Não o oprimam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5517, 'nvi', 'Deuteronômio', 'dt', 23, 17, 'Nenhum israelita, homem ou mulher, poderá tornar-se prostituto cultual.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5518, 'nvi', 'Deuteronômio', 'dt', 23, 18, 'Não tragam ao santuário do Senhor, do seu Deus, os ganhos de uma prostituta ou de um prostituto, a fim de pagar algum voto, pois o Senhor, o seu Deus, por ambos têm repugnância.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5519, 'nvi', 'Deuteronômio', 'dt', 23, 19, 'Não cobrem juros de um israelita, por dinheiro, alimento, ou qualquer outra coisa que possa render juros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5520, 'nvi', 'Deuteronômio', 'dt', 23, 20, 'Vocês poderão cobrar juros do estrangeiro, mas não do seu irmão israelita, para que o Senhor, o seu Deus, os abençoe em tudo o que vocês fizerem na terra em que estão entrando para dela tomar posse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5521, 'nvi', 'Deuteronômio', 'dt', 23, 21, 'Se um de vocês fizer um voto ao Senhor, ao seu Deus, não demore a cumpri-lo, pois o Senhor, o seu Deus, certamente lhe pedirá contas, e você será culpado de pecado se não o cumprir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5522, 'nvi', 'Deuteronômio', 'dt', 23, 22, 'Mas se você não fizer o voto, de nada será culpado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5523, 'nvi', 'Deuteronômio', 'dt', 23, 23, 'Faça tudo para cumprir o que os seus lábios prometeram, pois com a sua própria boca você fez, espontaneamente, o seu voto ao Senhor, ao seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5524, 'nvi', 'Deuteronômio', 'dt', 23, 24, 'Se vocês entrarem na vinha do seu próximo, poderão comer as uvas que desejarem, mas nada poderão levar em sua cesta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5525, 'nvi', 'Deuteronômio', 'dt', 23, 25, 'Se entrarem na plantação de trigo do seu próximo, poderão apanhar espigas com as mãos, mas nunca usem foice para ceifar o trigo do seu próximo.');
