INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5548, 'nvi', 'Deuteronômio', 'dt', 25, 1, 'Quando dois homens se envolverem numa briga, terão que levar a causa ao tribunal, e os juízes decidirão a questão, absolvendo o inocente e condenando o culpado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5549, 'nvi', 'Deuteronômio', 'dt', 25, 2, 'Se o culpado merecer açoitamento, o juiz ordenará que ele se deite e seja açoitado em sua presença com o número de açoites que o seu crime merecer,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5550, 'nvi', 'Deuteronômio', 'dt', 25, 3, 'desde que nunca ultrapasse quarenta açoites. Açoitá-lo além disso seria humilhar publicamente um israelita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5551, 'nvi', 'Deuteronômio', 'dt', 25, 4, 'Não amordacem o boi enquanto está debulhando o cereal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5552, 'nvi', 'Deuteronômio', 'dt', 25, 5, 'Se dois irmãos morarem juntos, e um deles morrer sem deixar filhos, a sua viúva não se casará com alguém de fora da família. O irmão do marido se casará com ela e cumprirá com ela o dever de cunhado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5553, 'nvi', 'Deuteronômio', 'dt', 25, 6, 'O primeiro filho que ela tiver levará o nome do irmão falecido, para que o seu nome não seja apagado de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5554, 'nvi', 'Deuteronômio', 'dt', 25, 7, 'Se, todavia, ele não quiser casar-se com a mulher do seu irmão, ela irá aos líderes do lugar, à porta da cidade, e dirá: "O irmão do meu marido está se recusando a dar continuidade ao nome do seu irmão em Israel. Ele não quer cumprir para comigo o dever de cunhado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5555, 'nvi', 'Deuteronômio', 'dt', 25, 8, 'Os líderes da cidade o convocarão e conversarão com ele. Se ele insistir em dizer: "Não quero me casar com ela",');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5556, 'nvi', 'Deuteronômio', 'dt', 25, 9, 'a viúva do seu irmão se aproximará dele, na presença dos líderes, tirará uma das sandálias dele, cuspirá no seu rosto e dirá: "É isso que se faz com o homem que não perpetua a descendência do seu irmão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5557, 'nvi', 'Deuteronômio', 'dt', 25, 10, 'E a descendência daquele homem será conhecida em Israel como a família do descalçado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5558, 'nvi', 'Deuteronômio', 'dt', 25, 11, 'Se dois homens estiverem brigando, e a mulher de um deles vier para livrar o marido daquele que o ataca e pegá-lo pelos órgãos genitais,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5559, 'nvi', 'Deuteronômio', 'dt', 25, 12, 'cortem a mão dela. Não tenham piedade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5560, 'nvi', 'Deuteronômio', 'dt', 25, 13, 'Não tenham na bolsa dois padrões para o mesmo peso, um maior e outro menor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5561, 'nvi', 'Deuteronômio', 'dt', 25, 14, 'Não tenham em casa dois padrões para a mesma medida, um maior e outro menor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5562, 'nvi', 'Deuteronômio', 'dt', 25, 15, 'Tenham pesos e medidas exatos e honestos, para que vocês vivam muito tempo na terra que o Senhor, o seu Deus, lhes dá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5563, 'nvi', 'Deuteronômio', 'dt', 25, 16, 'Pois o seu Deus detesta quem faz essas coisas, quem negocia desonestamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5564, 'nvi', 'Deuteronômio', 'dt', 25, 17, 'Lembrem-se do que os amalequitas lhes fizeram no caminho, quando vocês saíram do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5565, 'nvi', 'Deuteronômio', 'dt', 25, 18, 'Quando vocês estavam cansados e exaustos, eles se encontraram com vocês no caminho e eliminaram todos os que ficaram para trás; não tiveram temor de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5566, 'nvi', 'Deuteronômio', 'dt', 25, 19, 'Quando o Senhor, o seu Deus, der a vocês o descanso de todos os inimigos ao seu redor na terra que ele lhes dá para dela tomar posse como herança, vocês farão que os amalequitas sejam esquecidos debaixo do céu. Não se esqueçam!');
