INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5567, 'nvi', 'Deuteronômio', 'dt', 26, 1, 'Quando vocês tiverem entrado na terra que o Senhor, o seu Deus, lhes dá por herança e dela tiverem tomado posse e lá estiverem estabelecidos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5568, 'nvi', 'Deuteronômio', 'dt', 26, 2, 'apanhem alguns dos primeiros frutos de tudo o que produzirem na terra que o Senhor, o seu Deus, lhes dá e ponha tudo numa cesta. Depois vocês deverão ir ao local que o Senhor, o seu Deus, escolher para habitação do seu Nome');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5569, 'nvi', 'Deuteronômio', 'dt', 26, 3, 'e dizer ao sacerdote que estiver exercendo o cargo naquela ocasião: "Declaro hoje ao Senhor, ao seu Deus, que vim para a terra que o Senhor jurou aos nossos antepassados que nos daria".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5570, 'nvi', 'Deuteronômio', 'dt', 26, 4, 'O sacerdote apanhará a cesta das suas mãos e a colocará em frente do altar do Senhor, do seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5571, 'nvi', 'Deuteronômio', 'dt', 26, 5, 'Então vocês declararão perante o Senhor, o seu Deus: "O meu pai era um arameu errante. Ele desceu ao Egito com pouca gente e ali viveu e se tornou uma grande nação, poderosa e numerosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5572, 'nvi', 'Deuteronômio', 'dt', 26, 6, 'Mas os egípcios nos maltrataram e nos oprimiram, sujeitando-nos a trabalhos forçados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5573, 'nvi', 'Deuteronômio', 'dt', 26, 7, 'Então clamamos ao Senhor, ao Deus dos nossos antepassados, e o Senhor ouviu a nossa voz e viu o nosso sofrimento, a nossa fadiga e a opressão que sofríamos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5574, 'nvi', 'Deuteronômio', 'dt', 26, 8, 'Por isso o Senhor nos tirou do Egito com mão poderosa e braço forte, com feitos temíveis e com sinais e maravilhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5575, 'nvi', 'Deuteronômio', 'dt', 26, 9, 'Ele nos trouxe a este lugar e nos deu esta terra, terra onde manam leite e mel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5576, 'nvi', 'Deuteronômio', 'dt', 26, 10, 'E agora trago os primeiros frutos do solo que tu, ó Senhor, me deste". Ponham a cesta perante o Senhor, o seu Deus, e curvem-se perante ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5577, 'nvi', 'Deuteronômio', 'dt', 26, 11, 'Vocês e os levitas e os estrangeiros que estiverem no meio de vocês se alegrarão com todas as coisas boas que o Senhor, o seu Deus, dá a vocês e às suas famílias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5578, 'nvi', 'Deuteronômio', 'dt', 26, 12, 'Quando tiverem separado o dízimo de tudo quanto produziram no terceiro ano, o ano do dízimo, entreguem-no ao levita, ao estrangeiro, ao órfão e à viúva, para que possam comer até saciar-se nas cidades de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5579, 'nvi', 'Deuteronômio', 'dt', 26, 13, 'Depois digam ao Senhor, ao seu Deus: "Retirei da minha casa a porção sagrada e dei-a ao levita, ao estrangeiro, ao órfão e à viúva, de acordo com tudo o que ordenaste. Não me afastei dos teus mandamentos nem esqueci nenhum deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5580, 'nvi', 'Deuteronômio', 'dt', 26, 14, 'Não comi nada da porção sagrada enquanto estive de luto, nada retirei dela enquanto estive impuro, e dela não ofereci nada aos mortos. Obedeci ao Senhor, ao meu Deus; fiz tudo o que me ordenaste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5581, 'nvi', 'Deuteronômio', 'dt', 26, 15, 'Olha dos céus, da tua santa habitação, e abençoa Israel, o teu povo, e a terra que nos deste, conforme prometeste sob juramento aos nossos antepassados, terra onde manam leite e mel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5582, 'nvi', 'Deuteronômio', 'dt', 26, 16, 'O Senhor, o seu Deus, lhes ordena hoje que sigam estes decretos e ordenanças; obedeçam-lhes atentamente, de todo o seu coração e de toda a sua alma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5583, 'nvi', 'Deuteronômio', 'dt', 26, 17, 'Hoje vocês declararam que o Senhor é o seu Deus e que vocês andarão nos seus caminhos, que guardarão os seus decretos, os seus mandamentos e as suas ordenanças, e que vocês lhe obedecerão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5584, 'nvi', 'Deuteronômio', 'dt', 26, 18, 'E hoje o Senhor declarou que vocês são o seu povo, o seu tesouro pessoal, conforme ele prometeu, e que vocês terão que guardar todos os seus mandamentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5585, 'nvi', 'Deuteronômio', 'dt', 26, 19, 'Ele declarou que lhes dará uma posição de glória, fama e honra muito acima de todas as nações que ele fez, e que vocês serão um povo santo para o Senhor, para o seu Deus, conforme ele prometeu.');
