INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5586, 'nvi', 'Deuteronômio', 'dt', 27, 1, 'Moisés e as autoridades de Israel ordenaram ao povo: "Obedeçam à toda esta lei que hoje lhes dou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5587, 'nvi', 'Deuteronômio', 'dt', 27, 2, 'Quando vocês atravessarem o Jordão, e entrarem na terra que o Senhor, o seu Deus, lhes dá, levantem algumas pedras grandes e pintem-nas com cal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5588, 'nvi', 'Deuteronômio', 'dt', 27, 3, 'Escrevam nelas todas as palavras desta lei, assim que tiverem atravessado para entrar na terra que o Senhor, o seu Deus, lhes dá, terra onde manam leite e mel, como o Senhor, o Deus dos seus antepassados, lhes prometeu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5589, 'nvi', 'Deuteronômio', 'dt', 27, 4, 'E, quando tiverem atravessado o Jordão, levantem essas pedras no monte Ebal, como hoje lhes ordeno, e pintem-nas com cal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5590, 'nvi', 'Deuteronômio', 'dt', 27, 5, 'Construam ali um altar ao Senhor, ao seu Deus, um altar de pedras. Não utilizem ferramenta de ferro nas pedras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5591, 'nvi', 'Deuteronômio', 'dt', 27, 6, 'Façam o altar do Senhor, do seu Deus, com pedras brutas, e sobre ele ofereçam holocaustos ao Senhor, ao seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5592, 'nvi', 'Deuteronômio', 'dt', 27, 7, 'Ofereçam também sacrifícios de comunhão, e comam e alegrem-se na presença do Senhor, do seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5593, 'nvi', 'Deuteronômio', 'dt', 27, 8, 'E nessas pedras que levantarem, vocês escreverão com bastante clareza todas as palavras desta lei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5594, 'nvi', 'Deuteronômio', 'dt', 27, 9, 'Então Moisés e os sacerdotes levitas disseram a todo Israel: "Faça silêncio e escute, ó Israel! Agora você se tornou o povo do Senhor, o seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5595, 'nvi', 'Deuteronômio', 'dt', 27, 10, 'Obedeça ao Senhor, ao seu Deus, e siga os seus mandamentos e decretos que hoje lhe dou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5596, 'nvi', 'Deuteronômio', 'dt', 27, 11, 'No mesmo dia Moisés ordenou ao povo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5597, 'nvi', 'Deuteronômio', 'dt', 27, 12, 'Quando vocês tiverem atravessado o Jordão, as tribos que estarão no monte Gerizim para abençoar o povo serão: Simeão, Levi, Judá, Issacar, José e Benjamim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5598, 'nvi', 'Deuteronômio', 'dt', 27, 13, 'E as tribos que estarão no monte Ebal para declararem maldições serão: Rúben, Gade, Aser, Zebulom, Dã e Naftali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5599, 'nvi', 'Deuteronômio', 'dt', 27, 14, 'E os levitas recitarão a todo o povo de Israel em alta voz:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5600, 'nvi', 'Deuteronômio', 'dt', 27, 15, '"Maldito quem esculpir uma imagem ou fizer um ídolo fundido, obra de artesãos, detestável ao Senhor, e levantá-lo secretamente". Todo o povo dirá: "Amém! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5601, 'nvi', 'Deuteronômio', 'dt', 27, 16, '"Maldito quem desonrar o seu pai ou a sua mãe". Todo o povo dirá: "Amém! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5602, 'nvi', 'Deuteronômio', 'dt', 27, 17, '"Maldito quem mudar o marco de divisa da propriedade do seu próximo". Todo o povo dirá: "Amém! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5603, 'nvi', 'Deuteronômio', 'dt', 27, 18, '"Maldito quem fizer o cego errar o caminho". Todo o povo dirá: "Amém! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5604, 'nvi', 'Deuteronômio', 'dt', 27, 19, '"Maldito quem negar justiça ao estrangeiro, ao órfão ou à viúva". Todo o povo dirá: "Amém! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5605, 'nvi', 'Deuteronômio', 'dt', 27, 20, '"Maldito quem se deitar com a mulher do seu pai, desonrando a cama do seu pai". Todo o povo dirá: "Amém! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5606, 'nvi', 'Deuteronômio', 'dt', 27, 21, '"Maldito quem tiver relações sexuais com algum animal". Todo o povo dirá: "Amém! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5607, 'nvi', 'Deuteronômio', 'dt', 27, 22, '"Maldito quem se deitar com a sua irmã, filha do seu pai ou da sua mãe". Todo o povo dirá: "Amém! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5608, 'nvi', 'Deuteronômio', 'dt', 27, 23, '"Maldito quem se deitar com a sua sogra". Todo o povo dirá: "Amém! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5609, 'nvi', 'Deuteronômio', 'dt', 27, 24, '"Maldito quem matar secretamente o seu próximo". Todo o povo dirá: "Amém! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5610, 'nvi', 'Deuteronômio', 'dt', 27, 25, '"Maldito quem aceitar pagamento para matar um inocente". Todo o povo dirá: "Amém! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (5611, 'nvi', 'Deuteronômio', 'dt', 27, 26, '"Maldito quem não puser em prática as palavras desta lei". Todo o povo dirá: "Amém! "');
